import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useCart } from '../context/CartContext';
import { useAuth } from '../context/AuthContext';
import { CreditCard, Smartphone, Truck, MapPin, Phone, Mail, User } from 'lucide-react';

const CheckoutPage = () => {
  const navigate = useNavigate();
  const { cartItems, getCartTotal, clearCart } = useCart();
  const { user } = useAuth();
  const [loading, setLoading] = useState(false);
  const [paymentMethod, setPaymentMethod] = useState('qr');
  const [showQR, setShowQR] = useState(false);
  const [orderPlaced, setOrderPlaced] = useState(false);

  const [deliveryInfo, setDeliveryInfo] = useState({
    name: user?.name || '',
    phone: user?.phone || '',
    email: user?.email || '',
    address: user?.address || '',
    pincode: '',
    city: '',
    state: ''
  });

  const [deliveryOption, setDeliveryOption] = useState('home');

  const calculateDeliveryTime = () => {
    if (cartItems.length === 0) return 0;
    return Math.max(...cartItems.map(item => item.deliveryTime || 2));
  };

  const generateOrderId = () => {
    const date = new Date();
    const year = date.getFullYear().toString().slice(-2);
    const month = (date.getMonth() + 1).toString().padStart(2, '0');
    const day = date.getDate().toString().padStart(2, '0');
    const random = Math.floor(Math.random() * 1000).toString().padStart(3, '0');
    return `SS${year}${month}${day}${random}`;
  };

  const handleInputChange = (field, value) => {
    setDeliveryInfo(prev => ({
      ...prev,
      [field]: value
    }));
  };

  const validateForm = () => {
    const required = ['name', 'phone'];
    if (deliveryOption === 'home') {
      required.push('address', 'pincode', 'city', 'state');
    }
    return required.every(field => deliveryInfo[field].trim() !== '');
  };

  const sendWhatsAppConfirmation = (orderData) => {
    const message = `🎉 *Order Confirmation - Stitch & Savour*

Hello ${orderData.customerName}!

Your order has been confirmed successfully! 

📋 *Order Details:*
Order ID: ${orderData.orderId}
Total Amount: ₹${orderData.total}
Payment Method: ${orderData.paymentMethod === 'qr' ? 'Online Payment' : 'Cash on Delivery'}

🛍️ *Items Ordered:*
${orderData.items.map(item => `• ${item.name} (Qty: ${item.quantity}) - ₹${item.price * item.quantity}`).join('\n')}

📅 *Expected Delivery:* ${orderData.expectedDelivery}
📍 *Delivery Address:* ${orderData.address}

Thank you for choosing Stitch & Savour! We'll keep you updated on your order status.

For any queries, feel free to contact us.

With love,
Sangita Thakur
Stitch & Savour`;

    const whatsappUrl = `https://wa.me/919970944685?text=${encodeURIComponent(message)}`;
    window.open(whatsappUrl, '_blank');
  };

  const handlePlaceOrder = async () => {
    if (!validateForm()) {
      alert('Please fill in all required fields');
      return;
    }

    if (paymentMethod === 'qr') {
      setShowQR(true);
      return;
    }

    // Place order for COD
    await processOrder();
  };

  const processOrder = async () => {
    setLoading(true);

    try {
      // Simulate API call
      await new Promise(resolve => setTimeout(resolve, 2000));

      const orderId = generateOrderId();
      const expectedDelivery = new Date();
      expectedDelivery.setDate(expectedDelivery.getDate() + calculateDeliveryTime());

      const orderData = {
        orderId,
        customerName: deliveryInfo.name,
        total: getCartTotal(),
        paymentMethod,
        items: cartItems,
        address: `${deliveryInfo.address}, ${deliveryInfo.city}, ${deliveryInfo.state} - ${deliveryInfo.pincode}`,
        expectedDelivery: expectedDelivery.toLocaleDateString('en-IN', {
          weekday: 'long',
          year: 'numeric',
          month: 'long',
          day: 'numeric'
        })
      };

      // Send WhatsApp confirmation
      sendWhatsAppConfirmation(orderData);

      // Clear cart and show success
      clearCart();
      setOrderPlaced(true);

    } catch (error) {
      alert('Failed to place order. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const handlePaymentSuccess = () => {
    setShowQR(false);
    processOrder();
  };

  if (cartItems.length === 0 && !orderPlaced) {
    navigate('/cart');
    return null;
  }

  if (orderPlaced) {
    return (
      <div style={{ padding: '2rem 0', minHeight: '60vh' }}>
        <div className="container">
          <div style={{ 
            textAlign: 'center',
            padding: '4rem 2rem',
            background: 'var(--bg-secondary)',
            borderRadius: '16px',
            border: '1px solid var(--border-color)',
            maxWidth: '600px',
            margin: '0 auto'
          }}>
            <div style={{ fontSize: '4rem', marginBottom: '1rem' }}>🎉</div>
            <h2 style={{ 
              color: 'var(--success)',
              marginBottom: '1rem'
            }}>
              Order Placed Successfully!
            </h2>
            <p style={{ 
              color: 'var(--text-secondary)',
              marginBottom: '2rem',
              fontSize: '1.1rem',
              lineHeight: '1.6'
            }}>
              Thank you for your order! We've sent a confirmation message to your WhatsApp. 
              You'll receive updates about your order status.
            </p>
            <div style={{ 
              display: 'flex',
              gap: '1rem',
              justifyContent: 'center',
              flexWrap: 'wrap'
            }}>
              <button
                onClick={() => navigate('/dashboard')}
                className="btn btn-primary"
              >
                View Orders
              </button>
              <button
                onClick={() => navigate('/products')}
                className="btn btn-outline"
              >
                Continue Shopping
              </button>
            </div>
          </div>
        </div>
      </div>
    );
  }

  if (showQR) {
    return (
      <div style={{ padding: '2rem 0', minHeight: '60vh' }}>
