import React, { useState, useEffect } from 'react';
import { useNavigate, useSearchParams } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import { useCart } from '../context/CartContext';
import { Search, Filter, Heart, ShoppingCart, Star } from 'lucide-react';

const ProductsPage = () => {
  const navigate = useNavigate();
  const [searchParams] = useSearchParams();
  const { user } = useAuth();
  const { addToCart } = useCart();
  const [products, setProducts] = useState([]);
  const [filteredProducts, setFilteredProducts] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedCategory, setSelectedCategory] = useState(searchParams.get('category') || 'all');
  const [sortBy, setSortBy] = useState('name');
  const [loading, setLoading] = useState(true);

  // Sample products data
  const sampleProducts = [
    {
      id: 1,
      name: 'Boho Chic Granny Square Crochet Top',
      category: 'crochet',
      price: 1299,
      image: '/images/crochet-boho-top.jpg',
      shortDescription: 'A vibrant and stylish handmade top featuring a classic granny square pattern.',
      rating: 4.8,
      reviewCount: 12,
      stockStatus: 'made-to-order',
      deliveryTime: 14,
      customization: [
        { type: 'thread-type', options: ['100% Cotton', 'Acrylic Blend'] },
        { type: 'color', options: ['As Pictured', 'Custom Colors'] },
        { type: 'size', options: ['S', 'M', 'L', 'Custom'] }
      ]
    },
    {
      id: 2,
      name: 'Classic Striped V-Neck Crochet Vest',
      category: 'crochet',
      price: 1199,
      image: '/images/crochet-striped-vest.jpg',
      shortDescription: 'A timeless and elegant V-neck vest with chic teal green and cream stripes.',
      rating: 4.9,
      reviewCount: 8,
      stockStatus: 'made-to-order',
      deliveryTime: 14,
      customization: [
        { type: 'thread-type', options: ['100% Cotton', 'Acrylic Blend'] },
        { type: 'color', options: ['As Pictured', 'Custom Colors'] },
        { type: 'size', options: ['S', 'M', 'L'] }
      ]
    },
    {
      id: 3,
      name: 'Minimalist Pink Crochet Tank Top',
      category: 'crochet',
      price: 999,
      image: '/images/crochet-pink-tank.jpg',
      shortDescription: 'Simple, sweet, and essential. This solid pink crochet tank top is a versatile wardrobe staple.',
      rating: 4.7,
      reviewCount: 15,
      stockStatus: 'made-to-order',
      deliveryTime: 10,
      customization: [
        { type: 'color', options: ['Bubblegum Pink', '30+ Colors Available'] },
        { type: 'size', options: ['S', 'M', 'L', 'XL'] }
      ]
    },
    {
      id: 4,
      name: 'Serene Blue & Pink Pooja Mat',
      category: 'crochet',
      price: 449,
      image: '/images/crochet-pooja-mat-blue.jpg',
      shortDescription: 'A beautiful circular mat, lovingly crocheted in calming shades of blue and pink.',
      rating: 4.9,
      reviewCount: 6,
      stockStatus: 'available',
      deliveryTime: 7,
      customization: [
        { type: 'color', options: ['As Pictured', 'Custom Colors'] }
      ]
    },
    {
      id: 5,
      name: 'Festive Multicolor Pooja Mat',
      category: 'crochet',
      price: 499,
      image: '/images/crochet-pooja-mat-multicolor.jpg',
      shortDescription: 'Brighten up your sacred space with this festive, multi-colored crocheted mat.',
      rating: 4.8,
      reviewCount: 4,
      stockStatus: 'available',
      deliveryTime: 7,
      customization: []
    },
    {
      id: 6,
      name: 'Homestyle Poha Chivda',
      category: 'food',
      price: 25,
      pricePerKg: 480,
      image: '/images/food-poha-chivda.jpg',
      shortDescription: 'A light, crispy, and savory Maharashtrian snack made with thin flattened rice.',
      rating: 4.6,
      reviewCount: 23,
      stockStatus: 'available',
      deliveryTime: 2,
      ingredients: ['Poha', 'Peanuts', 'Curry Leaves', 'Spices'],
      allergens: ['Peanuts']
    },
    {
      id: 7,
      name: 'Sweet & Flaky Shakarpara',
      category: 'food',
      price: 25,
      pricePerKg: 480,
      image: '/images/food-shakarpara.jpg',
      shortDescription: 'Traditional Indian sweet snack that melts in your mouth.',
      rating: 4.7,
      reviewCount: 18,
      stockStatus: 'available',
      deliveryTime: 2,
      ingredients: ['Maida', 'Ghee', 'Sugar', 'Cardamom'],
      allergens: ['Gluten', 'Dairy']
    },
    {
      id: 8,
      name: 'Crispy & Savory Namak Pare',
      category: 'food',
      price: 25,
      pricePerKg: 480,
      image: '/images/food-namak-pare.jpg',
      shortDescription: 'The perfect savory companion for your tea.',
      rating: 4.5,
      reviewCount: 21,
      stockStatus: 'available',
      deliveryTime: 2,
      ingredients: ['Maida', 'Ajwain', 'Salt'],
      allergens: ['Gluten']
    },
    {
      id: 9,
      name: 'Spicy Mixture Namkeen',
      category: 'food',
      price: 25,
      pricePerKg: 500,
      image: '/images/food-mixture.jpg',
      shortDescription: 'A flavor-packed medley of crispy sev, fried peanuts, and lentils.',
      rating: 4.8,
      reviewCount: 16,
      stockStatus: 'available',
      deliveryTime: 2,
      ingredients: ['Besan', 'Peanuts', 'Lentils', 'Spices'],
      allergens: ['Peanuts', 'Gluten']
    },
    {
      id: 10,
      name: 'Classic Salty Mathri',
      category: 'food',
      price: 25,
      pricePerKg: 480,
      image: '/images/food-mathri.jpg',
      shortDescription: 'Traditional North Indian flaky biscuit seasoned with ajwain.',
      rating: 4.6,
      reviewCount: 14,
      stockStatus: 'available',
      deliveryTime: 2,
      ingredients: ['Maida', 'Ghee', 'Ajwain', 'Salt'],
      allergens: ['Gluten', 'Dairy']
    },
    {
      id: 11,
      name: 'Baked Jeera Biscuits',
      category: 'food',
      price: 25,
      pricePerKg: 480,
      image: '/images/food-jeera-biscuits.webp',
      shortDescription: 'Light, savory, and crumbly biscuits flavored with roasted cumin.',
      rating: 4.4,
      reviewCount: 11,
      stockStatus: 'available',
      deliveryTime: 2,
      ingredients: ['Maida', 'Butter', 'Jeera', 'Salt'],
      allergens: ['Gluten', 'Dairy']
    },
    {
      id: 12,
      name: 'Homemade Gujiya',
      category: 'food',
      price: 600,
      image: '/images/food-gujiya.jpg',
      shortDescription: 'Classic festive delicacy filled with khoya, nuts, and coconut.',
      rating: 4.9,
      reviewCount: 7,
      stockStatus: 'made-to-order',
      deliveryTime: 3,
      ingredients: ['Maida', 'Khoya', 'Coconut', 'Nuts'],
      allergens: ['Gluten', 'Dairy', 'Nuts']
    }
  ];

  useEffect(() => {
    // Simulate API call
    setTimeout(() => {
      setProducts(sampleProducts);
      setFilteredProducts(sampleProducts);
      setLoading(false);
    }, 1000);
  }, []);

  useEffect(() => {
    let filtered = products;

    // Filter by category
    if (selectedCategory !== 'all') {
      filtered = filtered.filter(product => product.category === selectedCategory);
    }

    // Filter by search term
    if (searchTerm) {
      filtered = filtered.filter(product =>
        product.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
        product.shortDescription.toLowerCase().includes(searchTerm.toLowerCase()) ||
        (product.ingredients && product.ingredients.some(ing => 
          ing.toLowerCase().includes(searchTerm.toLowerCase())
        ))
      );
    }

    // Sort products
    const sorted = [...filtered].sort((a, b) => {
        switch (sortBy) {
            case 'price-low':
                return a.price - b.price;
            case 'price-high':
                return b.price - a.price;
            case 'rating':
                return b.rating - a.rating;
            case 'name':
            default:
                return a.name.localeCompare(b.name);
        }
    });

    setFilteredProducts(sorted);
  }, [products, selectedCategory, searchTerm, sortBy]);

  const ProductCard = ({ product }) => (
    <div 
      className="product-card"
      style={{
        border: '1px solid var(--border-color)',
        borderRadius: '16px',
        overflow: 'hidden',
        background: 'var(--bg-secondary)',
        transition: 'all 0.3s ease',
        cursor: 'pointer',
        position: 'relative'
      }}
      onClick={() => navigate(`/product/${product.id}`)}
    >
      <div style={{ position: 'relative' }}>
        <img
          src={product.image}
          alt={product.name}
          style={{
            width: '100%',
            height: '200px',
            objectFit: 'cover'
          }}
          onError={(e) => {
            e.target.src = '/images/placeholder.jpg';
          }}
        />
        <button
          style={{
            position: 'absolute',
            top: '10px',
            right: '10px',
            background: 'rgba(255,255,255,0.9)',
            border: 'none',
            borderRadius: '50%',
            width: '36px',
            height: '36px',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            cursor: 'pointer'
          }}
          onClick={(e) => {
            e.stopPropagation(); // <-- Fix: Prevents card navigation
            // Add to wishlist logic
            console.log('Wishlist clicked for product:', product.id);
          }}
        >
          <Heart size={16} />
        </button>
        {product.stockStatus === 'made-to-order' && (
          <div style={{
            position: 'absolute',
            top: '10px',
            left: '10px',
            background: 'var(--warning)',
            color: 'white',
            padding: '4px 8px',
            borderRadius: '12px',
            fontSize: '0.7rem',
            fontWeight: '600'
          }}>
            Made to Order
          </div>
        )}
      </div>

      <div style={{ padding: '1rem' }}>
        <h4 style={{ 
          margin: '0 0 0.5rem 0', 
          fontSize: '1rem',
          fontWeight: '600',
          lineHeight: '1.3'
        }}>
          {product.name}
        </h4>

        <p style={{ 
          color: 'var(--text-secondary)', 
          fontSize: '0.85rem',
          margin: '0 0 0.75rem 0',
          lineHeight: '1.4'
        }}>
          {product.shortDescription}
        </p>

        <div style={{ 
          display: 'flex', 
          alignItems: 'center', 
          gap: '0.5rem',
          marginBottom: '0.75rem'
        }}>
          <div style={{ display: 'flex', alignItems: 'center', gap: '0.25rem' }}>
            {[...Array(5)].map((_, i) => (
              <Star
                key={i}
                size={12}
                fill={i < Math.floor(product.rating) ? '#fbbf24' : 'none'}
                color={i < Math.floor(product.rating) ? '#fbbf24' : '#d1d5db'}
              />
            ))}
          </div>
          <span style={{ fontSize: '0.8rem', color: 'var(--text-secondary)' }}>
            {product.rating} ({product.reviewCount})
          </span>
        </div>

        <div style={{ 
          display: 'flex', 
          justifyContent: 'space-between', 
          alignItems: 'center',
          marginBottom: '0.75rem'
        }}>
          <div>
            <span style={{ 
              fontSize: '1.1rem', 
              fontWeight: '700',
              color: 'var(--primary-color)'
            }}>
              ‚Çπ{product.price}
            </span>
            {product.pricePerKg && (
              <span style={{ 
                fontSize: '0.8rem', 
                color: 'var(--text-secondary)',
                marginLeft: '0.5rem'
              }}>
                (‚Çπ{product.pricePerKg}/kg)
              </span>
            )}
          </div>
          <span style={{ 
            fontSize: '0.75rem',
            color: 'var(--text-secondary)',
            background: 'var(--bg-tertiary)',
            padding: '2px 6px',
            borderRadius: '8px'
          }}>
            {product.deliveryTime} days
          </span>
        </div>

        <div style={{ display: 'flex', gap: '0.5rem' }}>
          <button
            style={{
              flex: 1,
              background: 'transparent',
              color: 'var(--primary-color)',
              border: '1px solid var(--primary-color)',
              padding: '0.75rem',
              borderRadius: '8px',
              fontWeight: '600',
              cursor: 'pointer',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
              gap: '0.5rem',
              transition: 'all 0.2s ease'
            }}
            onClick={(e) => {
              e.stopPropagation(); // <-- Fix: Prevents card navigation
              if (!user) {
                localStorage.setItem('redirectAfterLogin', window.location.pathname);
                navigate('/login');
              } else {
                addToCart(product, 1, {});
                const button = e.currentTarget;
                button.disabled = true;
                button.style.background = '#10b981';
                button.style.color = 'white';
                button.innerHTML = '‚úì Added!';
                setTimeout(() => {
                  button.disabled = false;
                  button.style.background = 'transparent';
                  button.style.color = 'var(--primary-color)';
                  button.innerHTML = '<svg stroke="currentColor" fill="none" stroke-width="2" viewBox="0 0 24 24" stroke-linecap="round" stroke-linejoin="round" height="14" width="14" xmlns="http://www.w3.org/2000/svg"><circle cx="9" cy="21" r="1"></circle><circle cx="20" cy="21" r="1"></circle><path d="M1 1h4l2.68 13.39a2 2 0 0 0 2 1.61h9.72a2 2 0 0 0 2-1.61L23 6H6"></path></svg> Add to Cart';
                }, 1500);
              }
            }}
          >
            <ShoppingCart size={14} />
            Add to Cart
          </button>
          <button
            style={{
              flex: 1,
              background: 'var(--primary-color)',
              color: 'white',
              border: 'none',
              padding: '0.75rem',
              borderRadius: '8px',
              fontWeight: '600',
              cursor: 'pointer',
              transition: 'all 0.2s ease'
            }}
            onClick={(e) => {
              e.stopPropagation(); // <-- Fix: Prevents card navigation
              if (!user) {
                localStorage.setItem('redirectAfterLogin', window.location.pathname);
                navigate('/login');
              } else {
                addToCart(product, 1, {});
                navigate('/checkout');
              }
            }}
          >
            Buy Now
          </button>
        </div>
      </div>
    </div>
  );

  if (loading) {
    return (
      <div style={{ 
        minHeight: '60vh', 
        display: 'flex', 
        alignItems: 'center', 
        justifyContent: 'center' 
      }}>
        <div className="spinner"></div>
      </div>
    );
  }

  return (
    <div style={{ padding: '2rem 0', minHeight: '100vh' }}>
      <div className="container">
        {/* Header */}
        <div style={{ marginBottom: '2rem' }}>
          <h1 style={{ 
            fontSize: '2.5rem', 
            fontWeight: '800',
            color: 'var(--primary-color)',
            marginBottom: '0.5rem'
          }}>
            Our Products
          </h1>
          <p style={{ 
            color: 'var(--text-secondary)', 
            fontSize: '1.1rem' 
          }}>
            Discover our collection of handmade crochet items and homemade food
          </p>
        </div>

        {/* Search and Filters */}
        <div style={{ 
          display: 'grid', 
          gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))',
          gap: '1rem',
          marginBottom: '2rem',
          padding: '1.5rem',
          background: 'var(--bg-secondary)',
          borderRadius: '16px',
          border: '1px solid var(--border-color)'
        }}>
          {/* Search */}
          <div style={{ position: 'relative' }}>
            <Search 
              size={20} 
              style={{ 
                position: 'absolute', 
                left: '12px', 
                top: '50%', 
                transform: 'translateY(-50%)',
                color: 'var(--text-secondary)'
              }} 
            />
            <input
              type="text"
              placeholder="Search products, ingredients..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              style={{
                width: '100%',
                padding: '0.75rem 0.75rem 0.75rem 2.5rem',
                border: '1px solid var(--border-color)',
                borderRadius: '8px',
                fontSize: '0.9rem'
              }}
            />
          </div>

          {/* Category Filter */}
          <select
            value={selectedCategory}
            onChange={(e) => setSelectedCategory(e.target.value)}
            style={{
              padding: '0.75rem',
              border: '1px solid var(--border-color)',
              borderRadius: '8px',
              fontSize: '0.9rem'
            }}
          >
            <option value="all">All Categories</option>
            <option value="crochet">Crochet Items</option>
            <option value="food">Food Items</option>
          </select>

          {/* Sort */}
          <select
            value={sortBy}
            onChange={(e) => setSortBy(e.target.value)}
            style={{
              padding: '0.75rem',
              border: '1px solid var(--border-color)',
              borderRadius: '8px',
              fontSize: '0.9rem'
            }}
          >
            <option value="name">Sort by Name</option>
            <option value="price-low">Price: Low to High</option>
            <option value="price-high">Price: High to Low</option>
            <option value="rating">Highest Rated</option>
          </select>
        </div>

        {/* Results Count */}
        <div style={{ 
          marginBottom: '1.5rem',
          color: 'var(--text-secondary)',
          fontSize: '0.9rem'
        }}>
          Showing {filteredProducts.length} products
          {selectedCategory !== 'all' && ` in ${selectedCategory}`}
          {searchTerm && ` for "${searchTerm}"`}
        </div>

        {/* Products Grid */}
        {filteredProducts.length === 0 ? (
          <div style={{ 
            textAlign: 'center', 
            padding: '4rem 2rem',
            background: 'var(--bg-secondary)',
            borderRadius: '16px'
          }}>
            <div style={{ fontSize: '4rem', marginBottom: '1rem' }}>üîç</div>
            <h3 style={{ marginBottom: '0.5rem' }}>No products found</h3>
            <p style={{ color: 'var(--text-secondary)' }}>
              Try adjusting your search or filters
            </p>
          </div>
        ) : (
          <div style={{
            display: 'grid',
            gridTemplateColumns: 'repeat(auto-fill, minmax(280px, 1fr))',
            gap: '1.5rem'
          }}>
            {filteredProducts.map(product => (
              <ProductCard key={product.id} product={product} />
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default ProductsPage;
