import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { 
  Heart, 
  Star,
  ChevronDown,
  ShoppingBag,
  Users,
  Award,
  Truck,
  Shield
} from 'lucide-react';

const Home = () => {
  const [openFAQ, setOpenFAQ] = useState(null);

  const faqs = [
    {
      question: "What products do you offer?",
      answer: "We offer a variety of homemade food items and handcrafted crochet products. Each item is made with care, fresh ingredients, and quality materials."
    },
    {
      question: "How can I place an order?",
      answer: "Browse through our product categories, add your desired items to the cart, and proceed to checkout. You can then confirm your order and make payment via our secure QR code option or choose Cash on Delivery (COD)."
    },
    {
      question: "What is the expected delivery time?",
      answer: "Food Items: Delivered within 2 days of order confirmation to ensure freshness. Crochet Items: Delivered within 2 weeks, as they are custom-made to your preferred design, colors, and thread type."
    },
    {
      question: "Can I customize crochet products?",
      answer: "Yes! You can choose from available designs, colors, and thread types. Customization options are shown on each crochet product page."
    },
    {
      question: "How do I make a payment?",
      answer: "We accept payments via QR code or Cash on Delivery. Simply scan the QR code during checkout or select COD to pay at delivery."
    },
    {
      question: "Can I cancel my order?",
      answer: "Food items: Cancellation possible within 6 hours of placing the order. Crochet items: Cancellation possible within 24 hours of placing the order, as they are made-to-order."
    },
    {
      question: "Do you offer returns or refunds?",
      answer: "Due to the perishable nature of food items and the custom nature of crochet products, returns are not possible. However, if your order arrives damaged or incorrect, contact us within 24 hours for assistance."
    },
    {
      question: "How can I track my order?",
      answer: "Log in to your account and visit the 'My Orders' section to see the current status of your orders."
    },
    {
      question: "Do you offer bulk orders or special requests?",
      answer: "Yes! We accept bulk orders for both food and crochet items. If the quantity of food items is more than 2 kg, or if your order contains more than one crochet product, the delivery timeline may be longer."
    },
    {
      question: "How can I contact customer support?",
      answer: "You can reach us via the contact form on our website or through WhatsApp/phone for quick queries."
    }
  ];

  const features = [
    {
      icon: <Heart className="w-8 h-8 text-pink-500" />,
      title: "Made with Love",
      description: "Every product is crafted with care and passion"
    },
    {
      icon: <Truck className="w-8 h-8 text-blue-500" />,
      title: "Fast Delivery",
      description: "Food in 2 days, Crochet in 2 weeks"
    },
    {
      icon: <Shield className="w-8 h-8 text-green-500" />,
      title: "Quality Assured",
      description: "Fresh ingredients and premium materials"
    },
    {
      icon: <Award className="w-8 h-8 text-yellow-500" />,
      title: "Custom Made",
      description: "Personalized crochet items to your preference"
    }
  ];

  return (
    <div>
      {/* Hero Section */}
      <section style={{ 
        minHeight: '100vh', 
        display: 'flex', 
        alignItems: 'center', 
        background: 'linear-gradient(135deg, #fce7f3 0%, #f3e8ff 100%)',
        position: 'relative',
        overflow: 'hidden'
      }}>
        <div className="container" style={{ position: 'relative', zIndex: 2 }}>
          <div style={{ textAlign: 'center', maxWidth: '800px', margin: '0 auto' }}>
            <h1 style={{ 
              fontSize: '3.5rem', 
              fontWeight: '800', 
              marginBottom: '1.5rem',
              background: 'linear-gradient(135deg, #ec4899, #8b5cf6)',
              WebkitBackgroundClip: 'text',
              WebkitTextFillColor: 'transparent',
              lineHeight: '1.2'
            }}>
              Welcome to Stitch & Savour
            </h1>
            <p style={{ 
              fontSize: '1.3rem', 
              color: 'var(--text-secondary)', 
              marginBottom: '2rem',
              lineHeight: '1.6'
            }}>
              Where homemade food and handcrafted crochet come together, 
              straight from our home to your heart
            </p>
            <div style={{ display: 'flex', gap: '1rem', justifyContent: 'center', flexWrap: 'wrap' }}>
              <Link to="/products" className="btn btn-primary btn-lg">
                <ShoppingBag size={20} />
                Shop Now
              </Link>
              <Link to="/about" className="btn btn-outline btn-lg">
                <Users size={20} />
                Our Story
              </Link>
            </div>
          </div>
        </div>

        {/* Scroll indicator */}
        <div style={{ 
          position: 'absolute', 
          bottom: '2rem', 
          left: '50%', 
          transform: 'translateX(-50%)',
          animation: 'bounce 2s infinite'
        }}>
          <ChevronDown size={32} color="var(--text-secondary)" />
        </div>
      </section>

      {/* About Sangita Section */}
      <section style={{ padding: '5rem 0', background: 'var(--bg-secondary)' }}>
        <div className="container">
          <div style={{ 
            display: 'grid', 
            gridTemplateColumns: '1fr 1fr', 
            gap: '3rem', 
            alignItems: 'center',
            maxWidth: '1000px',
            margin: '0 auto'
          }}>
            <div>
              <img
                src="/images/sangita-photo.jpg"
                alt="Sangita Thakur"
                style={{
                  width: '100%',
                  maxWidth: '400px',
                  borderRadius: '16px',
                  boxShadow: '0 20px 40px rgba(0,0,0,0.1)'
                }}
                onError={(e) => {
                  e.target.src = '/images/placeholder.jpg';
                }}
              />
            </div>
            <div>
              <h2 style={{ 
                fontSize: '2.5rem', 
                fontWeight: '800', 
                marginBottom: '1.5rem',
                color: 'var(--primary-color)'
              }}>
                Meet Sangita Thakur 💛
              </h2>
              <p style={{ 
                fontSize: '1.1rem', 
                lineHeight: '1.8', 
                color: 'var(--text-secondary)', 
                marginBottom: '1.5rem' 
              }}>
                Welcome to Stitch & Savour! I'm Sangita, and every product here tells a story of warmth, 
                care, and creativity. What started as a hobby in the kitchen and a passion for crocheting 
                has grown into something beautiful that I'm excited to share with you.
              </p>
              <p style={{ 
                fontSize: '1.1rem', 
                lineHeight: '1.8', 
                color: 'var(--text-secondary)', 
                marginBottom: '2rem' 
              }}>
                From homemade delicacies crafted with traditional methods to handmade crochet items 
                woven with patience and love, everything here is made from our home to yours.
              </p>
              <Link to="/about" className="btn btn-primary">
                Read Our Story
              </Link>
            </div>
          </div>
        </div>
      </section>

      {/* Features Section */}
      <section style={{ padding: '5rem 0' }}>
        <div className="container">
          <div style={{ textAlign: 'center', marginBottom: '3rem' }}>
            <h2 style={{ 
              fontSize: '2.5rem', 
              fontWeight: '800', 
              marginBottom: '1rem',
              color: 'var(--primary-color)'
            }}>
              Why Choose Stitch & Savour?
            </h2>
            <p style={{ 
              fontSize: '1.2rem', 
              color: 'var(--text-secondary)',
              maxWidth: '600px',
              margin: '0 auto'
            }}>
              Experience the difference of handmade quality
            </p>
          </div>

          <div style={{ 
            display: 'grid', 
            gridTemplateColumns: 'repeat(auto-fit, minmax(250px, 1fr))', 
            gap: '2rem' 
          }}>
            {features.map((feature, index) => (
              <div
                key={index}
                style={{
                  textAlign: 'center',
                  padding: '2rem',
                  background: 'var(--bg-secondary)',
                  borderRadius: '16px',
                  border: '1px solid var(--border-color)',
                  transition: 'transform 0.3s ease, box-shadow 0.3s ease'
                }}
                onMouseEnter={(e) => {
                  e.currentTarget.style.transform = 'translateY(-4px)';
                  e.currentTarget.style.boxShadow = '0 20px 40px rgba(0,0,0,0.1)';
                }}
                onMouseLeave={(e) => {
                  e.currentTarget.style.transform = 'translateY(0)';
                  e.currentTarget.style.boxShadow = 'none';
                }}
              >
                <div style={{ marginBottom: '1rem' }}>
                  {feature.icon}
                </div>
                <h3 style={{ 
                  fontSize: '1.3rem', 
                  fontWeight: '700', 
                  marginBottom: '1rem',
                  color: 'var(--text-primary)'
                }}>
                  {feature.title}
                </h3>
                <p style={{ 
                  color: 'var(--text-secondary)',
                  lineHeight: '1.6'
                }}>
                  {feature.description}
                </p>
              </div>
            ))}
          </div>
        </div>
      </section>

      {/* Product Categories */}
      <section style={{ padding: '5rem 0', background: 'var(--bg-secondary)' }}>
        <div className="container">
          <div style={{ textAlign: 'center', marginBottom: '3rem' }}>
            <h2 style={{ 
              fontSize: '2.5rem', 
              fontWeight: '800', 
              marginBottom: '1rem',
              color: 'var(--primary-color)'
            }}>
              Our Product Categories
            </h2>
            <p style={{ 
              fontSize: '1.2rem', 
              color: 'var(--text-secondary)',
              maxWidth: '600px',
              margin: '0 auto'
            }}>
              Discover our range of homemade food and handcrafted items
            </p>
          </div>

          <div style={{ 
            display: 'grid', 
            gridTemplateColumns: '1fr 1fr', 
            gap: '2rem',
            maxWidth: '800px',
            margin: '0 auto'
          }}>
            <Link
              to="/products?category=food"
              style={{
                position: 'relative',
                overflow: 'hidden',
                borderRadius: '16px',
                textDecoration: 'none',
                color: 'white',
                minHeight: '300px',
                display: 'flex',
                alignItems: 'end',
                background: 'linear-gradient(135deg, #10b981, #059669)',
                transition: 'transform 0.3s ease'
              }}
              onMouseEnter={(e) => {
                e.currentTarget.style.transform = 'scale(1.02)';
              }}
              onMouseLeave={(e) => {
                e.currentTarget.style.transform = 'scale(1)';
              }}
            >
              <div style={{ padding: '2rem', width: '100%' }}>
                <div style={{ fontSize: '3rem', marginBottom: '1rem' }}>🍲</div>
                <h3 style={{ fontSize: '1.8rem', fontWeight: '700', marginBottom: '0.5rem' }}>
                  Homemade Food
                </h3>
                <p style={{ fontSize: '1rem', opacity: 0.9 }}>
                  Fresh, traditional recipes made with love
                </p>
              </div>
            </Link>

            <Link
              to="/products?category=crochet"
              style={{
                position: 'relative',
                overflow: 'hidden',
                borderRadius: '16px',
                textDecoration: 'none',
                color: 'white',
                minHeight: '300px',
                display: 'flex',
                alignItems: 'end',
                background: 'linear-gradient(135deg, #ec4899, #db2777)',
                transition: 'transform 0.3s ease'
              }}
              onMouseEnter={(e) => {
                e.currentTarget.style.transform = 'scale(1.02)';
              }}
              onMouseLeave={(e) => {
                e.currentTarget.style.transform = 'scale(1)';
              }}
            >
              <div style={{ padding: '2rem', width: '100%' }}>
                <div style={{ fontSize: '3rem', marginBottom: '1rem' }}>🧶</div>
                <h3 style={{ fontSize: '1.8rem', fontWeight: '700', marginBottom: '0.5rem' }}>
                  Handcrafted Crochet
                </h3>
                <p style={{ fontSize: '1rem', opacity: 0.9 }}>
                  Custom designs made just for you
                </p>
              </div>
            </Link>
          </div>
        </div>
      </section>

      {/* FAQ Section */}
      <section style={{ padding: '5rem 0' }}>
        <div className="container">
          <div style={{ textAlign: 'center', marginBottom: '3rem' }}>
            <h2 style={{ 
              fontSize: '2.5rem', 
              fontWeight: '800', 
              marginBottom: '1rem',
              color: 'var(--primary-color)'
            }}>
              Frequently Asked Questions
            </h2>
            <p style={{ 
              fontSize: '1.2rem', 
              color: 'var(--text-secondary)',
              maxWidth: '600px',
              margin: '0 auto'
            }}>
              Find answers to common questions about our products and services
            </p>
          </div>

          <div style={{ maxWidth: '800px', margin: '0 auto' }}>
            {faqs.map((faq, index) => (
              <div
                key={index}
                style={{
                  background: 'var(--bg-secondary)',
                  borderRadius: '12px',
                  border: '1px solid var(--border-color)',
                  marginBottom: '1rem',
                  overflow: 'hidden'
                }}
              >
                <button
                  onClick={() => setOpenFAQ(openFAQ === index ? null : index)}
                  style={{
                    width: '100%',
                    padding: '1.5rem',
                    textAlign: 'left',
                    background: 'none',
                    border: 'none',
                    cursor: 'pointer',
                    display: 'flex',
                    justifyContent: 'space-between',
                    alignItems: 'center',
                    fontSize: '1.1rem',
                    fontWeight: '600',
                    color: 'var(--text-primary)'
                  }}
                >
                  {faq.question}
                  <ChevronDown
                    size={20}
                    style={{
                      transform: openFAQ === index ? 'rotate(180deg)' : 'rotate(0deg)',
                      transition: 'transform 0.3s ease'
                    }}
                  />
                </button>
                {openFAQ === index && (
                  <div style={{ 
                    padding: '0 1.5rem 1.5rem 1.5rem',
                    color: 'var(--text-secondary)',
                    lineHeight: '1.6'
                  }}>
                    {faq.answer}
                  </div>
                )}
              </div>
            ))}
          </div>
        </div>
      </section>

      {/* CTA Section */}
      <section style={{ 
        padding: '5rem 0', 
        background: 'linear-gradient(135deg, #ec4899, #8b5cf6)',
        color: 'white'
      }}>
        <div className="container">
          <div style={{ textAlign: 'center', maxWidth: '600px', margin: '0 auto' }}>
            <h2 style={{ 
              fontSize: '2.5rem', 
              fontWeight: '800', 
              marginBottom: '1rem'
            }}>
              Ready to Experience Handmade Quality?
            </h2>
            <p style={{ 
              fontSize: '1.2rem', 
              marginBottom: '2rem',
              opacity: 0.9
            }}>
              Browse our collection of homemade food and handcrafted crochet items
            </p>
            <Link
              to="/products"
              style={{
                display: 'inline-flex',
                alignItems: 'center',
                gap: '0.5rem',
                background: 'white',
                color: 'var(--primary-color)',
                padding: '1rem 2rem',
                borderRadius: '12px',
                textDecoration: 'none',
                fontWeight: '600',
                fontSize: '1.1rem',
                transition: 'transform 0.3s ease'
              }}
              onMouseEnter={(e) => {
                e.currentTarget.style.transform = 'translateY(-2px)';
              }}
              onMouseLeave={(e) => {
                e.currentTarget.style.transform = 'translateY(0)';
              }}
            >
              <ShoppingBag size={20} />
              Shop Now
            </Link>
          </div>
        </div>
      </section>
    </div>
  );
};

export default Home;
