import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import { useCart } from '../context/CartContext';
import Chatbot from '../components/Chatbot';
import { 
  Package, 
  ShoppingBag, 
  Heart, 
  User, 
  MapPin, 
  Phone, 
  Mail,
  Clock,
  CheckCircle,
  Truck,
  Star,
  MessageCircle,
  CreditCard,
  AlertCircle,
  Calendar,
  Search,
  Filter,
  Download,
  Eye
} from 'lucide-react';

const UserDashboard = () => {
  const { user } = useAuth();
  const { cartItems } = useCart();
  const navigate = useNavigate();
  const [activeTab, setActiveTab] = useState('overview');
  const [orders, setOrders] = useState([]);
  const [wishlist, setWishlist] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');
  const [statusFilter, setStatusFilter] = useState('all');
  const [paymentDue, setPaymentDue] = useState(0);

  useEffect(() => {
    fetchUserData();
  }, []);

  const getExpectedDeliveryDate = (productType, orderDate) => {
    const date = new Date(orderDate);
    if (productType === 'food') {
      date.setDate(date.getDate() + 2); // 2 days for food
    } else if (productType === 'crochet') {
      date.setDate(date.getDate() + 14); // 2 weeks for crochet
    }
    return date;
  };

  const getGreetingMessage = () => {
    const hour = new Date().getHours();
    if (hour < 12) return 'Good morning';
    if (hour < 17) return 'Good afternoon';
    return 'Good evening';
  };

  const filteredOrders = orders.filter(order => {
    const matchesSearch = order.id.toString().includes(searchTerm) || 
                         order.customerName?.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesStatus = statusFilter === 'all' || order.status === statusFilter;
    return matchesSearch && matchesStatus;
  });

  const fetchUserData = async () => {
    try {
      // Mock data for demonstration
      const mockOrders = [
        {
          id: 'ORD001',
          customerName: user?.name,
          status: 'in-progress',
          totalAmount: 450,
          paymentStatus: 'paid',
          createdAt: new Date().toISOString(),
          expectedDelivery: getExpectedDeliveryDate('food', new Date()).toISOString(),
          productType: 'food',
          reviewed: false
        },
        {
          id: 'ORD002',
          customerName: user?.name,
          status: 'delivered',
          totalAmount: 1200,
          paymentStatus: 'cod',
          createdAt: new Date(Date.now() - 7 * 24 * 60 * 60 * 1000).toISOString(),
          expectedDelivery: getExpectedDeliveryDate('crochet', new Date(Date.now() - 7 * 24 * 60 * 60 * 1000)).toISOString(),
          productType: 'crochet',
          reviewed: false
        }
      ];

      setOrders(mockOrders);
      setWishlist([]);
      setPaymentDue(mockOrders.filter(o => o.paymentStatus === 'cod' && o.status !== 'delivered').reduce((sum, o) => sum + o.totalAmount, 0));
      setLoading(false);
    } catch (error) {
      console.error('Error fetching user data:', error);
      setLoading(false);
    }
  };

  const getStatusIcon = (status) => {
    switch (status) {
      case 'received':
        return <Clock className="w-4 h-4 text-blue-500" />;
      case 'in-progress':
        return <Package className="w-4 h-4 text-yellow-500" />;
      case 'out-for-delivery':
        return <Truck className="w-4 h-4 text-orange-500" />;
      case 'delivered':
        return <CheckCircle className="w-4 h-4 text-green-500" />;
      default:
        return <Clock className="w-4 h-4 text-gray-500" />;
    }
  };

  const getStatusColor = (status) => {
    switch (status) {
      case 'received':
        return 'bg-blue-100 text-blue-800';
      case 'in-progress':
        return 'bg-yellow-100 text-yellow-800';
      case 'out-for-delivery':
        return 'bg-orange-100 text-orange-800';
      case 'delivered':
        return 'bg-green-100 text-green-800';
      case 'cancelled':
        return 'bg-red-100 text-red-800';
      default:
        return 'bg-gray-100 text-gray-800';
    }
  };

  const tabs = [
    { id: 'overview', label: 'Overview', icon: <User className="w-4 h-4" /> },
    { id: 'orders', label: 'My Orders', icon: <Package className="w-4 h-4" /> },
    { id: 'categories', label: 'Shop Categories', icon: <ShoppingBag className="w-4 h-4" /> },
    { id: 'wishlist', label: 'Wishlist', icon: <Heart className="w-4 h-4" /> },
    { id: 'profile', label: 'Profile', icon: <User className="w-4 h-4" /> }
  ];

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-pink-600"></div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50 dark:bg-gray-900 py-8">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        {/* Welcome Header */}
        <div className="mb-8">
          <div className="bg-gradient-to-r from-purple-600 to-pink-600 rounded-2xl p-8 text-white">
            <h1 className="text-4xl font-bold mb-2">
              {getGreetingMessage()}, {user?.name}! 
            </h1>
            <p className="text-purple-100 text-lg">
              We're happy to see you again. Here's what's happening with your orders.
            </p>
            {paymentDue > 0 && (
              <div className="mt-4 bg-white/20 backdrop-blur-sm rounded-lg p-4 flex items-center gap-3">
                <AlertCircle className="w-5 h-5" />
                <span>Payment Due: {paymentDue}</span>
                <button className="ml-auto bg-white text-purple-600 px-4 py-2 rounded-lg font-semibold hover:bg-purple-50">
                  Pay Now
                </button>
              </div>
            )}
          </div>
        </div>

        {/* Quick Stats */}
        <div className="grid grid-cols-1 md:grid-cols-4 gap-6 mb-8">
          <div className="bg-white dark:bg-gray-800 rounded-lg shadow p-6">
            <div className="flex items-center">
              <div className="p-2 bg-pink-100 dark:bg-pink-900/20 rounded-lg">
                <Package className="w-6 h-6 text-pink-600" />
              </div>
              <div className="ml-4">
                <p className="text-sm font-medium text-gray-600 dark:text-gray-400">Total Orders</p>
                <p className="text-2xl font-bold text-gray-900 dark:text-white">{orders.length}</p>
              </div>
            </div>
          </div>

          <div className="bg-white dark:bg-gray-800 rounded-lg shadow p-6">
            <div className="flex items-center">
              <div className="p-2 bg-green-100 dark:bg-green-900/20 rounded-lg">
                <CheckCircle className="w-6 h-6 text-green-600" />
              </div>
              <div className="ml-4">
                <p className="text-sm font-medium text-gray-600 dark:text-gray-400">Delivered</p>
                <p className="text-2xl font-bold text-gray-900 dark:text-white">
                  {orders.filter(order => order.status === 'delivered').length}
                </p>
              </div>
            </div>
          </div>

          <div className="bg-white dark:bg-gray-800 rounded-lg shadow p-6">
            <div className="flex items-center">
              <div className="p-2 bg-blue-100 dark:bg-blue-900/20 rounded-lg">
                <ShoppingBag className="w-6 h-6 text-blue-600" />
              </div>
              <div className="ml-4">
                <p className="text-sm font-medium text-gray-600 dark:text-gray-400">Cart Items</p>
                <p className="text-2xl font-bold text-gray-900 dark:text-white">{cartItems.length}</p>
              </div>
            </div>
          </div>

          <div className="bg-white dark:bg-gray-800 rounded-lg shadow p-6">
            <div className="flex items-center">
              <div className="p-2 bg-orange-100 dark:bg-orange-900/20 rounded-lg">
                <CreditCard className="w-6 h-6 text-orange-600" />
              </div>
              <div className="ml-4">
                <p className="text-sm font-medium text-gray-600 dark:text-gray-400">Payment Due</p>
                <p className="text-2xl font-bold text-gray-900 dark:text-white">{paymentDue}</p>
              </div>
            </div>
          </div>
        </div>

        {/* Tab Navigation */}
        <div className="bg-white dark:bg-gray-800 rounded-lg shadow mb-8">
          <div className="border-b border-gray-200 dark:border-gray-700">
            <nav className="flex space-x-8 px-6">
              {tabs.map((tab) => (
                <button
                  key={tab.id}
                  onClick={() => setActiveTab(tab.id)}
                  className={`py-4 px-1 border-b-2 font-medium text-sm flex items-center space-x-2 ${
                    activeTab === tab.id
                      ? 'border-pink-500 text-pink-600 dark:text-pink-400'
                      : 'border-transparent text-gray-500 hover:text-gray-700 dark:text-gray-400 dark:hover:text-gray-300'
                  }`}
                >
                  {tab.icon}
                  <span>{tab.label}</span>
                </button>
              ))}
            </nav>
          </div>

          {/* Tab Content */}
          <div className="p-6">
            {activeTab === 'overview' && (
              <div className="space-y-6">
                <h3 className="text-lg font-semibold text-gray-900 dark:text-white">Recent Orders</h3>
                {orders.length === 0 ? (
                  <div className="text-center py-8">
                    <Package className="w-12 h-12 text-gray-400 mx-auto mb-4" />
                    <p className="text-gray-500 dark:text-gray-400">No orders yet</p>
                    <a
                      href="/products"
                      className="mt-4 bg-pink-600 text-white px-6 py-2 rounded-lg hover:bg-pink-700 inline-block"
                    >
                      Start Shopping
                    </a>
                  </div>
                ) : (
                  <div className="space-y-4">
                    {orders.slice(0, 3).map((order) => (
                      <div key={order.id} className="border border-gray-200 dark:border-gray-700 rounded-lg p-4">
                        <div className="flex items-center justify-between">
                          <div className="flex items-center space-x-3">
                            {getStatusIcon(order.status)}
                            <div>
                              <p className="font-medium text-gray-900 dark:text-white">Order #{order.id}</p>
                              <p className="text-sm text-gray-500 dark:text-gray-400">
                                {new Date(order.createdAt).toLocaleDateString()}
                              </p>
                            </div>
                          </div>
                          <div className="text-right">
                            <span className={`px-2 py-1 rounded-full text-xs font-medium ${getStatusColor(order.status)}`}>
                              {order.status.replace('-', ' ').toUpperCase()}
                            </span>
                            <p className="text-sm font-medium text-gray-900 dark:text-white mt-1">
                              {order.totalAmount}
                            </p>
                          </div>
                        </div>
                      </div>
                    ))}
                  </div>
                )}
              </div>
            )}

            {activeTab === 'orders' && (
              <div className="space-y-6">
                <div className="flex flex-col sm:flex-row gap-4 items-start sm:items-center justify-between">
                  <h3 className="text-lg font-semibold text-gray-900 dark:text-white">All Orders</h3>
                  <div className="flex gap-3 w-full sm:w-auto">
                    <div className="relative flex-1 sm:flex-none">
                      <Search className="w-4 h-4 absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400" />
                      <input
                        type="text"
                        placeholder="Search orders..."
                        value={searchTerm}
                        onChange={(e) => setSearchTerm(e.target.value)}
                        className="pl-10 pr-4 py-2 border border-gray-300 rounded-lg w-full sm:w-64"
                      />
                    </div>
                    <select 
                      value={statusFilter}
                      onChange={(e) => setStatusFilter(e.target.value)}
                      className="px-3 py-2 border border-gray-300 rounded-lg"
                    >
                      <option value="all">All Status</option>
                      <option value="received">Order Received</option>
                      <option value="in-progress">In Progress</option>
                      <option value="out-for-delivery">Out for Delivery</option>
                      <option value="delivered">Delivered</option>
                      <option value="cancelled">Cancelled</option>
                    </select>
                  </div>
                </div>

                {orders.length === 0 ? (
                  <div className="text-center py-12">
                    <Package className="w-16 h-16 text-gray-400 mx-auto mb-4" />
                    <h4 className="text-lg font-medium text-gray-900 dark:text-white mb-2">No orders found</h4>
                    <p className="text-gray-500 dark:text-gray-400 mb-6">
                      You haven't placed any orders yet. Start shopping to see your orders here.
                    </p>
                    <a
                      href="/products"
                      className="bg-pink-600 text-white px-6 py-3 rounded-lg hover:bg-pink-700 inline-block"
                    >
                      Browse Products
                    </a>
                  </div>
                ) : (
                  <div className="space-y-4">
                    {filteredOrders.map((order) => (
                      <div key={order.id} className="border border-gray-200 dark:border-gray-700 rounded-lg p-6 hover:shadow-lg transition-shadow">
                        <div className="flex items-center justify-between mb-4">
                          <div>
                            <h4 className="font-semibold text-gray-900 dark:text-white">Order #{order.id}</h4>
                            <p className="text-sm text-gray-500 dark:text-gray-400">
                              Placed on {new Date(order.createdAt).toLocaleDateString()}
                            </p>
                          </div>
                          <div className="flex items-center gap-3">
                            <span className={`px-3 py-1 rounded-full text-sm font-medium ${getStatusColor(order.status)}`}>
                              {order.status.replace('-', ' ').toUpperCase()}
                            </span>
                            <button className="text-blue-600 hover:text-blue-800">
                              <Eye className="w-4 h-4" />
                            </button>
                          </div>
                        </div>

                        {/* Order Tracking Bar */}
                        <div className="mb-4">
                          <div className="flex items-center justify-between text-xs text-gray-500 mb-2">
                            <span>Order Received</span>
                            <span>In Progress</span>
                            <span>Out for Delivery</span>
                            <span>Delivered</span>
                          </div>
                          <div className="w-full bg-gray-200 rounded-full h-2">
                            <div 
                              className={`h-2 rounded-full transition-all duration-300 ${
                                order.status === 'received' ? 'bg-blue-500 w-1/4' :
                                order.status === 'in-progress' ? 'bg-yellow-500 w-2/4' :
                                order.status === 'out-for-delivery' ? 'bg-orange-500 w-3/4' :
                                order.status === 'delivered' ? 'bg-green-500 w-full' :
                                'bg-gray-300 w-0'
                              }`}
                            />
                          </div>
                        </div>

                        <div className="border-t border-gray-200 dark:border-gray-700 mt-4 pt-4 flex items-center justify-between">
                          <div className="flex items-center gap-4">
                            <div className="text-sm text-gray-500 dark:text-gray-400">
                              <Calendar className="w-4 h-4 inline mr-1" />
                              Expected: {order.expectedDelivery ? new Date(order.expectedDelivery).toLocaleDateString() : 'TBD'}
                            </div>
                            <div className="text-sm">
                              <span className={`px-2 py-1 rounded text-xs ${
                                order.paymentStatus === 'paid' ? 'bg-green-100 text-green-800' : 'bg-yellow-100 text-yellow-800'
                              }`}>
                                {order.paymentStatus === 'paid' ? 'Paid' : 'COD'}
                              </span>
                            </div>
                          </div>
                          <div className="text-lg font-semibold text-gray-900 dark:text-white">
                            Total: {order.totalAmount}
                          </div>
                        </div>

                        {order.status === 'delivered' && !order.reviewed && (
                          <div className="mt-4 p-3 bg-yellow-50 dark:bg-yellow-900/20 rounded-lg">
                            <p className="text-sm text-yellow-800 dark:text-yellow-200 mb-2">
                              How was your experience? Leave a review!
                            </p>
                            <button className="bg-yellow-600 text-white px-4 py-2 rounded-lg text-sm hover:bg-yellow-700">
                              Write Review
                            </button>
                          </div>
                        )}
                      </div>
                    ))}
                  </div>
                )}
              </div>
            )}

            {activeTab === 'categories' && (
              <div className="space-y-6">
                <h3 className="text-lg font-semibold text-gray-900 dark:text-white">Shop by Categories</h3>
                <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                  <Link
                    to="/products?category=food"
                    className="group relative overflow-hidden rounded-2xl bg-gradient-to-br from-orange-400 to-red-500 p-8 text-white hover:scale-105 transition-transform"
                  >
                    <div className="absolute top-4 right-4 text-6xl opacity-20"></div>
                    <div className="relative z-10">
                      <h4 className="text-2xl font-bold mb-2">Homemade Food</h4>
                      <p className="text-orange-100 mb-4">Fresh, traditional recipes made with love</p>
                      <div className="flex items-center gap-2 text-sm bg-white/20 backdrop-blur-sm rounded-full px-3 py-1 w-fit">
                        <Clock className="w-4 h-4" />
                        <span>2 days delivery</span>
                      </div>
                    </div>
                  </Link>

                  <Link
                    to="/products?category=crochet"
                    className="group relative overflow-hidden rounded-2xl bg-gradient-to-br from-purple-500 to-pink-500 p-8 text-white hover:scale-105 transition-transform"
                  >
                    <div className="absolute top-4 right-4 text-6xl opacity-20"></div>
                    <div className="relative z-10">
                      <h4 className="text-2xl font-bold mb-2">Handcrafted Crochet</h4>
                      <p className="text-purple-100 mb-4">Custom designs made just for you</p>
                      <div className="flex items-center gap-2 text-sm bg-white/20 backdrop-blur-sm rounded-full px-3 py-1 w-fit">
                        <Clock className="w-4 h-4" />
                        <span>2 weeks delivery</span>
                      </div>
                    </div>
                  </Link>
                </div>

                <div className="bg-gradient-to-r from-blue-50 to-indigo-50 dark:from-blue-900/20 dark:to-indigo-900/20 rounded-xl p-6">
                  <h4 className="text-lg font-semibold text-gray-900 dark:text-white mb-3">Quick Actions</h4>
                  <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
                    <Link to="/cart" className="flex flex-col items-center p-4 bg-white dark:bg-gray-800 rounded-lg hover:shadow-md transition-shadow">
                      <ShoppingBag className="w-8 h-8 text-blue-600 mb-2" />
                      <span className="text-sm font-medium">View Cart</span>
                      {cartItems.length > 0 && (
                        <span className="text-xs text-blue-600">({cartItems.length} items)</span>
                      )}
                    </Link>
                    <button className="flex flex-col items-center p-4 bg-white dark:bg-gray-800 rounded-lg hover:shadow-md transition-shadow">
                      <Heart className="w-8 h-8 text-red-600 mb-2" />
                      <span className="text-sm font-medium">Wishlist</span>
                    </button>
                    <button className="flex flex-col items-center p-4 bg-white dark:bg-gray-800 rounded-lg hover:shadow-md transition-shadow">
                      <Search className="w-8 h-8 text-green-600 mb-2" />
                      <span className="text-sm font-medium">Search</span>
                    </button>
                    <Link to="/contact" className="flex flex-col items-center p-4 bg-white dark:bg-gray-800 rounded-lg hover:shadow-md transition-shadow">
                      <MessageCircle className="w-8 h-8 text-purple-600 mb-2" />
                      <span className="text-sm font-medium">Contact</span>
                    </Link>
                  </div>
                </div>
              </div>
            )}

            {activeTab === 'wishlist' && (
              <div className="space-y-6">
                <h3 className="text-lg font-semibold text-gray-900 dark:text-white">My Wishlist</h3>
                {wishlist.length === 0 ? (
                  <div className="text-center py-12">
                    <Heart className="w-16 h-16 text-gray-400 mx-auto mb-4" />
                    <h4 className="text-lg font-medium text-gray-900 dark:text-white mb-2">Your wishlist is empty</h4>
                    <p className="text-gray-500 dark:text-gray-400 mb-6">
                      Save items you love to your wishlist and shop them later.
                    </p>
                    <a
                      href="/products"
                      className="bg-pink-600 text-white px-6 py-3 rounded-lg hover:bg-pink-700 inline-block"
                    >
                      Discover Products
                    </a>
                  </div>
                ) : (
                  <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                    {wishlist.map((product) => (
                      <div key={product.id} className="border border-gray-200 dark:border-gray-700 rounded-lg p-4">
                        <img
                          src={product.images?.[0] || '/images/placeholder.jpg'}
                          alt={product.name}
                          className="w-full h-48 object-cover rounded-lg mb-4"
                        />
                        <h4 className="font-medium text-gray-900 dark:text-white mb-2">{product.name}</h4>
                        <p className="text-sm text-gray-500 dark:text-gray-400 mb-3">{product.shortDescription}</p>
                        <div className="flex items-center justify-between">
                          <span className="text-lg font-semibold text-gray-900 dark:text-white">{product.price}</span>
                          <div className="flex items-center space-x-2">
                            <button className="text-pink-600 hover:text-pink-700">
                              <Heart className="w-5 h-5 fill-current" />
                            </button>
                            <button className="bg-pink-600 text-white px-4 py-2 rounded-lg text-sm hover:bg-pink-700">
                              Add to Cart
                            </button>
                          </div>
                        </div>
                      </div>
                    ))}
                  </div>
                )}
              </div>
            )}

            {activeTab === 'profile' && (
              <div className="space-y-6">
                <h3 className="text-lg font-semibold text-gray-900 dark:text-white">Profile Information</h3>
                <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                  <div className="space-y-4">
                    <div>
                      <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">Full Name</label>
                      <div className="flex items-center space-x-3 p-3 bg-gray-50 dark:bg-gray-700 rounded-lg">
                        <User className="w-5 h-5 text-gray-400" />
                        <span className="text-gray-900 dark:text-white">{user?.name}</span>
                      </div>
                    </div>

                    <div>
                      <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">Email Address</label>
                      <div className="flex items-center space-x-3 p-3 bg-gray-50 dark:bg-gray-700 rounded-lg">
                        <Mail className="w-5 h-5 text-gray-400" />
                        <span className="text-gray-900 dark:text-white">{user?.email}</span>
                      </div>
                    </div>

                    <div>
                      <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">Phone Number</label>
                      <div className="flex items-center space-x-3 p-3 bg-gray-50 dark:bg-gray-700 rounded-lg">
                        <Phone className="w-5 h-5 text-gray-400" />
                        <span className="text-gray-900 dark:text-white">{user?.phone}</span>
                      </div>
                    </div>
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">Delivery Address</label>
                    <div className="flex items-start space-x-3 p-3 bg-gray-50 dark:bg-gray-700 rounded-lg">
                      <MapPin className="w-5 h-5 text-gray-400 mt-1" />
                      <span className="text-gray-900 dark:text-white">{user?.address}</span>
                    </div>
                  </div>
                </div>

                <div className="flex space-x-4">
                  <button className="bg-pink-600 text-white px-6 py-2 rounded-lg hover:bg-pink-700">
                    Edit Profile
                  </button>
                  <button className="border border-gray-300 dark:border-gray-600 text-gray-700 dark:text-gray-300 px-6 py-2 rounded-lg hover:bg-gray-50 dark:hover:bg-gray-700">
                    Change Password
                  </button>
                </div>
              </div>
            )}
          </div>
        </div>

        {/* Contact & Help Section */}
        <div className="bg-gradient-to-r from-green-500 to-teal-600 rounded-2xl p-8 text-white">
          <div className="text-center mb-6">
            <h3 className="text-2xl font-bold mb-2">Need Help or Have Questions?</h3>
            <p className="text-green-100">We're here to help you with anything you need!</p>
          </div>

          <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
            <a
              href="https://wa.me/919970944685"
              target="_blank"
              rel="noopener noreferrer"
              className="bg-white/20 backdrop-blur-sm rounded-xl p-6 text-center hover:bg-white/30 transition-colors"
            >
              <MessageCircle className="w-12 h-12 mx-auto mb-4" />
              <h4 className="font-semibold mb-2">WhatsApp Support</h4>
              <p className="text-sm text-green-100">Quick responses via WhatsApp</p>
            </a>

            <Link
              to="/contact"
              className="bg-white/20 backdrop-blur-sm rounded-xl p-6 text-center hover:bg-white/30 transition-colors"
            >
              <Mail className="w-12 h-12 mx-auto mb-4" />
              <h4 className="font-semibold mb-2">Contact Form</h4>
              <p className="text-sm text-green-100">Send us detailed queries</p>
            </Link>

            <div className="bg-white/20 backdrop-blur-sm rounded-xl p-6 text-center">
              <Phone className="w-12 h-12 mx-auto mb-4" />
              <h4 className="font-semibold mb-2">Call Us</h4>
              <p className="text-sm text-green-100">+91 9970944685</p>
              <p className="text-xs text-green-100 mt-1">Alt: +91 8668806190</p>
            </div>
          </div>
        </div>

        {/* Chatbot */}
        <Chatbot />
      </div>
    </div>
  );
};

export default UserDashboard;
