                    } else if (deleteConfirm.type === 'inventory') {
                      deleteInventoryItem(deleteConfirm.id);
                    }
                  }}
                  className="btn"
                  style={{ background: 'var(--error)', color: 'white', minWidth: '100px' }}
                >
                  Delete
                </button>
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default AdminDashboard;
