import React, { useState, useEffect } from 'react';
import { Bell, X, MessageCircle } from 'lucide-react';
import { useAuth } from '../context/AuthContext';

const InboxNotification = () => {
  const { user } = useAuth();
  const [showInbox, setShowInbox] = useState(false);
  const [messages, setMessages] = useState([]);
  const [unreadCount, setUnreadCount] = useState(0);
  const [loading, setLoading] = useState(false);

  const fetchInbox = async () => {
    if (!user) return;

    setLoading(true);
    try {
      const response = await fetch(`${process.env.REACT_APP_API_URL}/contact/inbox`, {
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        }
      });

      const data = await response.json();
      if (data.success) {
        setMessages(data.contacts);
        const unread = data.contacts.filter(msg => msg.status === 'replied' && !msg.read).length;
        setUnreadCount(unread);
      }
    } catch (error) {
      console.error('Error fetching inbox:', error);
      // Fallback to localStorage
      const localMessages = JSON.parse(localStorage.getItem('contactMessages') || '[]');
      const userMessages = localMessages.filter(msg => msg.email === user.email);
      setMessages(userMessages);
      const unread = userMessages.filter(msg => msg.status === 'replied' && !msg.read).length;
      setUnreadCount(unread);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    if (user) {
      fetchInbox();
      // Poll for new messages every 30 seconds
      const interval = setInterval(fetchInbox, 30000);
      return () => clearInterval(interval);
    }
  }, [user]);

  if (!user || user.role === 'admin') return null;

  return (
    <div style={{ position: 'relative' }}>
      <button
        onClick={() => setShowInbox(!showInbox)}
        style={{
          position: 'relative',
          background: 'none',
          border: 'none',
          cursor: 'pointer',
          color: 'var(--text-primary)',
          padding: '0.5rem'
        }}
      >
        <Bell size={20} />
        {unreadCount > 0 && (
          <span
            style={{
              position: 'absolute',
              top: '0',
              right: '0',
              background: '#ef4444',
              color: 'white',
              borderRadius: '50%',
              width: '18px',
              height: '18px',
              fontSize: '0.7rem',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center'
            }}
          >
            {unreadCount}
          </span>
        )}
      </button>

      {showInbox && (
        <div
          style={{
            position: 'absolute',
            top: '100%',
            right: '0',
            width: '350px',
            maxHeight: '400px',
            background: 'var(--bg-secondary)',
            border: '1px solid var(--border-color)',
            borderRadius: '12px',
            boxShadow: 'var(--shadow-lg)',
            zIndex: 1000,
            overflow: 'hidden'
          }}
        >
          <div
            style={{
              padding: '1rem',
              borderBottom: '1px solid var(--border-color)',
              display: 'flex',
              justifyContent: 'space-between',
              alignItems: 'center'
            }}
          >
            <h3 style={{ margin: 0, fontSize: '1rem' }}>Inbox</h3>
            <button
              onClick={() => setShowInbox(false)}
              style={{
                background: 'none',
                border: 'none',
                cursor: 'pointer',
                color: 'var(--text-secondary)'
              }}
            >
              <X size={16} />
            </button>
          </div>

          <div style={{ maxHeight: '300px', overflowY: 'auto' }}>
            {loading ? (
              <div style={{ padding: '2rem', textAlign: 'center' }}>
                <div className="spinner" style={{ width: '20px', height: '20px' }}></div>
              </div>
            ) : messages.length === 0 ? (
              <div style={{ padding: '2rem', textAlign: 'center', color: 'var(--text-secondary)' }}>
                No messages yet
              </div>
            ) : (
              messages.map((message) => (
                <div
                  key={message._id}
                  style={{
                    padding: '1rem',
                    borderBottom: '1px solid var(--border-light)',
                    background: message.status === 'replied' ? 'var(--success-light)' : 'transparent'
                  }}
                >
                  <div style={{ display: 'flex', alignItems: 'center', gap: '0.5rem', marginBottom: '0.5rem' }}>
                    <MessageCircle size={14} />
                    <span style={{ fontWeight: '600', fontSize: '0.9rem' }}>
                      {message.subject === 'others' ? message.customSubject : message.subject.replace('-', ' ')}
                    </span>
                    {message.status === 'replied' && (
                      <span style={{ color: 'var(--success)', fontSize: '0.7rem' }}>Replied</span>
                    )}
                  </div>

                  <p style={{ 
                    margin: '0 0 0.5rem 0', 
                    fontSize: '0.8rem', 
                    color: 'var(--text-secondary)',
                    display: '-webkit-box',
                    WebkitLineClamp: 2,
                    WebkitBoxOrient: 'vertical',
                    overflow: 'hidden'
                  }}>
                    {message.message}
                  </p>

                  {message.adminReply && (
                    <div
                      style={{
                        background: 'var(--bg-primary)',
                        padding: '0.75rem',
                        borderRadius: '8px',
                        marginTop: '0.5rem',
                        borderLeft: '3px solid var(--success)'
                      }}
                    >
                      <div style={{ fontSize: '0.7rem', color: 'var(--success)', marginBottom: '0.25rem' }}>
                        Admin Reply:
                      </div>
                      <p style={{ margin: 0, fontSize: '0.8rem' }}>
                        {message.adminReply}
                      </p>
                    </div>
                  )}

                  <div style={{ fontSize: '0.7rem', color: 'var(--text-muted)', marginTop: '0.5rem' }}>
                    {new Date(message.createdAt).toLocaleDateString()}
                  </div>
                </div>
              ))
            )}
          </div>
        </div>
      )}
    </div>
  );
};

export default InboxNotification;
