import React, { useState, useEffect } from 'react';
import { Routes, Route, Link, useNavigate, useParams } from 'react-router-dom';

function App() {
  const [theme, setTheme] = useState(localStorage.getItem('theme') || 'system');
  const [user, setUser] = useState(JSON.parse(localStorage.getItem('user')) || null);
  const [cart, setCart] = useState(() => {
    try {
      return JSON.parse(localStorage.getItem('cart')) || [];
    } catch {
      return [];
    }
  });
  const [showLogin, setShowLogin] = useState(false);
  const [showSignup, setShowSignup] = useState(false);
  const [authError, setAuthError] = useState('');
  const [showFAQ, setShowFAQ] = useState({});
  const [showThemeMenu, setShowThemeMenu] = useState(false);
  const [orders, setOrders] = useState(() => JSON.parse(localStorage.getItem('orders')) || []);
  const [selectedProduct, setSelectedProduct] = useState(null);
  const [searchQuery, setSearchQuery] = useState('');
  const [showCheckout, setShowCheckout] = useState(false);
  const [showPayment, setShowPayment] = useState(false);
  const [orderData, setOrderData] = useState(null);
  const [userProfile, setUserProfile] = useState(() => JSON.parse(localStorage.getItem('userProfile')) || {});
  const navigate = useNavigate();

  useEffect(() => {
    document.documentElement.setAttribute('data-theme', theme);
    localStorage.setItem('theme', theme);
  }, [theme]);

  useEffect(() => {
    localStorage.setItem('cart', JSON.stringify(cart));
  }, [cart]);

  useEffect(() => {
    localStorage.setItem('orders', JSON.stringify(orders));
  }, [orders]);

  useEffect(() => {
    localStorage.setItem('userProfile', JSON.stringify(userProfile));
  }, [userProfile]);

  useEffect(() => {
    const handleKeyDown = (e) => {
      if (e.key === 'ArrowLeft') {
        const productsContainer = document.querySelector('.products-scroll');
        const categoriesContainer = document.querySelector('.categories-scroll');
        if (productsContainer) productsContainer.scrollBy({ left: -340, behavior: 'smooth' });
        if (categoriesContainer) categoriesContainer.scrollBy({ left: -220, behavior: 'smooth' });
      }
      if (e.key === 'ArrowRight') {
        const productsContainer = document.querySelector('.products-scroll');
        const categoriesContainer = document.querySelector('.categories-scroll');
        if (productsContainer) productsContainer.scrollBy({ left: 340, behavior: 'smooth' });
        if (categoriesContainer) categoriesContainer.scrollBy({ left: 220, behavior: 'smooth' });
      }
    };

    window.addEventListener('keydown', handleKeyDown);
    return () => window.removeEventListener('keydown', handleKeyDown);
  }, []);

  useEffect(() => {
    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          if (entry.isIntersecting) {
            entry.target.classList.remove('animate');
            entry.target.classList.add('visible');
          } else {
            entry.target.classList.add('animate');
            entry.target.classList.remove('visible');
          }
        });
      },
      { threshold: 0.1 }
    );

    const elements = document.querySelectorAll('.animate-on-scroll');
    elements.forEach((el) => {
      el.classList.add('animate');
      observer.observe(el);
    });

    return () => observer.disconnect();
  }, []);

  const login = (email, password) => {
    setAuthError('');
    if (email === 'admin@stitchandsavour.com' && password === 'admin123') {
      const adminUser = { name: 'Admin', email, role: 'admin' };
      setUser(adminUser);
      localStorage.setItem('user', JSON.stringify(adminUser));
      setShowLogin(false);
      navigate('/admin');
      return;
    }

    // Check if user exists in registered users
    const registeredUsers = JSON.parse(localStorage.getItem('registeredUsers')) || [];
    const existingUser = registeredUsers.find(u => u.email === email && u.password === password);

    if (existingUser) {
      const userData = { name: existingUser.name, email: existingUser.email, role: 'user' };
      setUser(userData);
      localStorage.setItem('user', JSON.stringify(userData));
      setShowLogin(false);
      navigate('/dashboard');
    } else {
      setAuthError('Invalid email or password');
    }
  };

  const logout = () => {
    setUser(null);
    localStorage.removeItem('user');
    navigate('/');
  };

  const addToCart = (product, customization = null) => {
    if (!user) {
      setShowLogin(true);
      return;
    }
    const cartItem = { 
      ...product, 
      quantity: 1, 
      customization,
      cartId: Date.now() + Math.random()
    };

    setCart([...cart, cartItem]);
    showNotification(`${product.name} added to cart!`, 'success');
  };

  const createOrder = (orderItems, totalAmount, paymentMethod = 'QR') => {
    const newOrder = {
      id: Date.now(),
      userId: user.email,
      items: orderItems,
      total: totalAmount,
      status: 'received',
      paymentMethod,
      createdAt: new Date().toISOString(),
      estimatedDelivery: calculateDeliveryDate(orderItems),
      trackingSteps: [
        { step: 'received', completed: true, date: new Date().toISOString() },
        { step: 'in-progress', completed: false, date: null },
        { step: 'out-for-delivery', completed: false, date: null },
        { step: 'delivered', completed: false, date: null }
      ]
    };

    setOrders([...orders, newOrder]);
    setCart([]);
    return newOrder;
  };

  const calculateDeliveryDate = (items) => {
    const hasCrochet = items.some(item => item.category === 'crochet');
    const days = hasCrochet ? 14 : 2;
    const deliveryDate = new Date();
    deliveryDate.setDate(deliveryDate.getDate() + days);
    return deliveryDate.toISOString();
  };

  const sendWhatsAppMessage = (message) => {
    const whatsappUrl = `https://wa.me/919970944685?text=${encodeURIComponent(message)}`;
    window.open(whatsappUrl, '_blank');
  };

  const generateOrderConfirmation = (order) => {
    const deliveryDate = new Date(order.estimatedDelivery).toLocaleDateString('en-IN');
    const itemsList = order.items.map(item => 
      `${item.name} (Qty: ${item.quantity}) - â‚¹${item.price * item.quantity}`
    ).join('\n');

    return `ðŸŽ‰ Order Confirmation - Stitch & Savour\n\nHi ${user.name}!\n\nYour order has been confirmed:\n\n${itemsList}\n\nTotal Amount: â‚¹${order.total}\nPayment: ${order.paymentMethod}\nExpected Delivery: ${deliveryDate}\n\nThank you for choosing Stitch & Savour! ðŸ’›`;
  };

  const showNotification = (message, type = 'info') => {
    const notification = document.createElement('div');
    notification.className = `notification notification-${type}`;
    notification.textContent = message;
    notification.style.cssText = `
      position: fixed;
      top: 100px;
      right: 20px;
      background: ${type === 'success' ? 'var(--success)' : 'var(--primary-color)'};
      color: white;
      padding: 1rem 1.5rem;
      border-radius: 12px;
      box-shadow: var(--shadow-md);
      z-index: 2000;
      animation: slideIn 0.3s ease;
    `;
    document.body.appendChild(notification);
    setTimeout(() => {
      notification.style.animation = 'fadeOut 0.3s ease';
      setTimeout(() => notification.remove(), 300);
    }, 3000);
  };

  const products = [
    {
      id: 1,
      name: 'Boho Chic Granny Square Top',
      category: 'crochet',
      price: 1299,
      image: '/images/crochet-boho-top.jpg',
