import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { Heart, ShoppingCart, Star, Truck, Shield, ArrowLeft } from 'lucide-react';
import { useCart } from '../context/CartContext';
import { useAuth } from '../context/AuthContext';
import { useWishlist } from '../context/WishlistContext';
import { useToast } from '../context/ToastContext';

const ProductDetailPage = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const { addToCart } = useCart();
  const { user } = useAuth();
  const { addToWishlist, removeFromWishlist, isInWishlist } = useWishlist();
  const { showToast } = useToast();
  const [product, setProduct] = useState(null);
  const [selectedImage, setSelectedImage] = useState(0);
  const [quantity, setQuantity] = useState(1);
  const [customizations, setCustomizations] = useState({});
  const [selectedWeight, setSelectedWeight] = useState('');
  const [currentPrice, setCurrentPrice] = useState(0);
  const [loading, setLoading] = useState(true);

  const sampleProducts = {
    6: {
      _id: 6,
      name: 'Homestyle Poha Chivda',
      category: 'food',
      price: 25,
      pricePerKg: 480,
      images: ['/images/food-poha-chivda.jpg'],
      shortDescription: 'A light, crispy, and savory Maharashtrian snack made with thin flattened rice.',
      description: 'Our homemade Poha Chivda is made with thin flattened rice, roasted peanuts, curry leaves, and a secret blend of mild spices.',
      rating: 4.6,
      reviewCount: 23,
      stockStatus: 'available',
      deliveryTime: 2,
      ingredients: ['Thin Flattened Rice (Poha)', 'Peanuts', 'Edible Vegetable Oil', 'Curry Leaves'],
      allergens: ['Peanuts'],
      weightOptions: ['100g Packet', '250g', '500g', '1kg']
    }
  };

  useEffect(() => {
    setTimeout(() => {
      let productData = sampleProducts[id];

      if (!productData) {
        const adminProducts = JSON.parse(localStorage.getItem('adminProducts') || '[]');
        productData = adminProducts.find(p => p.id.toString() === id || p._id.toString() === id);
      }

      if (productData) {
        setProduct(productData);
        setCurrentPrice(productData.price);
      }
      setLoading(false);
    }, 1000);
  }, [id]);

  const calculateWeightPrice = (weight) => {
    if (!weight || !product.pricePerKg) return product.price;

    const numbers = weight.replace(/[^0-9]/g, '');
    const numericWeight = parseFloat(numbers) || 0;
    const unit = weight.toLowerCase();

    if (unit.includes('kg')) {
      return Math.round(product.pricePerKg * numericWeight);
    } else if (unit.includes('g')) {
      return Math.round((product.pricePerKg * numericWeight) / 1000);
    }

    return product.price;
  };

  const handleWeightChange = (weight) => {
    setSelectedWeight(weight);
    const newPrice = calculateWeightPrice(weight);
    setCurrentPrice(newPrice);
  };

  const handleAddToCart = () => {
    if (!user) {
      navigate('/login');
      return;
    }

    if (product.category === 'food' && product.weightOptions && !selectedWeight) {
      showToast('Please select a weight option', 'error');
      return;
    }

    if (product) {
      const productToAdd = {
        ...product,
        price: currentPrice,
        selectedWeight: selectedWeight
      };

      addToCart(productToAdd, quantity, customizations);
      showToast('Added to cart successfully!', 'success');
    }
  };

  const handleBuyNow = () => {
    if (!user) {
      navigate('/login');
      return;
    }

    if (product.category === 'food' && product.weightOptions && !selectedWeight) {
      showToast('Please select a weight option', 'error');
      return;
    }

    if (product) {
      const productToAdd = {
        ...product,
        price: currentPrice,
        selectedWeight: selectedWeight
      };

      addToCart(productToAdd, quantity, customizations);
      navigate('/checkout');
    }
  };

  if (loading) {
    return (
      <div style={{ 
        minHeight: '60vh', 
        display: 'flex', 
        alignItems: 'center', 
        justifyContent: 'center' 
      }}>
        <div className="spinner"></div>
      </div>
    );
  }

  if (!product) {
    return (
      <div style={{ 
        minHeight: '60vh', 
        display: 'flex', 
        alignItems: 'center', 
        justifyContent: 'center',
        flexDirection: 'column',
        gap: '1rem'
      }}>
        <h2>Product not found</h2>
        <button onClick={() => navigate('/products')} className="btn btn-primary">
          Back to Products
        </button>
      </div>
    );
  }

  return (
    <div style={{ padding: '2rem 0', minHeight: '100vh' }}>
      <div className="container">
        <button
          onClick={() => navigate(-1)}
          style={{
            display: 'flex',
            alignItems: 'center',
            gap: '0.5rem',
            background: 'none',
            border: 'none',
            color: 'var(--primary-color)',
            fontSize: '0.9rem',
            cursor: 'pointer',
            marginBottom: '2rem'
          }}
        >
          <ArrowLeft size={16} />
          Back
        </button>

        <div style={{ 
          display: 'grid', 
          gridTemplateColumns: '1fr 1fr', 
          gap: '3rem',
          marginBottom: '3rem'
        }}>
          <div>
            <div style={{ 
              border: '1px solid var(--border-color)',
              borderRadius: '16px',
              overflow: 'hidden',
              marginBottom: '1rem'
            }}>
              <img
                src={product.images[selectedImage]}
                alt={product.name}
                style={{
                  width: '100%',
                  height: '400px',
                  objectFit: 'cover'
                }}
                onError={(e) => {
                  e.target.src = '/images/placeholder.jpg';
                }}
              />
            </div>
          </div>

          <div>
            <div style={{ marginBottom: '1rem' }}>
              <span style={{
                background: product.category === 'crochet' ? 'var(--primary-color)' : 'var(--success)',
                color: 'white',
                padding: '0.25rem 0.75rem',
                borderRadius: '12px',
                fontSize: '0.8rem',
                fontWeight: '600',
                textTransform: 'capitalize'
              }}>
                {product.category}
              </span>
            </div>

            <h1 style={{ 
              fontSize: '2rem', 
              fontWeight: '800',
              marginBottom: '1rem',
              lineHeight: '1.2'
            }}>
              {product.name}
            </h1>

            <div style={{ marginBottom: '1.5rem' }}>
              <div style={{ 
                fontSize: '2rem', 
                fontWeight: '800',
                color: 'var(--primary-color)',
                marginBottom: '0.5rem'
              }}>
                ₹{currentPrice}
                {product.pricePerKg && (
                  <span style={{ 
                    fontSize: '1rem', 
                    color: 'var(--text-secondary)',
                    fontWeight: '400',
                    marginLeft: '0.5rem'
                  }}>
                    (₹{product.pricePerKg}/kg)
                  </span>
                )}
              </div>

              <div style={{ 
                display: 'flex', 
                alignItems: 'center', 
                gap: '1rem',
                fontSize: '0.9rem',
                color: 'var(--text-secondary)'
              }}>
                <div style={{ display: 'flex', alignItems: 'center', gap: '0.25rem' }}>
                  <Truck size={16} />
                  Delivery in {product.deliveryTime} days
                </div>
                <div style={{ display: 'flex', alignItems: 'center', gap: '0.25rem' }}>
                  <Shield size={16} />
                  {product.stockStatus === 'made-to-order' ? 'Made to Order' : 'In Stock'}
                </div>
              </div>
            </div>

            <p style={{ 
              color: 'var(--text-secondary)', 
              lineHeight: '1.6',
              marginBottom: '2rem'
            }}>
              {product.description}
            </p>

            {product.weightOptions && (
              <div style={{ marginBottom: '2rem' }}>
                <h3 style={{ marginBottom: '1rem' }}>
                  Weight Options 
                  <span style={{ color: 'var(--error)', fontSize: '0.9rem' }}>*</span>
                  {!selectedWeight && (
                    <span style={{ 
                      fontSize: '0.8rem', 
                      color: 'var(--error)', 
                      marginLeft: '0.5rem',
                      fontWeight: '400'
                    }}>
                      (Required)
                    </span>
                  )}
                </h3>
                <div style={{ 
                  display: 'grid', 
                  gridTemplateColumns: 'repeat(auto-fit, minmax(100px, 1fr))',
                  gap: '0.5rem'
                }}>
                  {product.weightOptions.map((weight) => {
                    const weightPrice = calculateWeightPrice(weight);
                    return (
                      <button
                        key={weight}
                        onClick={() => handleWeightChange(weight)}
                        style={{
                          padding: '0.75rem',
                          border: selectedWeight === weight ? '2px solid var(--primary-color)' : '1px solid var(--border-color)',
                          borderRadius: '8px',
                          background: selectedWeight === weight ? 'var(--primary-color)' : 'var(--bg-primary)',
                          color: selectedWeight === weight ? 'white' : 'var(--text-primary)',
                          cursor: 'pointer',
                          fontSize: '0.9rem',
                          fontWeight: selectedWeight === weight ? '600' : '400',
                          transition: 'all 0.2s ease',
                          textAlign: 'center'
                        }}
                      >
                        <div>{weight}</div>
                        <div style={{ fontSize: '0.8rem', opacity: 0.8 }}>₹{weightPrice}</div>
                      </button>
                    );
                  })}
                </div>
              </div>
            )}

            <div style={{ 
              display: 'flex', 
              alignItems: 'center', 
              gap: '1rem',
              marginBottom: '2rem'
            }}>
              <label style={{ fontWeight: '600' }}>Quantity:</label>
              <div style={{ display: 'flex', alignItems: 'center', gap: '0.5rem' }}>
                <button
                  onClick={() => setQuantity(Math.max(1, quantity - 1))}
                  style={{
                    width: '36px',
                    height: '36px',
                    borderRadius: '8px',
                    cursor: 'pointer',
                    border: '1px solid var(--border-color)',
                    background: 'var(--bg-secondary)'
                  }}
                >
                  -
                </button>
                <span style={{ 
                  minWidth: '40px', 
                  textAlign: 'center',
                  fontWeight: '600'
                }}>
                  {quantity}
                </span>
                <button
                  onClick={() => setQuantity(quantity + 1)}
                  style={{
                    width: '36px',
                    height: '36px',
                    borderRadius: '8px',
                    cursor: 'pointer',
                    border: '1px solid var(--border-color)',
                    background: 'var(--bg-secondary)'
                  }}
                >
                  +
                </button>
              </div>
            </div>

            <div style={{ 
              display: 'flex', 
              gap: '1rem',
              marginBottom: '2rem'
            }}>
              <button
                onClick={handleAddToCart}
                style={{
                  flex: 1,
                  padding: '1rem',
                  background: 'var(--bg-secondary)',
                  border: '2px solid var(--primary-color)',
                  color: 'var(--primary-color)',
                  borderRadius: '12px',
                  fontWeight: '600',
                  cursor: 'pointer',
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'center',
                  gap: '0.5rem'
                }}
              >
                <ShoppingCart size={20} />
                Add to Cart
              </button>
              <button
                onClick={handleBuyNow}
                style={{
                  flex: 1,
                  padding: '1rem',
                  background: 'var(--primary-color)',
                  border: 'none',
                  color: 'white',
                  borderRadius: '12px',
                  fontWeight: '600',
                  cursor: 'pointer'
                }}
              >
                Buy Now
              </button>
              <button
                onClick={() => {
                  if (isInWishlist(product._id)) {
                    removeFromWishlist(product._id);
                    showToast('Removed from wishlist', 'success');
                  } else {
                    addToWishlist(product);
                    showToast('Added to wishlist!', 'success');
                  }
                }}
                style={{
                  padding: '1rem',
                  background: 'var(--bg-secondary)',
                  border: '1px solid var(--border-color)',
                  borderRadius: '12px',
                  cursor: 'pointer',
                  color: isInWishlist(product._id) ? '#ef4444' : 'var(--text-secondary)'
                }}
              >
                <Heart size={20} fill={isInWishlist(product._id) ? 'currentColor' : 'none'} />
              </button>
            </div>

            <div style={{ 
              background: 'var(--bg-secondary)',
              padding: '1.5rem',
              borderRadius: '12px',
              border: '1px solid var(--border-color)'
            }}>
              <h3 style={{ marginBottom: '1rem' }}>Product Details</h3>

              {product.ingredients && (
                <div style={{ marginBottom: '0.75rem' }}>
                  <strong>Ingredients:</strong> {product.ingredients.join(', ')}
                </div>
              )}

              {product.allergens && (
                <div style={{ marginBottom: '0.75rem' }}>
                  <strong>Allergen Info:</strong> {product.allergens.join(', ')}
                </div>
              )}

              <div>
                <strong>Delivery Area:</strong> Pan-India delivery available
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ProductDetailPage;
