function OrderCard({ order }) {
  const getStatusColor = (status) => {
    switch(status) {
      case 'received': return 'var(--info)';
      case 'in-progress': return 'var(--warning)';
      case 'out-for-delivery': return 'var(--primary-color)';
      case 'delivered': return 'var(--success)';
      default: return 'var(--text-secondary)';
    }
  };

  const getStatusIcon = (status) => {
    switch(status) {
      case 'received': return 'ðŸ“¦';
      case 'in-progress': return 'ðŸ”§';
      case 'out-for-delivery': return 'ðŸšš';
      case 'delivered': return 'âœ…';
      default: return 'ðŸ“¦';
    }
  };

  return (
    <div style={{ 
      border: '1px solid var(--border-color)', 
      borderRadius: '12px', 
      padding: '1.5rem',
      background: 'var(--bg-secondary)'
    }}>
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'start', marginBottom: '1rem' }}>
        <div>
          <h4 style={{ margin: 0, marginBottom: '0.5rem' }}>Order #{order.id.toString().slice(-6)}</h4>
          <p style={{ color: 'var(--text-secondary)', fontSize: '0.9rem', margin: 0 }}>
            {new Date(order.createdAt).toLocaleDateString('en-IN')}
          </p>
        </div>
        <div style={{ textAlign: 'right' }}>
          <div style={{ 
            display: 'inline-flex', 
            alignItems: 'center', 
            gap: '0.5rem',
            background: getStatusColor(order.status),
            color: 'white',
            padding: '0.25rem 0.75rem',
            borderRadius: '20px',
            fontSize: '0.8rem',
            fontWeight: '600'
          }}>
            {getStatusIcon(order.status)} {order.status.replace('-', ' ').toUpperCase()}
          </div>
        </div>
      </div>

      <div style={{ marginBottom: '1rem' }}>
        <p style={{ fontSize: '0.9rem', color: 'var(--text-secondary)', margin: 0 }}>
          {order.items.length} item(s) â€¢ â‚¹{order.total} â€¢ {order.paymentMethod}
        </p>
      </div>

      <div style={{ 
        display: 'flex', 
        justifyContent: 'space-between', 
        alignItems: 'center',
        paddingTop: '1rem',
        borderTop: '1px solid var(--border-light)'
      }}>
        <p style={{ fontSize: '0.9rem', color: 'var(--text-secondary)', margin: 0 }}>
          Expected: {new Date(order.estimatedDelivery).toLocaleDateString('en-IN')}
        </p>
        <button className="btn btn-outline btn-sm">
          Track Order
        </button>
      </div>
    </div>
  );
}

