function ProductDetail({ products, addToCart, selectedProduct }) {
  const { id } = useParams();
  const product = products.find(p => p.id === parseInt(id)) || selectedProduct;
  const [customization, setCustomization] = useState({
    thread: product?.threadTypes?.[0] || '',
    color: product?.colors?.[0] || '',
    size: product?.sizes?.[0] || ''
  });
  const [currentPrice, setCurrentPrice] = useState(product?.price || 0);

  if (!product) return <div>Product not found</div>;

  return (
    <div style={{ padding: '2rem', maxWidth: '1000px', margin: '0 auto' }}>
      <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '3rem' }}>
        <div>
          <img src={product.image} alt={product.name} style={{ width: '100%', borderRadius: '12px' }} />
        </div>
        <div>
          <h1>{product.name}</h1>
          <p style={{ fontSize: '2rem', color: 'var(--primary-color)', fontWeight: '700' }}>â‚¹{currentPrice}</p>
          <p>{product.description}</p>

          {product.customizable && (
            <div style={{ marginBottom: '2rem' }}>
              <h3>Customization Options</h3>
              <div style={{ display: 'grid', gap: '1rem' }}>
                <div>
                  <label>Thread Type:</label>
                  <select value={customization.thread} onChange={(e) => setCustomization({...customization, thread: e.target.value})}>
                    {product.threadTypes.map(type => <option key={type} value={type}>{type}</option>)}
                  </select>
                </div>
                <div>
                  <label>Color:</label>
                  <select value={customization.color} onChange={(e) => setCustomization({...customization, color: e.target.value})}>
                    {product.colors.map(color => <option key={color} value={color}>{color}</option>)}
                  </select>
                </div>
                <div>
                  <label>Size:</label>
                  <select value={customization.size} onChange={(e) => setCustomization({...customization, size: e.target.value})}>
                    {product.sizes.map(size => <option key={size} value={size}>{size}</option>)}
                  </select>
                </div>
              </div>
            </div>
          )}

          <button 
            onClick={() => addToCart(product, product.customizable ? customization : null)}
            className="btn btn-primary btn-lg"
          >
            Add to Cart
          </button>
        </div>
      </div>
    </div>
  );
}

function Checkout({ cart, setCart, user, createOrder, setShowPayment, setOrderData }) {
  const navigate = useNavigate();
  const total = cart.reduce((sum, item) => sum + (item.price * item.quantity), 0);
  const [paymentMethod, setPaymentMethod] = useState('QR');

  const handleCheckout = () => {
    const order = createOrder(cart, total, paymentMethod);
    setOrderData(order);
    if (paymentMethod === 'QR') {
      navigate('/payment');
    } else {
      // COD - direct WhatsApp confirmation
      const message = `ðŸŽ‰ Order Confirmation - Stitch & Savour\n\nHi ${user.name}!\n\nYour COD order has been confirmed:\n\nTotal: â‚¹${total}\nPayment: Cash on Delivery\n\nThank you!`;
      window.open(`https://wa.me/919970944685?text=${encodeURIComponent(message)}`, '_blank');
      navigate('/dashboard');
    }
  };

  return (
    <div style={{ padding: '2rem', maxWidth: '800px', margin: '0 auto' }}>
      <h1>Checkout</h1>
      <div className="card">
        <div className="card-body">
          <h3>Order Summary</h3>
          {cart.map(item => (
            <div key={item.cartId} style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '1rem' }}>
              <span>{item.name} x {item.quantity}</span>
              <span>â‚¹{item.price * item.quantity}</span>
            </div>
          ))}
          <hr />
          <div style={{ display: 'flex', justifyContent: 'space-between', fontSize: '1.2rem', fontWeight: '700' }}>
            <span>Total:</span>
            <span>â‚¹{total}</span>
          </div>

          <div style={{ marginTop: '2rem' }}>
            <h4>Payment Method</h4>
            <div>
              <label>
                <input type="radio" value="QR" checked={paymentMethod === 'QR'} onChange={(e) => setPaymentMethod(e.target.value)} />
                QR Code Payment
              </label>
            </div>
            <div>
              <label>
                <input type="radio" value="COD" checked={paymentMethod === 'COD'} onChange={(e) => setPaymentMethod(e.target.value)} />
                Cash on Delivery
              </label>
            </div>
          </div>

          <button onClick={handleCheckout} className="btn btn-primary btn-lg" style={{ width: '100%', marginTop: '2rem' }}>
            Place Order
          </button>
        </div>
      </div>
    </div>
  );
}

function Payment({ orderData, sendWhatsAppMessage, generateOrderConfirmation }) {
  const navigate = useNavigate();
  const [paymentComplete, setPaymentComplete] = useState(false);

  const handlePaymentSuccess = () => {
    setPaymentComplete(true);
    const message = generateOrderConfirmation(orderData);
    sendWhatsAppMessage(message);
    setTimeout(() => navigate('/dashboard'), 2000);
  };

  if (paymentComplete) {
    return (
      <div style={{ padding: '2rem', textAlign: 'center' }}>
        <h1>âœ… Payment Successful!</h1>
        <p>Your order has been confirmed. Redirecting to dashboard...</p>
      </div>
    );
  }

  return (
    <div style={{ padding: '2rem', maxWidth: '600px', margin: '0 auto', textAlign: 'center' }}>
      <h1>Complete Payment</h1>
      <div className="card">
        <div className="card-body">
          <h3>Scan QR Code to Pay</h3>
          <div style={{ fontSize: '10rem', margin: '2rem 0' }}>ðŸ“±</div>
          <p>Total Amount: â‚¹{orderData?.total}</p>
          <button onClick={handlePaymentSuccess} className="btn btn-success btn-lg">
            Payment Done
          </button>
        </div>
      </div>
    </div>
  );
}

