import jsPDF from 'jspdf';

export const generateInvoicePDF = (order) => {
  const pdf = new jsPDF();

  // Header
  pdf.setFontSize(20);
  pdf.setTextColor(217, 70, 239);
  pdf.text('Stitch & Savour', 20, 30);

  pdf.setFontSize(12);
  pdf.setTextColor(0, 0, 0);
  pdf.text('Invoice', 20, 45);

  // Business Details
  pdf.setFontSize(10);
  pdf.text('Sangita Thakur', 20, 60);
  pdf.text('Email: sangita.shreyas@gmail.com', 20, 70);
  pdf.text('Phone: +91 9970944685', 20, 80);

  // Invoice Details
  pdf.text(`Invoice #: ${order.orderNumber}`, 120, 60);
  pdf.text(`Date: ${new Date(order.createdAt).toLocaleDateString()}`, 120, 70);
  pdf.text(`Status: ${order.status.toUpperCase()}`, 120, 80);

  // Line
  pdf.line(20, 90, 190, 90);

  // Items Header
  pdf.setFontSize(12);
  pdf.text('Items:', 20, 105);

  // Items
  let yPos = 120;
  pdf.setFontSize(10);

  order.items?.forEach((item, index) => {
    pdf.text(`${index + 1}. ${item.name}`, 20, yPos);
    pdf.text(`Qty: ${item.quantity}`, 120, yPos);
    pdf.text(`₹${item.price * item.quantity}`, 160, yPos);
    yPos += 15;
  });

  // Total
  pdf.line(20, yPos, 190, yPos);
  yPos += 15;
  pdf.setFontSize(12);
  pdf.text(`Total: ₹${order.total}`, 20, yPos);
  pdf.text(`Payment: ${order.paymentMethod === 'qr' ? 'Paid' : 'COD'}`, 120, yPos);

  // Footer
  yPos += 30;
  pdf.setFontSize(8);
  pdf.text('Thank you for shopping with Stitch & Savour!', 20, yPos);

  // Download
  pdf.save(`Invoice-${order.orderNumber}.pdf`);
};
