import React, { useState } from 'react';
import { MessageCircle, Send, X, Bot } from 'lucide-react';

// Add your Gemini API key here
// 1. Go to https://makersuite.google.com/app/apikey
// 2. Create a new API key
// 3. Replace 'YOUR_GEMINI_API_KEY_HERE' with your actual key
const GEMINI_API_KEY = 'YOUR_GEMINI_API_KEY_HERE';

const Chatbot = () => {
  const [isOpen, setIsOpen] = useState(false);
  const [messages, setMessages] = useState([
    {
      id: 1,
      text: "Hi! I'm your Stitch & Savour assistant! 🌟 I can help you with information about our homemade food and handcrafted crochet products. What would you like to know?",
      isBot: true,
      timestamp: new Date()
    }
  ]);
  const [inputText, setInputText] = useState('');
  const [isTyping, setIsTyping] = useState(false);

  const websiteInfo = {
    products: {
      food: [
        "Homestyle Poha Chivda - ₹25",
        "Sweet & Flaky Shakarpara - ₹30", 
        "Crispy & Savory Namak Pare - ₹25",
        "Spicy Mixture Namkeen - ₹35",
        "Classic Salty Mathri - ₹40",
        "Baked Jeera Biscuits - ₹50",
        "Homemade Gujiya - ₹600"
      ],
      crochet: [
        "Boho Chic Granny Square Top - ₹1299",
        "Classic Striped V-Neck Vest - ₹999", 
        "Minimalist Pink Tank Top - ₹799",
        "Serene Blue & Pink Pooja Mat - ₹449",
        "Festive Multicolor Pooja Mat - ₹599"
      ]
    },
    delivery: {
      food: "2 days",
      crochet: "2 weeks (custom-made)"
    },
    contact: {
      whatsapp: "+91 9970944685",
      email: "sangita.shreyas@gmail.com",
      phone: "+91 8668806190"
    },
    payment: ["QR Code Payment", "Cash on Delivery (COD)"],
    customization: "Yes! Crochet items can be customized with different colors, sizes, and thread types",
    owner: "Sangita Thakur - passionate about homemade food and handcrafted crochet"
  };

  const generateResponse = async (userMessage) => {
    const message = userMessage.toLowerCase();
    const websiteKeywords = ['food', 'crochet', 'delivery', 'payment', 'price', 'contact', 'custom', 'sangita', 'order', 'product'];
    const isWebsiteRelated = websiteKeywords.some(keyword => message.includes(keyword));

    if (isWebsiteRelated && GEMINI_API_KEY !== 'YOUR_GEMINI_API_KEY_HERE') {
      try {
        const context = `You are Stitch & Savour assistant. Products: Food (${websiteInfo.products.food.join(', ')}) - ${websiteInfo.delivery.food} delivery, Crochet (${websiteInfo.products.crochet.join(', ')}) - ${websiteInfo.delivery.crochet} delivery. Contact: ${websiteInfo.contact.whatsapp}. Answer briefly with emojis.`;

        const response = await fetch(`https://generativelanguage.googleapis.com/v1beta/models/gemini-pro:generateContent?key=${GEMINI_API_KEY}`, {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({
            contents: [{ parts: [{ text: `${context}\n\nUser: ${userMessage}` }] }]
          })
        });

        const data = await response.json();
        if (data.candidates?.[0]) {
          return data.candidates[0].content.parts[0].text;
        }
      } catch (error) {
        console.error('Gemini API error:', error);
      }
    }

    // Fallback responses
    if (message.includes('food')) return `🍽️ Our homemade food: ${websiteInfo.products.food.slice(0,3).join(', ')} and more! Delivered in ${websiteInfo.delivery.food}. Ready to order?`;
    if (message.includes('crochet')) return `🧶 Handcrafted crochet: ${websiteInfo.products.crochet.slice(0,2).join(', ')} and more! Custom-made in ${websiteInfo.delivery.crochet}!`;
    if (message.includes('contact')) return `📞 Contact: WhatsApp ${websiteInfo.contact.whatsapp} or visit our contact page for more help!`;

    if (isWebsiteRelated) {
      return "For detailed information about this, please contact us directly: [Contact Form](/contact) 📞";
    }

    // Off-topic responses
    const responses = [
      "That's interesting! Have you seen our beautiful crochet tops? Perfect for any occasion! 🌟 [Shop Crochet](/products?category=crochet)",
      "While that's outside my expertise, our homemade snacks are amazing! Fresh and made with love! 🍪 [Try Our Food](/products?category=food)",
      "I focus on Stitch & Savour! Our handmade items make perfect gifts! 🎁 [Browse All](/products)"
    ];

    return responses[Math.floor(Math.random() * responses.length)];
  };

  const handleSend = () => {
    if (!inputText.trim()) return;

    const userMessage = {
      id: messages.length + 1,
      text: inputText,
      isBot: false,
      timestamp: new Date()
    };

    setMessages(prev => [...prev, userMessage]);
    setInputText('');
    setIsTyping(true);

    setTimeout(async () => {
      const responseText = await generateResponse(inputText);
      const botResponse = {
        id: messages.length + 2,
        text: responseText,
        isBot: true,
        timestamp: new Date()
      };
      setMessages(prev => [...prev, botResponse]);
      setIsTyping(false);
    }, 1000);
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      handleSend();
    }
  };

  return (
    <>
      {/* Chat Button */}
      <button
        onClick={() => setIsOpen(true)}
        style={{
          position: 'fixed',
          bottom: '100px',
          right: '20px',
          width: '60px',
          height: '60px',
          backgroundColor: 'var(--primary-color)',
          borderRadius: '50%',
          border: 'none',
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
          boxShadow: '0 4px 12px rgba(217, 70, 239, 0.4)',
          cursor: 'pointer',
          zIndex: 1000,
          transition: 'all 0.3s ease'
        }}
        onMouseEnter={(e) => e.target.style.transform = 'scale(1.1)'}
        onMouseLeave={(e) => e.target.style.transform = 'scale(1)'}
      >
        <MessageCircle size={24} color="white" />
      </button>

      {/* Chat Window */}
      {isOpen && (
        <div style={{
          position: 'fixed',
          bottom: '20px',
          right: '20px',
          width: '350px',
          height: '500px',
          backgroundColor: 'var(--bg-primary)',
          border: '1px solid var(--border-color)',
          borderRadius: '16px',
          boxShadow: '0 10px 30px rgba(0,0,0,0.2)',
          zIndex: 1001,
          display: 'flex',
          flexDirection: 'column',
          overflow: 'hidden'
        }}>
          {/* Header */}
          <div style={{
            background: 'linear-gradient(135deg, var(--primary-color), var(--secondary-color))',
            color: 'white',
            padding: '1rem',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'space-between'
          }}>
            <div style={{ display: 'flex', alignItems: 'center', gap: '0.5rem' }}>
              <Bot size={20} />
              <span style={{ fontWeight: '600' }}>Stitch & Savour Assistant</span>
            </div>
            <button
              onClick={() => setIsOpen(false)}
              style={{
                background: 'none',
                border: 'none',
                color: 'white',
                cursor: 'pointer'
              }}
            >
              <X size={20} />
            </button>
          </div>

          {/* Messages */}
          <div style={{
            flex: 1,
            padding: '1rem',
            overflowY: 'auto',
            display: 'flex',
            flexDirection: 'column',
            gap: '1rem'
          }}>
            {messages.map((message) => (
              <div
                key={message.id}
                style={{
                  display: 'flex',
                  justifyContent: message.isBot ? 'flex-start' : 'flex-end'
                }}
              >
                <div style={{
                  maxWidth: '80%',
                  padding: '0.75rem',
                  borderRadius: '12px',
                  backgroundColor: message.isBot ? 'var(--bg-secondary)' : 'var(--primary-color)',
                  color: message.isBot ? 'var(--text-primary)' : 'white',
                  fontSize: '0.9rem',
                  lineHeight: '1.4'
                }}>
                  {message.text}
                </div>
              </div>
            ))}

            {isTyping && (
              <div style={{ display: 'flex', justifyContent: 'flex-start' }}>
                <div style={{
                  padding: '0.75rem',
                  borderRadius: '12px',
                  backgroundColor: 'var(--bg-secondary)',
                  color: 'var(--text-primary)'
                }}>
                  <div style={{ display: 'flex', gap: '0.25rem' }}>
                    <div style={{ width: '6px', height: '6px', borderRadius: '50%', backgroundColor: 'var(--primary-color)', animation: 'bounce 1.4s infinite' }}></div>
                    <div style={{ width: '6px', height: '6px', borderRadius: '50%', backgroundColor: 'var(--primary-color)', animation: 'bounce 1.4s infinite 0.2s' }}></div>
                    <div style={{ width: '6px', height: '6px', borderRadius: '50%', backgroundColor: 'var(--primary-color)', animation: 'bounce 1.4s infinite 0.4s' }}></div>
                  </div>
                </div>
              </div>
            )}
          </div>

          {/* Input */}
          <div style={{
            padding: '1rem',
            borderTop: '1px solid var(--border-color)',
            display: 'flex',
            gap: '0.5rem'
          }}>
            <input
              type="text"
              value={inputText}
              onChange={(e) => setInputText(e.target.value)}
              onKeyPress={handleKeyPress}
              placeholder="Ask me about our products..."
              style={{
                flex: 1,
                padding: '0.75rem',
                border: '1px solid var(--border-color)',
                borderRadius: '8px',
                fontSize: '0.9rem'
              }}
            />
            <button
              onClick={handleSend}
              style={{
                padding: '0.75rem',
                backgroundColor: 'var(--primary-color)',
                color: 'white',
                border: 'none',
                borderRadius: '8px',
                cursor: 'pointer'
              }}
            >
              <Send size={16} />
            </button>
          </div>
        </div>
      )}
    </>
  );
};

export default Chatbot;
