import React from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';

// Simple test component first
function App() {
  return (
    <Router>
      <div style={{ padding: '2rem', textAlign: 'center' }}>
        <h1 style={{ color: '#ec4899', fontSize: '3rem', marginBottom: '1rem' }}>
          Stitch & Savour
        </h1>
        <p style={{ fontSize: '1.2rem', color: '#64748b' }}>
          Welcome to our homemade food and handcrafted crochet store!
        </p>
        <div style={{ marginTop: '2rem' }}>
          <a 
            href="/products" 
            style={{
              background: '#ec4899',
              color: 'white',
              padding: '1rem 2rem',
              borderRadius: '8px',
              textDecoration: 'none',
              fontSize: '1.1rem',
              fontWeight: '600'
            }}
          >
            Shop Now
          </a>
        </div>
      </div>
    </Router>
  );
}

export default App;
