      reviewCount: 7,
      stockStatus: 'made-to-order',
      deliveryTime: 3,
      ingredients: ['Maida', 'Khoya', 'Coconut', 'Nuts'],
      allergens: ['Gluten', 'Dairy', 'Nuts'],
      weightOptions: ['6 pieces', '12 pieces', '24 pieces']
    }
  };

  const sampleReviews = [
    {
      id: 1,
      userName: 'Priya K.',
      rating: 5,
      comment: 'I absolutely love my top! The colors are so bright and the fit is perfect.',
      date: '2024-01-15',
      verified: true
    },
    {
      id: 2,
      userName: 'Sunita P.',
      rating: 5,
      comment: 'The chivda is so fresh and perfectly spiced. Not too oily at all!',
      date: '2024-01-10',
      verified: true
    }
  ];

  useEffect(() => {
    // Simulate API call
    setTimeout(() => {
      const productData = sampleProducts[id];
      if (productData) {
        setProduct(productData);
        setCurrentPrice(productData.price);
        setReviews(sampleReviews.filter(r => 
          (productData.category === 'crochet' && [1, 3].includes(r.id)) ||
          (productData.category === 'food' && r.id === 2)
        ));
      }
      setLoading(false);
    }, 1000);
  }, [id]);

  const handleCustomizationChange = (type, value) => {
    const newCustomizations = { ...customizations, [type]: value };
    setCustomizations(newCustomizations);

    // Calculate price with modifiers
    let newPrice = product.price;
    product.customization?.forEach(option => {
      const selectedValue = newCustomizations[option.type];
      if (selectedValue && option.priceModifier && typeof option.priceModifier === 'object') {
        newPrice += option.priceModifier[selectedValue] || 0;
      }
    });
    setCurrentPrice(newPrice);
  };

  const handleAddToCart = () => {
    if (!user) {
      navigate('/login');
      return;
    }
    if (product) {
      setButtonAnimation(true);
      setTimeout(() => setButtonAnimation(false), 200);

      // Create floating cart animation
      const newCarts = Array.from({ length: 2 }, (_, i) => ({
        id: Date.now() + i,
        left: Math.random() * 30 - 15,
        delay: i * 150
      }));
      setFloatingCarts(newCarts);
      setTimeout(() => setFloatingCarts([]), 1000);

      addToCart(product, quantity, customizations);
      showToast('Added to cart successfully!', 'success');
    }
  };

  const handleBuyNow = () => {
    if (!user) {
      navigate('/login');
      return;
    }
    if (product) {
      addToCart(product, quantity, customizations);
      navigate('/checkout');
    }
  };

  if (loading) {
    return (
      <div style={{ 
        minHeight: '60vh', 
        display: 'flex', 
        alignItems: 'center', 
        justifyContent: 'center' 
      }}>
        <div className="spinner"></div>
      </div>
    );
  }

  if (!product) {
    return (
      <div style={{ 
        minHeight: '60vh', 
        display: 'flex', 
        alignItems: 'center', 
        justifyContent: 'center',
        flexDirection: 'column',
        gap: '1rem'
      }}>
        <h2>Product not found</h2>
        <button onClick={() => navigate('/products')} className="btn btn-primary">
          Back to Products
        </button>
      </div>
    );
  }

  return (
    <div style={{ padding: '2rem 0', minHeight: '100vh' }}>
      <div className="container">
        {/* Back Button */}
        <button
          onClick={() => navigate(-1)}
          style={{
            display: 'flex',
            alignItems: 'center',
            gap: '0.5rem',
            background: 'none',
            border: 'none',
            color: 'var(--primary-color)',
            fontSize: '0.9rem',
            cursor: 'pointer',
            marginBottom: '2rem'
          }}
        >
          <ArrowLeft size={16} />
          Back
        </button>

        <div className="animate-fade-in" style={{ 
          display: 'grid', 
          gridTemplateColumns: '1fr 1fr', 
          gap: '3rem',
          marginBottom: '3rem'
        }}>
          {/* Product Images */}
          <div className="animate-slide-in">
            <div style={{ 
              border: '1px solid var(--border-color)',
              borderRadius: '16px',
              overflow: 'hidden',
              marginBottom: '1rem'
            }}>
              <img
                src={product.images[selectedImage]}
                alt={product.name}
                style={{
                  width: '100%',
                  height: '400px',
                  objectFit: 'cover'
                }}
                onError={(e) => {
                  e.target.src = '/images/placeholder.jpg';
                }}
              />
            </div>

            {product.images.length > 1 && (
              <div style={{ 
                display: 'flex', 
                gap: '0.5rem',
                overflowX: 'auto'
              }}>
                {product.images.map((image, index) => (
                  <img
                    key={index}
                    src={image}
                    alt={`${product.name} ${index + 1}`}
                    style={{
                      width: '80px',
                      height: '80px',
                      objectFit: 'cover',
                      borderRadius: '8px',
                      cursor: 'pointer',
                      border: selectedImage === index ? '2px solid var(--primary-color)' : '1px solid var(--border-color)'
                    }}
                    onClick={() => setSelectedImage(index)}
                  />
                ))}
              </div>
            )}
          </div>

          {/* Product Info */}
          <div className="animate-slide-up" style={{ animationDelay: '0.2s' }}>
            <div style={{ marginBottom: '1rem' }}>
              <span style={{
                background: product.category === 'crochet' ? 'var(--primary-color)' : 'var(--success)',
                color: 'white',
                padding: '0.25rem 0.75rem',
                borderRadius: '12px',
                fontSize: '0.8rem',
                fontWeight: '600',
                textTransform: 'capitalize'
              }}>
                {product.category}
              </span>
            </div>

            <h1 style={{ 
              fontSize: '2rem', 
              fontWeight: '800',
              marginBottom: '1rem',
              lineHeight: '1.2'
            }}>
              {product.name}
            </h1>

            <div style={{ 
              display: 'flex', 
              alignItems: 'center', 
              gap: '1rem',
              marginBottom: '1rem'
            }}>
              <div style={{ display: 'flex', alignItems: 'center', gap: '0.25rem' }}>
                {[...Array(5)].map((_, i) => (
                  <Star
                    key={i}
                    size={16}
                    fill={i < Math.floor(product.rating) ? '#fbbf24' : 'none'}
                    color={i < Math.floor(product.rating) ? '#fbbf24' : '#d1d5db'}
                  />
                ))}
              </div>
              <span style={{ color: 'var(--text-secondary)' }}>
                {product.rating} ({product.reviewCount} reviews)
              </span>
            </div>

            <div style={{ marginBottom: '1.5rem' }}>
              <div style={{ 
                fontSize: '2rem', 
                fontWeight: '800',
                color: 'var(--primary-color)',
                marginBottom: '0.5rem'
              }}>
                ₹{currentPrice}
                {product.pricePerKg && (
                  <span style={{ 
                    fontSize: '1rem', 
                    color: 'var(--text-secondary)',
                    fontWeight: '400',
                    marginLeft: '0.5rem'
                  }}>
                    (₹{product.pricePerKg}/kg)
                  </span>
                )}
              </div>

              <div style={{ 
                display: 'flex', 
                alignItems: 'center', 
                gap: '1rem',
                fontSize: '0.9rem',
                color: 'var(--text-secondary)'
              }}>
                <div style={{ display: 'flex', alignItems: 'center', gap: '0.25rem' }}>
                  <Truck size={16} />
                  Delivery in {product.deliveryTime} days
                </div>
                <div style={{ display: 'flex', alignItems: 'center', gap: '0.25rem' }}>
                  <Shield size={16} />
                  {product.stockStatus === 'made-to-order' ? 'Made to Order' : 'In Stock'}
                </div>
              </div>
            </div>

            <p style={{ 
              color: 'var(--text-secondary)', 
              lineHeight: '1.6',
              marginBottom: '2rem'
            }}>
              {product.description}
            </p>

            {/* Customization Options */}
            {product.customization && (
              <div style={{ marginBottom: '2rem' }}>
                <h3 style={{ marginBottom: '1rem' }}>Customization Options</h3>
                {product.customization.map((option) => (
                  <div key={option.type} style={{ marginBottom: '1rem' }}>
                    <label style={{ 
                      display: 'block', 
                      fontWeight: '600',
                      marginBottom: '0.5rem'
                    }}>
                      {option.label}
                    </label>
                    <select
                      value={customizations[option.type] || ''}
                      onChange={(e) => handleCustomizationChange(option.type, e.target.value)}
                      style={{
                        width: '100%',
                        padding: '0.75rem',
                        border: '1px solid var(--border-color)',
                        borderRadius: '8px',
                        fontSize: '0.9rem'
                      }}
                    >
                      <option value="">Select {option.label}</option>
                      {option.options.map((opt) => (
                        <option key={opt} value={opt}>{opt}</option>
                      ))}
                    </select>
                  </div>
                ))}
              </div>
            )}

            {/* Weight Options for Food */}
            {product.weightOptions && (
              <div style={{ marginBottom: '2rem' }}>
                <h3 style={{ marginBottom: '1rem' }}>Weight Options</h3>
                <div style={{ 
                  display: 'grid', 
                  gridTemplateColumns: 'repeat(auto-fit, minmax(100px, 1fr))',
                  gap: '0.5rem'
                }}>
                  {product.weightOptions.map((weight) => (
                    <button
                      key={weight}
                      onClick={() => setSelectedWeight(weight)}
                      style={{
                        padding: '0.75rem',
                        border: selectedWeight === weight ? '2px solid var(--primary-color)' : '1px solid var(--border-color)',
                        borderRadius: '8px',
                        background: selectedWeight === weight ? 'var(--primary-light)' : 'var(--bg-secondary)',
                        cursor: 'pointer',
                        fontSize: '0.9rem',
                        fontWeight: selectedWeight === weight ? '600' : '400'
                      }}
                    >
                      {weight}
                    </button>
                  ))}
                </div>
              </div>
            )}

            {/* Quantity */}
            <div style={{ 
              display: 'flex', 
              alignItems: 'center', 
              gap: '1rem',
              marginBottom: '2rem'
            }}>
              <label style={{ fontWeight: '600' }}>Quantity:</label>
              <div style={{ display: 'flex', alignItems: 'center', gap: '0.5rem' }}>
                <button
                  onClick={() => setQuantity(Math.max(1, quantity - 1))}
                  className="quantity-btn"
                  style={{
                    width: '36px',
                    height: '36px',
                    borderRadius: '8px',
                    cursor: 'pointer'
                  }}
                >
                  -
                </button>
                <span style={{ 
                  minWidth: '40px', 
                  textAlign: 'center',
                  fontWeight: '600'
                }}>
                  {quantity}
                </span>
                <button
                  onClick={() => setQuantity(quantity + 1)}
                  className="quantity-btn"
                  style={{
                    width: '36px',
                    height: '36px',
                    borderRadius: '8px',
                    cursor: 'pointer'
                  }}
                >
                  +
                </button>
              </div>
            </div>

            {/* Action Buttons */}
            <div className="animate-scale-in" style={{ 
              display: 'flex', 
              gap: '1rem',
              marginBottom: '2rem',
              animationDelay: '0.3s'
            }}>
              <button
                onClick={handleAddToCart}
                className={`hover-scale ${buttonAnimation ? 'animate-button-pop' : ''}`}
                style={{
                  flex: 1,
                  padding: '1rem',
                  background: 'var(--bg-secondary)',
                  border: '2px solid var(--primary-color)',
                  color: 'var(--primary-color)',
                  borderRadius: '12px',
                  fontWeight: '600',
                  cursor: 'pointer',
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'center',
                  gap: '0.5rem',
                  position: 'relative'
                }}
              >
                <ShoppingCart size={20} />
                Add to Cart
                {floatingCarts.map(cart => (
                  <div
                    key={cart.id}
                    className="animate-floating-cart"
                    style={{
                      position: 'absolute',
                      left: `${cart.left}px`,
                      animationDelay: `${cart.delay}ms`,
                      fontSize: '1.5rem'
                    }}
                  >
                    🛒
                  </div>
                ))}
              </button>
              <button
                onClick={handleBuyNow}
                className="hover-scale"
                style={{
                  flex: 1,
                  padding: '1rem',
                  background: 'var(--primary-color)',
                  border: 'none',
                  color: 'white',
                  borderRadius: '12px',
                  fontWeight: '600',
                  cursor: 'pointer'
                }}
              >
                Buy Now
              </button>
              <button
                onClick={() => {
                  setHeartAnimation(true);
                  setTimeout(() => setHeartAnimation(false), 300);

                  // Create floating hearts
                  const newHearts = Array.from({ length: 3 }, (_, i) => ({
                    id: Date.now() + i,
                    left: Math.random() * 20 - 10,
                    delay: i * 100
                  }));
                  setFloatingHearts(newHearts);
                  setTimeout(() => setFloatingHearts([]), 1000);

                  if (isInWishlist(product._id)) {
                    removeFromWishlist(product._id);
                    showToast('Removed from wishlist', 'success');
                  } else {
                    addToWishlist(product);
                    showToast('Added to wishlist!', 'success');
                  }
                }}
                className={heartAnimation ? 'animate-heart-pop' : ''}
                style={{
                  padding: '1rem',
                  background: 'var(--bg-secondary)',
                  border: '1px solid var(--border-color)',
                  borderRadius: '12px',
                  cursor: 'pointer',
                  color: isInWishlist(product._id) ? '#ef4444' : 'var(--text-secondary)',
                  position: 'relative'
                }}
              >
                <Heart size={20} fill={isInWishlist(product._id) ? 'currentColor' : 'none'} />
                {floatingHearts.map(heart => (
                  <div
                    key={heart.id}
                    className="floating-heart animate-floating-heart"
                    style={{
                      left: `${heart.left}px`,
                      animationDelay: `${heart.delay}ms`
                    }}
                  >
                    ❤️
                  </div>
                ))}
              </button>
            </div>

            {/* Product Details */}
            <div style={{ 
              background: 'var(--bg-secondary)',
              padding: '1.5rem',
              borderRadius: '12px',
              border: '1px solid var(--border-color)'
            }}>
              <h3 style={{ marginBottom: '1rem' }}>Product Details</h3>

              {product.yarnType && (
                <div style={{ marginBottom: '0.75rem' }}>
                  <strong>Yarn Type:</strong> {product.yarnType}
                </div>
              )}

              {product.careInstructions && (
                <div style={{ marginBottom: '0.75rem' }}>
                  <strong>Care Instructions:</strong> {product.careInstructions}
                </div>
              )}

              {product.ingredients && (
                <div style={{ marginBottom: '0.75rem' }}>
                  <strong>Ingredients:</strong> {product.ingredients.join(', ')}
                </div>
              )}

              {product.allergens && (
                <div style={{ marginBottom: '0.75rem' }}>
                  <strong>Allergen Info:</strong> {product.allergens.join(', ')}
                </div>
              )}

              <div>
                <strong>Delivery Area:</strong> Pan-India delivery available
              </div>
            </div>
          </div>
        </div>

        {/* Reviews Section */}
        <div className="animate-fade-in" style={{ 
          background: 'var(--bg-secondary)',
          padding: '2rem',
          borderRadius: '16px',
          border: '1px solid var(--border-color)',
          animationDelay: '0.4s'
        }}>
          <h3 style={{ marginBottom: '1.5rem' }}>Customer Reviews</h3>

          {reviews.length === 0 ? (
            <p style={{ color: 'var(--text-secondary)' }}>No reviews yet. Be the first to review!</p>
          ) : (
            <div style={{ display: 'grid', gap: '1rem' }}>
              {reviews.map((review) => (
                <div
                  key={review._id}
                  style={{
                    padding: '1rem',
                    background: 'var(--bg-primary)',
                    borderRadius: '8px',
                    border: '1px solid var(--border-light)'
                  }}
                >
                  <div style={{ 
                    display: 'flex', 
                    justifyContent: 'space-between',
                    alignItems: 'start',
                    marginBottom: '0.5rem'
                  }}>
                    <div>
                      <div style={{ fontWeight: '600', marginBottom: '0.25rem' }}>
                        {review.user?.name || 'Anonymous'}
                        {review.verified && (
                          <span style={{ 
                            color: 'var(--success)',
                            fontSize: '0.8rem',
                            marginLeft: '0.5rem'
                          }}>
                            ✓ Verified Purchase
                          </span>
                        )}
                      </div>
                      <div style={{ display: 'flex', alignItems: 'center', gap: '0.25rem' }}>
                        {[...Array(5)].map((_, i) => (
                          <Star
                            key={i}
                            size={12}
                            fill={i < review.rating ? '#fbbf24' : 'none'}
                            color={i < review.rating ? '#fbbf24' : '#d1d5db'}
                          />
                        ))}
                      </div>
                    </div>
                    <span style={{ 
                      color: 'var(--text-secondary)',
                      fontSize: '0.8rem'
                    }}>
                      {new Date(review.createdAt).toLocaleDateString()}
                    </span>
                  </div>
                  <p style={{ 
                    color: 'var(--text-secondary)',
                    lineHeight: '1.5',
                    margin: 0
                  }}>
                    {review.comment}
                  </p>
                </div>
              ))}
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default ProductDetailPage;
