import jsPDF from 'jspdf';

export const generateInvoicePDF = (order) => {
  const pdf = new jsPDF();

  // Header - Business Details
  pdf.setFontSize(22);
  pdf.setTextColor(139, 69, 19);
  pdf.text('Stitch & Savour', 20, 25);

  pdf.setFontSize(10);
  pdf.setTextColor(100, 100, 100);
  pdf.text('Homemade Food & Handcrafted Crochet Products', 20, 35);
  pdf.text('Contact: +91 9970944685 | Email: sangita.shreyas@gmail.com', 20, 42);

  // Invoice Title & Number
  pdf.setFontSize(18);
  pdf.setTextColor(0, 0, 0);
  pdf.text('INVOICE', 150, 25);

  pdf.setFontSize(10);
  pdf.text(`Invoice #: ${order.orderNumber}`, 150, 35);
  pdf.text(`Date: ${new Date(order.createdAt).toLocaleDateString('en-IN')}`, 150, 42);

  // Separator line
  pdf.setLineWidth(0.5);
  pdf.line(20, 50, 190, 50);

  // Customer Details Section
  pdf.setFontSize(12);
  pdf.setTextColor(139, 69, 19);
  pdf.text('Bill To:', 20, 65);

  pdf.setFontSize(10);
  pdf.setTextColor(0, 0, 0);
  pdf.text(`Name: ${order.customerInfo?.name || 'N/A'}`, 20, 75);
  pdf.text(`Email: ${order.customerInfo?.email || 'N/A'}`, 20, 82);
  pdf.text(`Phone: ${order.customerInfo?.phone || 'N/A'}`, 20, 89);

  // Shipping Address
  if (order.shippingAddress) {
    pdf.text('Shipping Address:', 20, 102);
    pdf.text(`${order.shippingAddress.street || ''}`, 20, 109);
    pdf.text(`${order.shippingAddress.city || ''}, ${order.shippingAddress.state || ''} ${order.shippingAddress.pincode || ''}`, 20, 116);
  }

  // Payment Method
  pdf.text(`Payment: ${order.paymentMethod || 'COD'}`, 120, 75);

  // Items Table Header
  const tableStartY = 135;
  pdf.setFillColor(245, 245, 245);
  pdf.rect(20, tableStartY - 5, 170, 12, 'F');

  pdf.setFontSize(10);
  pdf.setTextColor(0, 0, 0);
  pdf.text('Item', 25, tableStartY);
  pdf.text('Qty', 120, tableStartY);
  pdf.text('Price', 140, tableStartY);
  pdf.text('Total', 165, tableStartY);

  // Items List
  let yPos = tableStartY + 15;
  pdf.setFontSize(9);

  order.items?.forEach((item) => {
    const itemTotal = item.quantity * item.price;
    pdf.text(item.name.length > 35 ? item.name.substring(0, 35) + '...' : item.name, 25, yPos);
    pdf.text(item.quantity.toString(), 125, yPos);
    pdf.text(`₹${item.price}`, 140, yPos);
    pdf.text(`₹${itemTotal}`, 165, yPos);
    yPos += 12;
  });

  // Total Section
  pdf.setLineWidth(0.3);
  pdf.line(120, yPos + 5, 190, yPos + 5);

  yPos += 15;
  pdf.setFontSize(11);
  pdf.setTextColor(139, 69, 19);
  pdf.text(`Total Amount: ₹${order.total || order.totalAmount}`, 120, yPos);

  // Footer
  yPos += 25;
  pdf.setFontSize(8);
  pdf.setTextColor(100, 100, 100);
  pdf.text('Thank you for choosing Stitch & Savour!', 20, yPos);
  pdf.text('For any queries, contact us at +91 9970944685', 20, yPos + 7);

  // Download
  pdf.save(`Invoice-${order.orderNumber}.pdf`);
};
