import React, { useState, useEffect } from 'react';
import { useToast } from '../context/ToastContext';
import { exportOrdersToExcel } from '../utils/excelExport';
import { 
  Package, Users, TrendingUp, DollarSign, Download, Search, 
  Filter, Plus, Edit, Trash2, Eye, Calendar, BarChart3,
  PieChart, Activity, ShoppingCart, Star, MessageSquare,
  FileText, Settings, AlertTriangle, CheckCircle, Clock,
  Truck, MapPin, Phone, Mail, ExternalLink, Save
} from 'lucide-react';

const AdminDashboard = () => {
  const { showToast } = useToast();
  const [activeTab, setActiveTab] = useState('analytics');
  const [searchTerm, setSearchTerm] = useState('');
  const [statusFilter, setStatusFilter] = useState('all');
  const [categoryFilter, setCategoryFilter] = useState('all');
  const [paymentFilter, setPaymentFilter] = useState('all');
  const [selectedOrder, setSelectedOrder] = useState(null);
  const [showAddProduct, setShowAddProduct] = useState(false);
  const [newProduct, setNewProduct] = useState({ name: '', price: '', category: 'food', description: '', image: '' });
  const [expenses, setExpenses] = useState({});
  const [loading, setLoading] = useState(false);

  const [dashboardData, setDashboardData] = useState({
    orders: [],
    products: [],
    reviews: [],
    customers: [],
    inventory: []
  });

  useEffect(() => {
    loadDashboardData();
  }, []);

  const loadDashboardData = () => {
    // Load all orders from localStorage
    const allOrders = JSON.parse(localStorage.getItem('userOrders') || '[]');
    const allReviews = JSON.parse(localStorage.getItem('productReviews') || '[]');
    const allProducts = JSON.parse(localStorage.getItem('adminProducts') || '[]');
    const savedExpenses = JSON.parse(localStorage.getItem('productExpenses') || '{}');

    // Get unique customers
    const customers = [...new Map(allOrders.map(order => 
      [order.customerName, { name: order.customerName, phone: order.phone, orders: allOrders.filter(o => o.customerName === order.customerName).length }]
    )).values()];

    setDashboardData({ orders: allOrders, products: allProducts, reviews: allReviews, customers });
    setExpenses(savedExpenses);
  };



  const tabs = [
    { id: 'analytics', label: 'Analytics', icon: <BarChart3 className="w-4 h-4" /> },
    { id: 'orders', label: 'Orders', icon: <Package className="w-4 h-4" /> },
    { id: 'products', label: 'Products', icon: <ShoppingCart className="w-4 h-4" /> },
    { id: 'inventory', label: 'Inventory', icon: <Activity className="w-4 h-4" /> },
    { id: 'customers', label: 'Customers', icon: <Users className="w-4 h-4" /> },
    { id: 'reviews', label: 'Reviews', icon: <Star className="w-4 h-4" /> }
  ];

  const updateOrderStatus = (orderNumber, newStatus) => {
    const allOrders = JSON.parse(localStorage.getItem('userOrders') || '[]');
    const updatedOrders = allOrders.map(order => 
      order.orderNumber === orderNumber ? { ...order, status: newStatus } : order
    );
    localStorage.setItem('userOrders', JSON.stringify(updatedOrders));
    loadDashboardData();
    showToast(`Order ${orderNumber} updated to ${newStatus}`, 'success');
  };

  const deleteOrder = (orderNumber) => {
    if (window.confirm('Are you sure you want to delete this order?')) {
      const allOrders = JSON.parse(localStorage.getItem('userOrders') || '[]');
      const filteredOrders = allOrders.filter(order => order.orderNumber !== orderNumber);
      localStorage.setItem('userOrders', JSON.stringify(filteredOrders));
      loadDashboardData();
      showToast('Order deleted successfully', 'success');
    }
  };

  const addProduct = () => {
    if (!newProduct.name || !newProduct.price) {
      showToast('Please fill all required fields', 'error');
      return;
    }

    const allProducts = JSON.parse(localStorage.getItem('adminProducts') || '[]');
    const product = {
      id: Date.now(),
      ...newProduct,
      price: parseFloat(newProduct.price),
      createdAt: new Date().toISOString()
    };

    allProducts.push(product);
    localStorage.setItem('adminProducts', JSON.stringify(allProducts));
    setNewProduct({ name: '', price: '', category: 'food', description: '', image: '' });
    setShowAddProduct(false);
    loadDashboardData();
    showToast('Product added successfully', 'success');
  };

  const updateExpense = (productName, cost) => {
    const updatedExpenses = { ...expenses, [productName]: parseFloat(cost) || 0 };
    setExpenses(updatedExpenses);
    localStorage.setItem('productExpenses', JSON.stringify(updatedExpenses));
  };

  const getDeliveryDeadline = (orderDate, category) => {
    const date = new Date(orderDate);
    const days = category === 'crochet' ? 14 : 2;
    date.setDate(date.getDate() + days);
    return date;
  };

  const getStats = () => {
    const orders = dashboardData.orders;
    const totalSales = orders.reduce((sum, order) => sum + order.total, 0);
    const todaysSales = orders.filter(order => 
      new Date(order.createdAt).toDateString() === new Date().toDateString()
    ).reduce((sum, order) => sum + order.total, 0);

    const totalExpenses = Object.values(expenses).reduce((sum, cost) => sum + cost, 0);
    const netProfit = totalSales - totalExpenses;

    return {
      totalSales,
      todaysSales,
      totalOrders: orders.length,
      pendingOrders: orders.filter(o => o.status !== 'delivered').length,
      totalCustomers: dashboardData.customers?.length || 0,
      netProfit
    };
  };

  const filteredOrders = dashboardData.orders.filter(order => {
    const matchesSearch = order.orderNumber.toLowerCase().includes(searchTerm.toLowerCase()) || 
                         order.customerName.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesStatus = statusFilter === 'all' || order.status === statusFilter;
    const matchesCategory = categoryFilter === 'all' || order.items?.[0]?.category === categoryFilter;
    const matchesPayment = paymentFilter === 'all' || 
                          (paymentFilter === 'paid' && order.paymentMethod === 'qr') ||
                          (paymentFilter === 'pending' && order.paymentMethod === 'cod');
    return matchesSearch && matchesStatus && matchesCategory && matchesPayment;
  });

  const stats = getStats();





  return (
    <div style={{ padding: '2rem 0', minHeight: '100vh' }}>
      <div className="container">
        <div style={{ marginBottom: '2rem' }}>
          <div style={{ background: 'linear-gradient(135deg, var(--primary-color), var(--secondary-color))', borderRadius: '20px', padding: '2rem', color: 'white' }}>
            <h1 style={{ fontSize: '2.5rem', fontWeight: '800', marginBottom: '0.5rem' }}>
              Admin Dashboard
            </h1>
            <p style={{ fontSize: '1.1rem', opacity: '0.9' }}>
              Manage your Stitch & Savour business - Welcome back, Sangita! 👋
            </p>
            <div style={{ display: 'flex', gap: '2rem', marginTop: '1.5rem' }}>
              <div style={{ background: 'rgba(255,255,255,0.2)', padding: '1rem', borderRadius: '12px', backdropFilter: 'blur(10px)' }}>
                <div style={{ fontSize: '1.5rem', fontWeight: '700' }}>₹{stats.todaysSales}</div>
                <div style={{ fontSize: '0.9rem', opacity: '0.8' }}>Today's Sales</div>
              </div>
              <div style={{ background: 'rgba(255,255,255,0.2)', padding: '1rem', borderRadius: '12px', backdropFilter: 'blur(10px)' }}>
                <div style={{ fontSize: '1.5rem', fontWeight: '700' }}>{stats.pendingOrders}</div>
                <div style={{ fontSize: '0.9rem', opacity: '0.8' }}>Pending Orders</div>
              </div>
            </div>
          </div>
        </div>

        {/* Stats Cards */}
        <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(250px, 1fr))', gap: '1.5rem', marginBottom: '2rem' }}>
          <div style={{ background: 'var(--bg-secondary)', padding: '2rem', borderRadius: '16px', border: '1px solid var(--border-color)' }}>
            <div style={{ display: 'flex', alignItems: 'center', gap: '1rem', marginBottom: '1rem' }}>
              <div style={{ padding: '0.75rem', background: 'var(--primary-color)', borderRadius: '12px' }}>
                <DollarSign size={24} color="white" />
              </div>
              <div>
                <h3 style={{ margin: 0, fontSize: '2rem', fontWeight: '800' }}>₹{stats.totalSales}</h3>
                <p style={{ margin: 0, color: 'var(--text-secondary)' }}>Total Sales</p>
              </div>
            </div>
            <div style={{ color: 'var(--success)', fontSize: '0.9rem' }}>
              +12% from last month
            </div>
          </div>

          <div style={{ background: 'var(--bg-secondary)', padding: '2rem', borderRadius: '16px', border: '1px solid var(--border-color)' }}>
            <div style={{ display: 'flex', alignItems: 'center', gap: '1rem', marginBottom: '1rem' }}>
              <div style={{ padding: '0.75rem', background: 'var(--success)', borderRadius: '12px' }}>
                <Package size={24} color="white" />
              </div>
              <div>
                <h3 style={{ margin: 0, fontSize: '2rem', fontWeight: '800' }}>{stats.totalOrders}</h3>
                <p style={{ margin: 0, color: 'var(--text-secondary)' }}>Total Orders</p>
              </div>
            </div>
            <div style={{ color: 'var(--success)', fontSize: '0.9rem' }}>
              +8% from last month
            </div>
          </div>

          <div style={{ background: 'var(--bg-secondary)', padding: '2rem', borderRadius: '16px', border: '1px solid var(--border-color)' }}>
            <div style={{ display: 'flex', alignItems: 'center', gap: '1rem', marginBottom: '1rem' }}>
              <div style={{ padding: '0.75rem', background: 'var(--warning)', borderRadius: '12px' }}>
                <Users size={24} color="white" />
              </div>
              <div>
                <h3 style={{ margin: 0, fontSize: '2rem', fontWeight: '800' }}>{stats.totalCustomers}</h3>
                <p style={{ margin: 0, color: 'var(--text-secondary)' }}>Customers</p>
              </div>
            </div>
            <div style={{ color: 'var(--success)', fontSize: '0.9rem' }}>
              +15% from last month
            </div>
          </div>

          <div style={{ background: 'var(--bg-secondary)', padding: '2rem', borderRadius: '16px', border: '1px solid var(--border-color)' }}>
            <div style={{ display: 'flex', alignItems: 'center', gap: '1rem', marginBottom: '1rem' }}>
              <div style={{ padding: '0.75rem', background: 'var(--info)', borderRadius: '12px' }}>
                <TrendingUp size={24} color="white" />
              </div>
              <div>
                <h3 style={{ margin: 0, fontSize: '2rem', fontWeight: '800' }}>₹{Math.round(stats.netProfit)}</h3>
                <p style={{ margin: 0, color: 'var(--text-secondary)' }}>Net Profit</p>
              </div>
            </div>
            <div style={{ color: 'var(--success)', fontSize: '0.9rem' }}>
              +18% from last month
            </div>
          </div>
        </div>

        {/* Tab Navigation */}
        <div style={{ background: 'var(--bg-secondary)', borderRadius: '16px', border: '1px solid var(--border-color)', marginBottom: '2rem' }}>
          <div style={{ borderBottom: '1px solid var(--border-color)' }}>
            <nav style={{ display: 'flex', padding: '0 1.5rem', overflowX: 'auto' }}>
              {tabs.map((tab) => (
                <button
                  key={tab.id}
                  onClick={() => setActiveTab(tab.id)}
                  style={{
                    padding: '1rem 1.5rem',
                    background: 'none',
                    border: 'none',
                    borderBottom: activeTab === tab.id ? '2px solid var(--primary-color)' : '2px solid transparent',
                    color: activeTab === tab.id ? 'var(--primary-color)' : 'var(--text-secondary)',
                    fontWeight: activeTab === tab.id ? '600' : '400',
                    cursor: 'pointer',
                    display: 'flex',
                    alignItems: 'center',
                    gap: '0.5rem',
                    whiteSpace: 'nowrap'
                  }}
                >
                  {tab.icon}
                  {tab.label}
                </button>
              ))}
            </nav>
          </div>

          <div style={{ padding: '2rem' }}>
            {activeTab === 'analytics' && (
              <div>
                <h3 style={{ marginBottom: '2rem', display: 'flex', alignItems: 'center', gap: '0.5rem' }}>
                  <BarChart3 className="w-5 h-5" />
                  Business Analytics
                </h3>

                <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))', gap: '2rem', marginBottom: '2rem' }}>
                  <div style={{ background: 'var(--bg-tertiary)', padding: '2rem', borderRadius: '16px' }}>
                    <h4 style={{ marginBottom: '1rem' }}>Sales Growth</h4>
                    <div style={{ height: '200px', background: 'var(--bg-primary)', borderRadius: '8px', display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
                      <TrendingUp className="w-12 h-12 text-green-500" />
                      <span style={{ marginLeft: '1rem', fontSize: '1.2rem' }}>+18% This Month</span>
                    </div>
                  </div>

                  <div style={{ background: 'var(--bg-tertiary)', padding: '2rem', borderRadius: '16px' }}>
                    <h4 style={{ marginBottom: '1rem' }}>Investment vs Output</h4>
                    <div style={{ height: '200px', background: 'var(--bg-primary)', borderRadius: '8px', display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
                      <PieChart className="w-12 h-12 text-blue-500" />
                      <div style={{ marginLeft: '1rem' }}>
                        <div>Investment: ₹15,000</div>
                        <div>Output: ₹28,000</div>
                        <div style={{ color: 'var(--success)', fontWeight: '600' }}>ROI: 87%</div>
                      </div>
                    </div>
                  </div>
                </div>

                <div style={{ background: 'var(--bg-tertiary)', padding: '2rem', borderRadius: '16px', marginBottom: '2rem' }}>
                  <h4 style={{ marginBottom: '1rem' }}>Order Heat Map</h4>
                  <div style={{ display: 'grid', gridTemplateColumns: 'repeat(7, 1fr)', gap: '0.5rem' }}>
                    {Array.from({ length: 28 }, (_, i) => (
                      <div key={i} style={{
                        width: '40px',
                        height: '40px',
                        borderRadius: '4px',
                        background: Math.random() > 0.5 ? 'var(--primary-color)' : 'var(--bg-primary)',
                        opacity: Math.random(),
                        display: 'flex',
                        alignItems: 'center',
                        justifyContent: 'center',
                        fontSize: '0.8rem',
                        color: 'white'
                      }}>
                        {Math.floor(Math.random() * 10)}
                      </div>
                    ))}
                  </div>
                </div>
              </div>
            )}

            {activeTab === 'overview' && (
              <div>
                <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '2rem' }}>
                  <h3>Recent Orders</h3>
                  <button onClick={exportToExcel} className="btn btn-primary" style={{ display: 'flex', alignItems: 'center', gap: '0.5rem' }}>
                    <Download size={16} />
                    Export to Excel
                  </button>
                </div>

                <div style={{ overflowX: 'auto' }}>
                  <table style={{ width: '100%', borderCollapse: 'collapse' }}>
                    <thead>
                      <tr style={{ borderBottom: '1px solid var(--border-color)' }}>
                        <th style={{ padding: '1rem', textAlign: 'left' }}>Order ID</th>
                        <th style={{ padding: '1rem', textAlign: 'left' }}>Customer</th>
                        <th style={{ padding: '1rem', textAlign: 'left' }}>Amount</th>
                        <th style={{ padding: '1rem', textAlign: 'left' }}>Status</th>
                        <th style={{ padding: '1rem', textAlign: 'left' }}>Date</th>
                        <th style={{ padding: '1rem', textAlign: 'left' }}>Actions</th>
                      </tr>
                    </thead>
                    <tbody>
                      {dashboardData.orders.map((order) => (
                        <tr key={order._id} style={{ borderBottom: '1px solid var(--border-light)' }}>
                          <td style={{ padding: '1rem' }}>{order.orderNumber}</td>
                          <td style={{ padding: '1rem' }}>{order.user?.name}</td>
                          <td style={{ padding: '1rem' }}>₹{order.total}</td>
                          <td style={{ padding: '1rem' }}>
                            <span style={{
                              padding: '0.25rem 0.75rem',
                              borderRadius: '12px',
                              fontSize: '0.8rem',
                              fontWeight: '600',
                              background: order.status === 'delivered' ? 'var(--success-light)' : 
                                         order.status === 'in-progress' ? 'var(--warning-light)' : 'var(--info-light)',
                              color: order.status === 'delivered' ? 'var(--success)' : 
                                     order.status === 'in-progress' ? 'var(--warning)' : 'var(--info)'
                            }}>
                              {order.status.toUpperCase()}
                            </span>
                          </td>
                          <td style={{ padding: '1rem' }}>{new Date(order.createdAt).toLocaleDateString()}</td>
                          <td style={{ padding: '1rem' }}>
                            <button className="btn btn-sm btn-outline">View</button>
                          </td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                </div>
              </div>
            )}

            {activeTab === 'orders' && (
              <div>
                <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '2rem', flexWrap: 'wrap', gap: '1rem' }}>
                  <h3 style={{ display: 'flex', alignItems: 'center', gap: '0.5rem' }}>
                    <Package className="w-5 h-5" />
                    Order Management ({dashboardData.orders.length})
                  </h3>
                  <div style={{ display: 'flex', gap: '1rem', flexWrap: 'wrap' }}>
                    <div style={{ position: 'relative' }}>
                      <Search size={16} style={{ position: 'absolute', left: '12px', top: '50%', transform: 'translateY(-50%)', color: 'var(--text-secondary)' }} />
                      <input
                        type="text"
                        placeholder="Search by customer or order ID..."
                        value={searchTerm}
                        onChange={(e) => setSearchTerm(e.target.value)}
                        style={{
                          padding: '0.5rem 0.5rem 0.5rem 2.5rem',
                          border: '1px solid var(--border-color)',
                          borderRadius: '8px',
                          width: '250px'
                        }}
                      />
                    </div>
                    <select 
                      value={statusFilter}
                      onChange={(e) => setStatusFilter(e.target.value)}
                      style={{ padding: '0.5rem', border: '1px solid var(--border-color)', borderRadius: '8px' }}
                    >
                      <option value="all">All Status</option>
                      <option value="received">Order Received</option>
                      <option value="in-progress">In Progress</option>
                      <option value="out-for-delivery">Out for Delivery</option>
                      <option value="delivered">Delivered</option>
                    </select>
                    <button onClick={exportToExcel} className="btn btn-primary btn-sm" style={{ display: 'flex', alignItems: 'center', gap: '0.5rem' }}>
                      <Download size={16} />
                      Export
                    </button>
                  </div>
                </div>

                <div style={{ overflowX: 'auto' }}>
                  <table style={{ width: '100%', borderCollapse: 'collapse', background: 'var(--bg-primary)', borderRadius: '12px', overflow: 'hidden' }}>
                    <thead style={{ background: 'var(--bg-tertiary)' }}>
                      <tr>
                        <th style={{ padding: '1rem', textAlign: 'left' }}>Order ID</th>
                        <th style={{ padding: '1rem', textAlign: 'left' }}>Customer</th>
                        <th style={{ padding: '1rem', textAlign: 'left' }}>Amount</th>
                        <th style={{ padding: '1rem', textAlign: 'left' }}>Status</th>
                        <th style={{ padding: '1rem', textAlign: 'left' }}>Payment</th>
                        <th style={{ padding: '1rem', textAlign: 'left' }}>Deadline</th>
                        <th style={{ padding: '1rem', textAlign: 'left' }}>Actions</th>
                      </tr>
                    </thead>
                    <tbody>
                      {dashboardData.orders.filter(order => 
                        (statusFilter === 'all' || order.status === statusFilter) &&
                        (order.orderNumber.toLowerCase().includes(searchTerm.toLowerCase()) || 
                         order.user?.name.toLowerCase().includes(searchTerm.toLowerCase()))
                      ).map((order) => (
                        <tr key={order._id} style={{ borderBottom: '1px solid var(--border-light)' }}>
                          <td style={{ padding: '1rem', fontWeight: '600' }}>{order.orderNumber}</td>
                          <td style={{ padding: '1rem' }}>{order.user?.name}</td>
                          <td style={{ padding: '1rem', fontWeight: '600' }}>₹{order.total}</td>
                          <td style={{ padding: '1rem' }}>
                            <select 
                              value={order.status}
                              onChange={(e) => updateOrderStatus(order._id, e.target.value)}
                              style={{
                                padding: '0.25rem 0.5rem',
                                borderRadius: '6px',
                                border: '1px solid var(--border-color)',
                                fontSize: '0.8rem',
                                background: order.status === 'delivered' ? 'var(--success-light)' : 
                                           order.status === 'in-progress' ? 'var(--warning-light)' : 'var(--info-light)'
                              }}
                            >
                              <option value="received">Order Received</option>
                              <option value="in-progress">In Progress</option>
                              <option value="out-for-delivery">Out for Delivery</option>
                              <option value="delivered">Delivered</option>
                            </select>
                          </td>
                          <td style={{ padding: '1rem' }}>
                            <span style={{
                              padding: '0.25rem 0.5rem',
                              borderRadius: '12px',
                              fontSize: '0.7rem',
                              fontWeight: '600',
                              background: order.paymentMethod === 'qr' ? 'var(--success-light)' : 'var(--warning-light)',
                              color: order.paymentMethod === 'qr' ? 'var(--success)' : 'var(--warning)'
                            }}>
                              {order.paymentMethod === 'qr' ? 'PAID' : 'COD'}
                            </span>
                          </td>
                          <td style={{ padding: '1rem' }}>
                            <div style={{ display: 'flex', alignItems: 'center', gap: '0.5rem' }}>
                              <Calendar className="w-4 h-4" />
                              {new Date(order.estimatedDelivery || order.createdAt).toLocaleDateString()}
                            </div>
                          </td>
                          <td style={{ padding: '1rem' }}>
                            <div style={{ display: 'flex', gap: '0.5rem' }}>
                              <button 
                                onClick={() => setSelectedOrder(order)}
                                className="btn btn-sm btn-outline"
                                style={{ padding: '0.25rem 0.5rem' }}
                              >
                                <Eye size={14} />
                              </button>
                              <button 
                                onClick={() => deleteOrder(order._id)}
                                className="btn btn-sm"
                                style={{ padding: '0.25rem 0.5rem', background: 'var(--error)', color: 'white' }}
                              >
                                <Trash2 size={14} />
                              </button>
                            </div>
                          </td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                </div>
              </div>
            )}

            {activeTab === 'products' && (
              <div>
                <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '2rem' }}>
                  <h3 style={{ display: 'flex', alignItems: 'center', gap: '0.5rem' }}>
                    <ShoppingCart className="w-5 h-5" />
                    Product Management
                  </h3>
                  <button className="btn btn-primary" style={{ display: 'flex', alignItems: 'center', gap: '0.5rem' }}>
                    <Plus size={16} />
                    Add New Product
                  </button>
                </div>

                <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))', gap: '2rem' }}>
                  <div style={{ background: 'var(--bg-tertiary)', padding: '2rem', borderRadius: '16px' }}>
                    <h4 style={{ marginBottom: '1rem' }}>Food Products</h4>
                    <div style={{ fontSize: '2rem', fontWeight: '800', color: 'var(--accent-color)' }}>7</div>
                    <p style={{ color: 'var(--text-secondary)' }}>Active food items</p>
                    <button className="btn btn-outline btn-sm" style={{ marginTop: '1rem' }}>Manage Food Items</button>
                  </div>

                  <div style={{ background: 'var(--bg-tertiary)', padding: '2rem', borderRadius: '16px' }}>
                    <h4 style={{ marginBottom: '1rem' }}>Crochet Products</h4>
                    <div style={{ fontSize: '2rem', fontWeight: '800', color: 'var(--primary-color)' }}>5</div>
                    <p style={{ color: 'var(--text-secondary)' }}>Active crochet items</p>
                    <button className="btn btn-outline btn-sm" style={{ marginTop: '1rem' }}>Manage Crochet Items</button>
                  </div>
                </div>
              </div>
            )}

            {activeTab === 'inventory' && (
              <div>
                <h3 style={{ marginBottom: '2rem', display: 'flex', alignItems: 'center', gap: '0.5rem' }}>
                  <Activity className="w-5 h-5" />
                  Inventory Tracking
                </h3>

                <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(250px, 1fr))', gap: '1.5rem', marginBottom: '2rem' }}>
                  <div style={{ background: 'var(--bg-tertiary)', padding: '1.5rem', borderRadius: '12px' }}>
                    <h4>Maida (Flour)</h4>
                    <div style={{ fontSize: '1.5rem', fontWeight: '700' }}>15 kg</div>
                    <div style={{ color: 'var(--success)', fontSize: '0.9rem' }}>In Stock</div>
                  </div>
                  <div style={{ background: 'var(--bg-tertiary)', padding: '1.5rem', borderRadius: '12px' }}>
                    <h4>Rava (Semolina)</h4>
                    <div style={{ fontSize: '1.5rem', fontWeight: '700' }}>8 kg</div>
                    <div style={{ color: 'var(--warning)', fontSize: '0.9rem' }}>Low Stock</div>
                  </div>
                  <div style={{ background: 'var(--bg-tertiary)', padding: '1.5rem', borderRadius: '12px' }}>
                    <h4>Crochet Thread</h4>
                    <div style={{ fontSize: '1.5rem', fontWeight: '700' }}>25 rolls</div>
                    <div style={{ color: 'var(--success)', fontSize: '0.9rem' }}>In Stock</div>
                  </div>
                </div>

                <div style={{ background: 'var(--bg-tertiary)', padding: '2rem', borderRadius: '16px' }}>
                  <h4 style={{ marginBottom: '1rem' }}>Expense & Profit Tracking</h4>
                  <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))', gap: '1rem' }}>
                    <div>
                      <label>Raw Material Cost per Product:</label>
                      <input type="number" placeholder="₹50" style={{ width: '100%', padding: '0.5rem', marginTop: '0.5rem', border: '1px solid var(--border-color)', borderRadius: '6px' }} />
                    </div>
                    <div>
                      <label>Selling Price:</label>
                      <input type="number" placeholder="₹150" style={{ width: '100%', padding: '0.5rem', marginTop: '0.5rem', border: '1px solid var(--border-color)', borderRadius: '6px' }} />
                    </div>
                    <div style={{ display: 'flex', alignItems: 'end' }}>
                      <div>
                        <div style={{ fontSize: '1.2rem', fontWeight: '600', color: 'var(--success)' }}>Profit: ₹100</div>
                        <div style={{ fontSize: '0.9rem', color: 'var(--text-secondary)' }}>66.7% margin</div>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            )}

            {activeTab === 'customers' && (
              <div>
                <h3 style={{ marginBottom: '2rem', display: 'flex', alignItems: 'center', gap: '0.5rem' }}>
                  <Users className="w-5 h-5" />
                  Customer Insights
                </h3>

                <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))', gap: '2rem', marginBottom: '2rem' }}>
                  <div style={{ background: 'var(--bg-tertiary)', padding: '2rem', borderRadius: '16px' }}>
                    <h4 style={{ marginBottom: '1rem' }}>Repeat Customers</h4>
                    <div style={{ fontSize: '3rem', fontWeight: '800', color: 'var(--primary-color)' }}>23</div>
                    <p style={{ color: 'var(--text-secondary)' }}>34% of total customers</p>
                    <div style={{ marginTop: '1rem', padding: '0.5rem', background: 'var(--success-light)', borderRadius: '6px', fontSize: '0.9rem', color: 'var(--success)' }}>
                      +5 new repeat customers this month
                    </div>
                  </div>

                  <div style={{ background: 'var(--bg-tertiary)', padding: '2rem', borderRadius: '16px' }}>
                    <h4 style={{ marginBottom: '1rem' }}>Most Popular Product</h4>
                    <div style={{ fontWeight: '700', fontSize: '1.2rem', marginBottom: '0.5rem' }}>Boho Chic Granny Square Top</div>
                    <p style={{ color: 'var(--text-secondary)' }}>12 orders this month</p>
                    <div style={{ marginTop: '1rem' }}>
                      <div style={{ fontSize: '0.9rem', color: 'var(--text-secondary)' }}>Category: Crochet</div>
                      <div style={{ fontSize: '0.9rem', color: 'var(--success)' }}>Revenue: ₹15,588</div>
                    </div>
                  </div>

                  <div style={{ background: 'var(--bg-tertiary)', padding: '2rem', borderRadius: '16px' }}>
                    <h4 style={{ marginBottom: '1rem' }}>Product Type Preference</h4>
                    <div style={{ marginBottom: '1rem' }}>
                      <div style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '0.5rem' }}>
                        <span>Crochet Items</span>
                        <span>65%</span>
                      </div>
                      <div style={{ width: '100%', height: '8px', background: 'var(--bg-primary)', borderRadius: '4px' }}>
                        <div style={{ width: '65%', height: '100%', background: 'var(--primary-color)', borderRadius: '4px' }}></div>
                      </div>
                    </div>
                    <div>
                      <div style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '0.5rem' }}>
                        <span>Food Items</span>
                        <span>35%</span>
                      </div>
                      <div style={{ width: '100%', height: '8px', background: 'var(--bg-primary)', borderRadius: '4px' }}>
                        <div style={{ width: '35%', height: '100%', background: 'var(--accent-color)', borderRadius: '4px' }}></div>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            )}

            {activeTab === 'reviews' && (
              <div>
                <h3 style={{ marginBottom: '2rem', display: 'flex', alignItems: 'center', gap: '0.5rem' }}>
                  <Star className="w-5 h-5" />
                  Reviews & Ratings
                </h3>

                <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))', gap: '2rem', marginBottom: '2rem' }}>
                  <div style={{ background: 'var(--bg-tertiary)', padding: '2rem', borderRadius: '16px' }}>
                    <h4>Average Rating</h4>
                    <div style={{ display: 'flex', alignItems: 'center', gap: '1rem', margin: '1rem 0' }}>
                      <div style={{ fontSize: '3rem', fontWeight: '800' }}>4.8</div>
                      <div>
                        <div style={{ display: 'flex', gap: '0.25rem' }}>
                          {[1,2,3,4,5].map(i => <Star key={i} className="w-5 h-5" style={{ fill: i <= 4 ? 'gold' : 'none', color: 'gold' }} />)}
                        </div>
                        <div style={{ color: 'var(--text-secondary)', fontSize: '0.9rem' }}>Based on 47 reviews</div>
                      </div>
                    </div>
                  </div>

                  <div style={{ background: 'var(--bg-tertiary)', padding: '2rem', borderRadius: '16px' }}>
                    <h4>Recent Reviews</h4>
                    <div style={{ marginTop: '1rem' }}>
                      <div style={{ marginBottom: '1rem', padding: '1rem', background: 'var(--bg-primary)', borderRadius: '8px' }}>
                        <div style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '0.5rem' }}>
                          <strong>Priya S.</strong>
                          <div style={{ display: 'flex' }}>
                            {[1,2,3,4,5].map(i => <Star key={i} className="w-4 h-4" style={{ fill: 'gold', color: 'gold' }} />)}
                          </div>
                        </div>
                        <p style={{ fontSize: '0.9rem', color: 'var(--text-secondary)' }}>"Amazing quality crochet top! Exactly as described."</p>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default AdminDashboard;
