import React, { useState } from 'react';
import { MessageCircle, Send, X, Bot } from 'lucide-react';

const Chatbot = () => {
  const [isOpen, setIsOpen] = useState(false);
  const [messages, setMessages] = useState([
    {
      id: 1,
      text: "Hi! I'm your Stitch & Savour assistant! ðŸŒŸ I can help you with information about our homemade food and handcrafted crochet products. What would you like to know?",
      isBot: true,
      timestamp: new Date()
    }
  ]);
  const [inputText, setInputText] = useState('');
  const [isTyping, setIsTyping] = useState(false);

  const websiteInfo = {
    products: {
      food: [
        "Homestyle Poha Chivda - â‚¹25",
        "Sweet & Flaky Shakarpara - â‚¹30", 
        "Crispy & Savory Namak Pare - â‚¹25",
        "Spicy Mixture Namkeen - â‚¹35",
        "Classic Salty Mathri - â‚¹40",
        "Baked Jeera Biscuits - â‚¹50",
        "Homemade Gujiya - â‚¹600"
      ],
      crochet: [
        "Boho Chic Granny Square Top - â‚¹1299",
        "Classic Striped V-Neck Vest - â‚¹999", 
        "Minimalist Pink Tank Top - â‚¹799",
        "Serene Blue & Pink Pooja Mat - â‚¹449",
        "Festive Multicolor Pooja Mat - â‚¹599"
      ]
    },
    delivery: {
      food: "2 days",
      crochet: "2 weeks (custom-made)"
    },
    contact: {
      whatsapp: "+91 9970944685",
      email: "sangita.shreyas@gmail.com",
      phone: "+91 8668806190"
    },
    payment: ["QR Code Payment", "Cash on Delivery (COD)"],
    customization: "Yes! Crochet items can be customized with different colors, sizes, and thread types",
    owner: "Sangita Thakur - passionate about homemade food and handcrafted crochet"
  };

  const generateResponse = (userMessage) => {
    const message = userMessage.toLowerCase();

    // Website-related responses
    if (message.includes('food') || message.includes('snack') || message.includes('eat')) {
      return `ðŸ½ï¸ Our homemade food items include: ${websiteInfo.products.food.join(', ')}. All made fresh with traditional recipes! Food items are delivered within ${websiteInfo.delivery.food}. Would you like to place an order?`;
    }

    if (message.includes('crochet') || message.includes('handmade') || message.includes('craft')) {
      return `ðŸ§¶ Our handcrafted crochet collection: ${websiteInfo.products.crochet.join(', ')}. Each item is custom-made and takes ${websiteInfo.delivery.crochet} for delivery. ${websiteInfo.customization}! Ready to create something beautiful?`;
    }

    if (message.includes('delivery') || message.includes('shipping')) {
      return `ðŸšš Delivery times: Food items - ${websiteInfo.delivery.food}, Crochet items - ${websiteInfo.delivery.crochet}. We deliver across India! Want to place an order?`;
    }

    if (message.includes('payment') || message.includes('pay')) {
      return `ðŸ’³ We accept: ${websiteInfo.payment.join(' and ')}. Simple and secure! Ready to shop?`;
    }

    if (message.includes('contact') || message.includes('phone') || message.includes('whatsapp')) {
      return `ðŸ“ž Contact us: WhatsApp: ${websiteInfo.contact.whatsapp}, Email: ${websiteInfo.contact.email}, Alt Phone: ${websiteInfo.contact.phone}. We're here to help!`;
    }

    if (message.includes('custom') || message.includes('personalize')) {
      return `âœ¨ ${websiteInfo.customization}! Each crochet piece is made specially for you. Share your preferences and we'll create something unique! Want to start customizing?`;
    }

    if (message.includes('sangita') || message.includes('owner') || message.includes('founder')) {
      return `ðŸ‘©â€ðŸ³ ${websiteInfo.owner}! Every product is made with love from our home to yours. Experience the difference of handmade quality!`;
    }

    if (message.includes('price') || message.includes('cost')) {
      return `ðŸ’° Our prices: Food items from â‚¹25-â‚¹600, Crochet items from â‚¹449-â‚¹1299. Great value for handmade quality! Which category interests you?`;
    }

    // Encouraging purchase for off-topic questions
    const encouragingResponses = [
      "That's interesting! Speaking of amazing things, have you seen our beautiful handcrafted crochet tops? They're perfect for any occasion! ðŸŒŸ",
      "I'd love to help with that, but I'm here to share the magic of Stitch & Savour! Our homemade snacks are calling your name - fresh, tasty, and made with love! ðŸª",
      "While I focus on Stitch & Savour, I can tell you that our products make perfect gifts! Handmade crochet items and delicious snacks - what could be better? ðŸŽ",
      "That's beyond my expertise, but I'm excited to tell you about our amazing food collection! From crispy namak pare to sweet gujiya - all homemade goodness! ðŸ¥¨",
      "I specialize in Stitch & Savour products! Did you know our crochet items are completely customizable? Choose your colors, size, and style - made just for you! âœ¨"
    ];

    return encouragingResponses[Math.floor(Math.random() * encouragingResponses.length)];
  };

  const handleSend = () => {
    if (!inputText.trim()) return;

    const userMessage = {
      id: messages.length + 1,
      text: inputText,
      isBot: false,
      timestamp: new Date()
    };

    setMessages(prev => [...prev, userMessage]);
    setInputText('');
    setIsTyping(true);

    // Simulate bot typing delay
    setTimeout(() => {
      const botResponse = {
        id: messages.length + 2,
        text: generateResponse(inputText),
        isBot: true,
        timestamp: new Date()
      };
      setMessages(prev => [...prev, botResponse]);
      setIsTyping(false);
    }, 1000);
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      handleSend();
    }
  };

  return (
    <>
      {/* Chat Button */}
      <button
        onClick={() => setIsOpen(true)}
        style={{
          position: 'fixed',
          bottom: '100px',
          right: '20px',
          width: '60px',
          height: '60px',
          backgroundColor: 'var(--primary-color)',
          borderRadius: '50%',
          border: 'none',
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
          boxShadow: '0 4px 12px rgba(217, 70, 239, 0.4)',
          cursor: 'pointer',
          zIndex: 1000,
          transition: 'all 0.3s ease'
        }}
        onMouseEnter={(e) => e.target.style.transform = 'scale(1.1)'}
        onMouseLeave={(e) => e.target.style.transform = 'scale(1)'}
      >
        <MessageCircle size={24} color="white" />
      </button>

      {/* Chat Window */}
      {isOpen && (
        <div style={{
          position: 'fixed',
          bottom: '20px',
          right: '20px',
          width: '350px',
          height: '500px',
          backgroundColor: 'var(--bg-primary)',
          border: '1px solid var(--border-color)',
          borderRadius: '16px',
          boxShadow: '0 10px 30px rgba(0,0,0,0.2)',
          zIndex: 1001,
          display: 'flex',
          flexDirection: 'column',
          overflow: 'hidden'
        }}>
          {/* Header */}
          <div style={{
            background: 'linear-gradient(135deg, var(--primary-color), var(--secondary-color))',
            color: 'white',
            padding: '1rem',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'space-between'
          }}>
            <div style={{ display: 'flex', alignItems: 'center', gap: '0.5rem' }}>
              <Bot size={20} />
              <span style={{ fontWeight: '600' }}>Stitch & Savour Assistant</span>
            </div>
            <button
              onClick={() => setIsOpen(false)}
              style={{
                background: 'none',
                border: 'none',
                color: 'white',
                cursor: 'pointer'
              }}
            >
              <X size={20} />
            </button>
          </div>

          {/* Messages */}
          <div style={{
            flex: 1,
            padding: '1rem',
            overflowY: 'auto',
            display: 'flex',
            flexDirection: 'column',
            gap: '1rem'
          }}>
            {messages.map((message) => (
              <div
                key={message.id}
                style={{
                  display: 'flex',
                  justifyContent: message.isBot ? 'flex-start' : 'flex-end'
                }}
              >
                <div style={{
                  maxWidth: '80%',
                  padding: '0.75rem',
                  borderRadius: '12px',
                  backgroundColor: message.isBot ? 'var(--bg-secondary)' : 'var(--primary-color)',
                  color: message.isBot ? 'var(--text-primary)' : 'white',
                  fontSize: '0.9rem',
                  lineHeight: '1.4'
                }}>
                  {message.text}
                </div>
              </div>
            ))}

            {isTyping && (
              <div style={{ display: 'flex', justifyContent: 'flex-start' }}>
                <div style={{
                  padding: '0.75rem',
                  borderRadius: '12px',
                  backgroundColor: 'var(--bg-secondary)',
                  color: 'var(--text-primary)'
                }}>
                  <div style={{ display: 'flex', gap: '0.25rem' }}>
                    <div style={{ width: '6px', height: '6px', borderRadius: '50%', backgroundColor: 'var(--primary-color)', animation: 'bounce 1.4s infinite' }}></div>
                    <div style={{ width: '6px', height: '6px', borderRadius: '50%', backgroundColor: 'var(--primary-color)', animation: 'bounce 1.4s infinite 0.2s' }}></div>
                    <div style={{ width: '6px', height: '6px', borderRadius: '50%', backgroundColor: 'var(--primary-color)', animation: 'bounce 1.4s infinite 0.4s' }}></div>
                  </div>
                </div>
              </div>
            )}
          </div>

          {/* Input */}
          <div style={{
            padding: '1rem',
            borderTop: '1px solid var(--border-color)',
            display: 'flex',
            gap: '0.5rem'
          }}>
            <input
              type="text"
              value={inputText}
              onChange={(e) => setInputText(e.target.value)}
              onKeyPress={handleKeyPress}
              placeholder="Ask me about our products..."
              style={{
                flex: 1,
                padding: '0.75rem',
                border: '1px solid var(--border-color)',
                borderRadius: '8px',
                fontSize: '0.9rem'
              }}
            />
            <button
              onClick={handleSend}
              style={{
                padding: '0.75rem',
                backgroundColor: 'var(--primary-color)',
                color: 'white',
                border: 'none',
                borderRadius: '8px',
                cursor: 'pointer'
              }}
            >
              <Send size={16} />
            </button>
          </div>
        </div>
      )}
    </>
  );
};

export default Chatbot;
