import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { Heart, ShoppingCart, Star, Truck, Shield, Award, ArrowLeft } from 'lucide-react';
import { useCart } from '../context/CartContext';

const ProductDetailPage = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const { addToCart } = useCart();
  const [product, setProduct] = useState(null);
  const [selectedImage, setSelectedImage] = useState(0);
  const [quantity, setQuantity] = useState(1);
  const [customizations, setCustomizations] = useState({});
  const [currentPrice, setCurrentPrice] = useState(0);
  const [loading, setLoading] = useState(true);
  const [reviews, setReviews] = useState([]);

  // Sample product data (in real app, this would come from API)
  const sampleProducts = {
    1: {
      id: 1,
      name: 'Boho Chic Granny Square Crochet Top',
      category: 'crochet',
      price: 1299,
      images: ['/images/crochet-boho-top.jpg'],
      shortDescription: 'A vibrant and stylish handmade top featuring a classic granny square pattern.',
      description: 'Made with soft, breathable yarn in a beautiful medley of pink, blue, yellow, and cream, it\'s the perfect statement piece for summer outings, music festivals, or a casual day out. Each top is handmade with care and attention to detail.',
      rating: 4.8,
      reviewCount: 12,
      stockStatus: 'made-to-order',
      deliveryTime: 14,
      yarnType: 'High-quality, skin-friendly cotton yarn',
      careInstructions: 'Gentle hand wash in cold water with mild detergent. Lay flat to dry. Do not bleach or iron.',
      customization: [
        {
          type: 'thread-type',
          label: 'Thread Type',
          options: ['100% Cotton (Soft & Breathable)', 'Acrylic Blend (Durable & Vibrant)'],
          priceModifier: 0
        },
        {
          type: 'color',
          label: 'Colors',
          options: ['As Pictured (Pink, Blue, Yellow, Cream)', 'Custom Color Palette (Specify up to 4 colors)'],
          priceModifier: 0
        },
        {
          type: 'size',
          label: 'Size',
          options: ['Small (S)', 'Medium (M)', 'Large (L)', 'Custom Sizing (+₹200)'],
          priceModifier: { 'Custom Sizing (+₹200)': 200 }
        }
      ],
      ingredients: null,
      allergens: null
    },
    3: {
      id: 3,
      name: 'Minimalist Pink Tank Top',
      category: 'crochet',
      price: 999,
      images: ['/images/crochet-pink-tank-1.jpg'],
      shortDescription: 'Simple, sweet, and essential. This solid pink crochet tank top is a versatile wardrobe staple.',
      description: 'This minimalist pink crochet tank top is perfect for layering or wearing on its own. Made with soft cotton yarn in a beautiful bubblegum pink shade, it features a classic fit that flatters all body types. The simple design makes it versatile enough to dress up or down.',
      rating: 4.7,
      reviewCount: 15,
      stockStatus: 'made-to-order',
      deliveryTime: 10,
      yarnType: 'Premium cotton yarn',
      careInstructions: 'Hand wash in cold water. Lay flat to dry. Do not bleach.',
      customization: [
        {
          type: 'color',
          label: 'Color',
          options: ['Bubblegum Pink', 'White', 'Cream', 'Lavender', 'Mint Green'],
          priceModifier: 0
        },
        {
          type: 'size',
          label: 'Size',
          options: ['Small (S)', 'Medium (M)', 'Large (L)', 'Extra Large (XL)'],
          priceModifier: 0
        }
      ],
      ingredients: null,
      allergens: null
    },
    6: {
      id: 6,
      name: 'Homestyle Poha Chivda',
      category: 'food',
      price: 25,
      pricePerKg: 480,
      images: ['/images/food-poha-chivda-1.jpg'],
      shortDescription: 'A light, crispy, and savory Maharashtrian snack made with thin flattened rice.',
      description: 'Our homemade Poha Chivda is made with thin flattened rice, roasted peanuts, curry leaves, and a secret blend of mild spices. The perfect guilt-free snack to enjoy with your evening tea. Freshly made every day to ensure maximum crispiness and flavor.',
      rating: 4.6,
      reviewCount: 23,
      stockStatus: 'available',
      deliveryTime: 2,
      ingredients: ['Thin Flattened Rice (Poha)', 'Peanuts', 'Edible Vegetable Oil', 'Curry Leaves', 'Turmeric Powder', 'Mustard Seeds', 'Salt', 'Powdered Sugar', 'Asafoetida'],
      allergens: ['Peanuts', 'Prepared in a kitchen that also handles tree nuts, gluten, and dairy'],
      customization: null,
      weightOptions: ['100g Packet', '250g', '500g', '1kg']
    }
  };

  const sampleReviews = [
    {
      id: 1,
      userName: 'Priya K.',
      rating: 5,
      comment: 'I absolutely love my top! The colors are so bright and the fit is perfect.',
      date: '2024-01-15',
      verified: true
    },
    {
      id: 2,
      userName: 'Sunita P.',
      rating: 5,
      comment: 'The chivda is so fresh and perfectly spiced. Not too oily at all!',
      date: '2024-01-10',
      verified: true
    }
  ];

  useEffect(() => {
    // Simulate API call
    setTimeout(() => {
      const productData = sampleProducts[id];
      if (productData) {
        setProduct(productData);
        setCurrentPrice(productData.price);
        setReviews(sampleReviews.filter(r => 
          (productData.category === 'crochet' && r.id === 1) ||
          (productData.category === 'food' && r.id === 2)
        ));
      }
      setLoading(false);
    }, 1000);
  }, [id]);

  const handleCustomizationChange = (type, value) => {
    const newCustomizations = { ...customizations, [type]: value };
    setCustomizations(newCustomizations);

    // Calculate price with modifiers
    let newPrice = product.price;
    product.customization?.forEach(option => {
      const selectedValue = newCustomizations[option.type];
      if (selectedValue && option.priceModifier && typeof option.priceModifier === 'object') {
        newPrice += option.priceModifier[selectedValue] || 0;
      }
    });
    setCurrentPrice(newPrice);
  };

  const handleAddToCart = () => {
    if (product) {
      addToCart(product, quantity, customizations);
      // Show success message
      alert('Added to cart successfully!');
    }
  };

  const handleBuyNow = () => {
    handleAddToCart();
    navigate('/cart');
  };

  if (loading) {
    return (
      <div style={{ 
        minHeight: '60vh', 
        display: 'flex', 
        alignItems: 'center', 
        justifyContent: 'center' 
      }}>
        <div className="spinner"></div>
      </div>
    );
  }

  if (!product) {
    return (
      <div style={{ 
        minHeight: '60vh', 
        display: 'flex', 
        alignItems: 'center', 
        justifyContent: 'center',
        flexDirection: 'column',
        gap: '1rem'
      }}>
        <h2>Product not found</h2>
        <button onClick={() => navigate('/products')} className="btn btn-primary">
          Back to Products
        </button>
      </div>
    );
  }

  return (
    <div style={{ padding: '2rem 0', minHeight: '100vh' }}>
      <div className="container">
        {/* Back Button */}
        <button
          onClick={() => navigate(-1)}
          style={{
            display: 'flex',
            alignItems: 'center',
            gap: '0.5rem',
            background: 'none',
            border: 'none',
            color: 'var(--primary-color)',
            fontSize: '0.9rem',
            cursor: 'pointer',
            marginBottom: '2rem'
          }}
        >
          <ArrowLeft size={16} />
          Back
        </button>

        <div style={{ 
          display: 'grid', 
          gridTemplateColumns: '1fr 1fr', 
          gap: '3rem',
          marginBottom: '3rem'
        }}>
          {/* Product Images */}
          <div>
            <div style={{ 
              border: '1px solid var(--border-color)',
              borderRadius: '16px',
              overflow: 'hidden',
              marginBottom: '1rem'
            }}>
              <img
                src={product.images[selectedImage]}
                alt={product.name}
                style={{
                  width: '100%',
                  height: '400px',
                  objectFit: 'cover'
                }}
                onError={(e) => {
                  e.target.src = '/images/placeholder.jpg';
                }}
              />
            </div>

            {product.images.length > 1 && (
              <div style={{ 
                display: 'flex', 
                gap: '0.5rem',
                overflowX: 'auto'
              }}>
                {product.images.map((image, index) => (
                  <img
                    key={index}
                    src={image}
                    alt={`${product.name} ${index + 1}`}
                    style={{
                      width: '80px',
                      height: '80px',
                      objectFit: 'cover',
                      borderRadius: '8px',
                      cursor: 'pointer',
                      border: selectedImage === index ? '2px solid var(--primary-color)' : '1px solid var(--border-color)'
                    }}
                    onClick={() => setSelectedImage(index)}
                  />
                ))}
              </div>
            )}
          </div>

          {/* Product Info */}
          <div>
            <div style={{ marginBottom: '1rem' }}>
              <span style={{
                background: product.category === 'crochet' ? 'var(--primary-color)' : 'var(--success)',
                color: 'white',
                padding: '0.25rem 0.75rem',
                borderRadius: '12px',
                fontSize: '0.8rem',
                fontWeight: '600',
                textTransform: 'capitalize'
              }}>
                {product.category}
              </span>
            </div>

            <h1 style={{ 
              fontSize: '2rem', 
              fontWeight: '800',
              marginBottom: '1rem',
              lineHeight: '1.2'
            }}>
              {product.name}
            </h1>

            <div style={{ 
              display: 'flex', 
              alignItems: 'center', 
              gap: '1rem',
              marginBottom: '1rem'
            }}>
              <div style={{ display: 'flex', alignItems: 'center', gap: '0.25rem' }}>
                {[...Array(5)].map((_, i) => (
                  <Star
                    key={i}
                    size={16}
                    fill={i < Math.floor(product.rating) ? '#fbbf24' : 'none'}
                    color={i < Math.floor(product.rating) ? '#fbbf24' : '#d1d5db'}
                  />
                ))}
              </div>
              <span style={{ color: 'var(--text-secondary)' }}>
                {product.rating} ({product.reviewCount} reviews)
              </span>
            </div>

            <div style={{ marginBottom: '1.5rem' }}>
              <div style={{ 
                fontSize: '2rem', 
                fontWeight: '800',
                color: 'var(--primary-color)',
                marginBottom: '0.5rem'
              }}>
                ₹{currentPrice}
                {product.pricePerKg && (
                  <span style={{ 
                    fontSize: '1rem', 
                    color: 'var(--text-secondary)',
                    fontWeight: '400',
                    marginLeft: '0.5rem'
                  }}>
                    (₹{product.pricePerKg}/kg)
                  </span>
                )}
              </div>

              <div style={{ 
                display: 'flex', 
                alignItems: 'center', 
                gap: '1rem',
                fontSize: '0.9rem',
                color: 'var(--text-secondary)'
              }}>
                <div style={{ display: 'flex', alignItems: 'center', gap: '0.25rem' }}>
                  <Truck size={16} />
                  Delivery in {product.deliveryTime} days
                </div>
                <div style={{ display: 'flex', alignItems: 'center', gap: '0.25rem' }}>
                  <Shield size={16} />
                  {product.stockStatus === 'made-to-order' ? 'Made to Order' : 'In Stock'}
                </div>
              </div>
            </div>

            <p style={{ 
              color: 'var(--text-secondary)', 
              lineHeight: '1.6',
              marginBottom: '2rem'
            }}>
              {product.description}
            </p>

            {/* Customization Options */}
            {product.customization && (
              <div style={{ marginBottom: '2rem' }}>
                <h3 style={{ marginBottom: '1rem' }}>Customization Options</h3>
                {product.customization.map((option) => (
                  <div key={option.type} style={{ marginBottom: '1rem' }}>
                    <label style={{ 
                      display: 'block', 
                      fontWeight: '600',
                      marginBottom: '0.5rem'
                    }}>
                      {option.label}
                    </label>
                    <select
                      value={customizations[option.type] || ''}
                      onChange={(e) => handleCustomizationChange(option.type, e.target.value)}
                      style={{
                        width: '100%',
                        padding: '0.75rem',
                        border: '1px solid var(--border-color)',
                        borderRadius: '8px',
                        fontSize: '0.9rem'
                      }}
                    >
                      <option value="">Select {option.label}</option>
                      {option.options.map((opt) => (
                        <option key={opt} value={opt}>{opt}</option>
                      ))}
                    </select>
                  </div>
                ))}
              </div>
            )}

            {/* Weight Options for Food */}
            {product.weightOptions && (
              <div style={{ marginBottom: '2rem' }}>
                <h3 style={{ marginBottom: '1rem' }}>Weight Options</h3>
                <div style={{ 
                  display: 'grid', 
                  gridTemplateColumns: 'repeat(auto-fit, minmax(100px, 1fr))',
                  gap: '0.5rem'
                }}>
                  {product.weightOptions.map((weight) => (
                    <button
                      key={weight}
                      style={{
                        padding: '0.75rem',
                        border: '1px solid var(--border-color)',
                        borderRadius: '8px',
                        background: 'var(--bg-secondary)',
                        cursor: 'pointer',
                        fontSize: '0.9rem'
                      }}
                    >
                      {weight}
                    </button>
                  ))}
                </div>
              </div>
            )}

            {/* Quantity */}
            <div style={{ 
              display: 'flex', 
              alignItems: 'center', 
              gap: '1rem',
              marginBottom: '2rem'
            }}>
              <label style={{ fontWeight: '600' }}>Quantity:</label>
              <div style={{ display: 'flex', alignItems: 'center', gap: '0.5rem' }}>
                <button
                  onClick={() => setQuantity(Math.max(1, quantity - 1))}
                  style={{
                    width: '36px',
                    height: '36px',
                    border: '1px solid var(--border-color)',
                    borderRadius: '8px',
                    background: 'var(--bg-secondary)',
                    cursor: 'pointer'
                  }}
                >
                  -
                </button>
                <span style={{ 
                  minWidth: '40px', 
                  textAlign: 'center',
                  fontWeight: '600'
                }}>
                  {quantity}
                </span>
                <button
                  onClick={() => setQuantity(quantity + 1)}
                  style={{
                    width: '36px',
                    height: '36px',
                    border: '1px solid var(--border-color)',
                    borderRadius: '8px',
                    background: 'var(--bg-secondary)',
                    cursor: 'pointer'
                  }}
                >
                  +
                </button>
              </div>
            </div>

            {/* Action Buttons */}
            <div style={{ 
              display: 'flex', 
              gap: '1rem',
              marginBottom: '2rem'
            }}>
              <button
                onClick={handleAddToCart}
                style={{
                  flex: 1,
                  padding: '1rem',
                  background: 'var(--bg-secondary)',
                  border: '2px solid var(--primary-color)',
                  color: 'var(--primary-color)',
                  borderRadius: '12px',
                  fontWeight: '600',
                  cursor: 'pointer',
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'center',
                  gap: '0.5rem'
                }}
              >
                <ShoppingCart size={20} />
                Add to Cart
              </button>
              <button
                onClick={handleBuyNow}
                style={{
                  flex: 1,
                  padding: '1rem',
                  background: 'var(--primary-color)',
                  border: 'none',
                  color: 'white',
                  borderRadius: '12px',
                  fontWeight: '600',
                  cursor: 'pointer'
                }}
              >
                Buy Now
              </button>
              <button
                style={{
                  padding: '1rem',
                  background: 'var(--bg-secondary)',
                  border: '1px solid var(--border-color)',
                  borderRadius: '12px',
                  cursor: 'pointer'
                }}
              >
                <Heart size={20} />
              </button>
            </div>

            {/* Product Details */}
            <div style={{ 
              background: 'var(--bg-secondary)',
              padding: '1.5rem',
              borderRadius: '12px',
              border: '1px solid var(--border-color)'
            }}>
              <h3 style={{ marginBottom: '1rem' }}>Product Details</h3>

              {product.yarnType && (
                <div style={{ marginBottom: '0.75rem' }}>
                  <strong>Yarn Type:</strong> {product.yarnType}
                </div>
              )}

              {product.careInstructions && (
                <div style={{ marginBottom: '0.75rem' }}>
                  <strong>Care Instructions:</strong> {product.careInstructions}
                </div>
              )}

              {product.ingredients && (
                <div style={{ marginBottom: '0.75rem' }}>
                  <strong>Ingredients:</strong> {product.ingredients.join(', ')}
                </div>
              )}

              {product.allergens && (
                <div style={{ marginBottom: '0.75rem' }}>
                  <strong>Allergen Info:</strong> {product.allergens.join(', ')}
                </div>
              )}

              <div>
                <strong>Delivery Area:</strong> Pan-India delivery available
              </div>
            </div>
          </div>
        </div>

        {/* Reviews Section */}
        <div style={{ 
          background: 'var(--bg-secondary)',
          padding: '2rem',
          borderRadius: '16px',
          border: '1px solid var(--border-color)'
        }}>
          <h3 style={{ marginBottom: '1.5rem' }}>Customer Reviews</h3>

          {reviews.length === 0 ? (
            <p style={{ color: 'var(--text-secondary)' }}>No reviews yet. Be the first to review!</p>
          ) : (
            <div style={{ display: 'grid', gap: '1rem' }}>
              {reviews.map((review) => (
                <div
                  key={review.id}
                  style={{
                    padding: '1rem',
                    background: 'var(--bg-primary)',
                    borderRadius: '8px',
                    border: '1px solid var(--border-light)'
                  }}
                >
                  <div style={{ 
                    display: 'flex', 
                    justifyContent: 'space-between',
                    alignItems: 'start',
                    marginBottom: '0.5rem'
                  }}>
                    <div>
                      <div style={{ fontWeight: '600', marginBottom: '0.25rem' }}>
                        {review.userName}
                        {review.verified && (
                          <span style={{ 
                            color: 'var(--success)',
                            fontSize: '0.8rem',
                            marginLeft: '0.5rem'
                          }}>
                            ✓ Verified Purchase
                          </span>
                        )}
                      </div>
                      <div style={{ display: 'flex', alignItems: 'center', gap: '0.25rem' }}>
                        {[...Array(5)].map((_, i) => (
                          <Star
                            key={i}
                            size={12}
                            fill={i < review.rating ? '#fbbf24' : 'none'}
                            color={i < review.rating ? '#fbbf24' : '#d1d5db'}
                          />
                        ))}
                      </div>
                    </div>
                    <span style={{ 
                      color: 'var(--text-secondary)',
                      fontSize: '0.8rem'
                    }}>
                      {new Date(review.date).toLocaleDateString()}
                    </span>
                  </div>
                  <p style={{ 
                    color: 'var(--text-secondary)',
                    lineHeight: '1.5',
                    margin: 0
                  }}>
                    {review.comment}
                  </p>
                </div>
              ))}
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default ProductDetailPage;
