import React, { useState, useEffect } from 'react';
import { Routes, Route, Link, useNavigate } from 'react-router-dom';

function App() {
  const [theme, setTheme] = useState(localStorage.getItem('theme') || 'system');
  const [user, setUser] = useState(JSON.parse(localStorage.getItem('user')) || null);
  const [cart, setCart] = useState(() => {
    try {
      return JSON.parse(localStorage.getItem('cart')) || [];
    } catch {
      return [];
    }
  });
  const [showLogin, setShowLogin] = useState(false);
  const [showSignup, setShowSignup] = useState(false);
  const [authError, setAuthError] = useState('');
  const [showFAQ, setShowFAQ] = useState({});
  const [showThemeMenu, setShowThemeMenu] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    document.documentElement.setAttribute('data-theme', theme);
    localStorage.setItem('theme', theme);
  }, [theme]);

  useEffect(() => {
    localStorage.setItem('cart', JSON.stringify(cart));
  }, [cart]);

  useEffect(() => {
    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          if (entry.isIntersecting) {
            entry.target.classList.remove('animate');
            entry.target.classList.add('visible');
          } else {
            entry.target.classList.add('animate');
            entry.target.classList.remove('visible');
          }
        });
      },
      { threshold: 0.1 }
    );

    const elements = document.querySelectorAll('.animate-on-scroll');
    elements.forEach((el) => {
      el.classList.add('animate');
      observer.observe(el);
    });

    return () => observer.disconnect();
  }, []);

  const login = (email, password) => {
    setAuthError('');
    if (email === 'admin@stitchandsavour.com' && password === 'admin123') {
      const adminUser = { name: 'Admin', email, role: 'admin' };
      setUser(adminUser);
      localStorage.setItem('user', JSON.stringify(adminUser));
      setShowLogin(false);
      navigate('/admin');
      return;
    }

    // Check if user exists in registered users
    const registeredUsers = JSON.parse(localStorage.getItem('registeredUsers')) || [];
    const existingUser = registeredUsers.find(u => u.email === email && u.password === password);

    if (existingUser) {
      const userData = { name: existingUser.name, email: existingUser.email, role: 'user' };
      setUser(userData);
      localStorage.setItem('user', JSON.stringify(userData));
      setShowLogin(false);
      navigate('/dashboard');
    } else {
      setAuthError('Invalid email or password');
    }
  };

  const logout = () => {
    setUser(null);
    localStorage.removeItem('user');
    navigate('/');
  };

  const addToCart = (product) => {
    if (!user) {
      setShowLogin(true);
      return;
    }
    const existingItem = cart.find(item => item.id === product.id);
    if (existingItem) {
      setCart(cart.map(item => 
        item.id === product.id 
          ? { ...item, quantity: item.quantity + 1 }
          : item
      ));
    } else {
      setCart([...cart, { ...product, quantity: 1 }]);
    }
    showNotification(`${product.name} added to cart!`, 'success');
  };

  const showNotification = (message, type = 'info') => {
    const notification = document.createElement('div');
    notification.className = `notification notification-${type}`;
    notification.textContent = message;
    notification.style.cssText = `
      position: fixed;
      top: 100px;
      right: 20px;
      background: ${type === 'success' ? 'var(--success)' : 'var(--primary-color)'};
      color: white;
      padding: 1rem 1.5rem;
      border-radius: 12px;
      box-shadow: var(--shadow-md);
      z-index: 2000;
      animation: slideIn 0.3s ease;
    `;
    document.body.appendChild(notification);
    setTimeout(() => {
      notification.style.animation = 'fadeOut 0.3s ease';
      setTimeout(() => notification.remove(), 300);
    }, 3000);
  };

  const products = [
    {
      id: 1,
      name: 'Boho Chic Granny Square Top',
      category: 'crochet',
      price: 1299,
      image: '/images/crochet-boho-top.jpg',
      description: 'A vibrant handmade top featuring classic granny square pattern',
      deliveryTime: '2 weeks',
      rating: 4.8,
      reviews: 12
    },
    {
      id: 2,
      name: 'Classic Striped V-Neck Vest',
      category: 'crochet',
      price: 1199,
      image: '/images/crochet-striped-vest.jpg',
      description: 'Timeless V-neck vest with chic teal green and cream stripes',
      deliveryTime: '2 weeks',
      rating: 4.9,
      reviews: 8
    },
    {
      id: 3,
      name: 'Minimalist Pink Tank Top',
      category: 'crochet',
      price: 999,
      image: '/images/crochet-pink-tank.jpg',
      description: 'Simple, sweet, and essential pink crochet tank top',
      deliveryTime: '2 weeks',
      rating: 4.7,
      reviews: 15
    },
    {
      id: 4,
      name: 'Homestyle Poha Chivda',
      category: 'food',
      price: 25,
      image: '/images/food-poha-chivda.jpg',
      description: 'Light, crispy Maharashtrian snack with roasted peanuts',
      deliveryTime: '2 days',
      rating: 4.9,
      reviews: 25
    },
    {
      id: 5,
      name: 'Sweet & Flaky Shakarpara',
      category: 'food',
      price: 25,
      image: '/images/food-shakarpara.jpg',
      description: 'Traditional sweet snack that melts in your mouth',
      deliveryTime: '2 days',
      rating: 4.8,
      reviews: 18
    },
    {
      id: 6,
      name: 'Crispy Namak Pare',
      category: 'food',
      price: 25,
      image: '/images/food-namak-pare.jpg',
      description: 'Perfect savory companion for tea with ajwain flavor',
      deliveryTime: '2 days',
      rating: 4.7,
      reviews: 22
    }
  ];

  const getThemeIcon = () => {
    switch(theme) {
      case 'light': return '☀️';
      case 'dark': return '🌙';
      case 'system': return '💻';
      default: return '💻';
    }
  };

  return (
    <div className="App">
      {/* Enhanced Navigation */}
      <nav style={{ 
        display: 'flex', 
        justifyContent: 'space-between', 
        alignItems: 'center',
        padding: '1rem 2rem', 
        background: 'var(--bg-primary)',
        borderBottom: '1px solid var(--border-light)',
        boxShadow: 'var(--shadow-sm)',
        position: 'fixed',
        top: 0,
        width: '100%',
        zIndex: 1000,
        backdropFilter: 'blur(10px)'
      }}>
        <Link to="/" style={{ textDecoration: 'none' }}>
          <div style={{ display: 'flex', alignItems: 'center', gap: '0.5rem' }}>
            <div style={{ 
              width: '40px', 
              height: '40px', 
              background: 'linear-gradient(135deg, var(--primary-color), var(--primary-dark))',
              borderRadius: '12px',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
              color: 'white',
              fontSize: '1.2rem'
            }}>
              🧶
            </div>
            <h2 style={{ 
              background: 'linear-gradient(135deg, var(--primary-color), var(--secondary-color))',
              WebkitBackgroundClip: 'text',
              WebkitTextFillColor: 'transparent',
              margin: 0,
              fontSize: '1.5rem',
              fontWeight: '700'
            }}>
              Stitch & Savour
            </h2>
          </div>
        </Link>

        <div style={{ display: 'flex', alignItems: 'center', gap: '1rem' }}>
          {/* Enhanced Theme Toggle */}
          <div style={{ position: 'relative' }}>
            <button 
              onClick={() => setShowThemeMenu(!showThemeMenu)}
              style={{
                background: 'var(--bg-secondary)',
                color: 'var(--text-primary)',
                border: '2px solid var(--border-color)',
                padding: '0.5rem',
                borderRadius: '12px',
                cursor: 'pointer',
                width: '44px',
                height: '44px',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                fontSize: '1.2rem',
                transition: 'all 0.3s ease'
              }}
            >
              {getThemeIcon()}
            </button>

            {showThemeMenu && (
              <div style={{
                position: 'absolute',
                top: '100%',
                right: 0,
                marginTop: '0.5rem',
                background: 'var(--bg-primary)',
                border: '1px solid var(--border-color)',
                borderRadius: '12px',
                boxShadow: 'var(--shadow-md)',
                padding: '0.5rem',
                minWidth: '120px',
                zIndex: 1001
              }}>
                {['light', 'dark', 'system'].map(themeOption => (
                  <button
                    key={themeOption}
                    onClick={() => {
                      setTheme(themeOption);
                      setShowThemeMenu(false);
                    }}
                    style={{
                      width: '100%',
                      padding: '0.5rem 0.75rem',
                      background: theme === themeOption ? 'var(--primary-color)' : 'transparent',
                      color: theme === themeOption ? 'white' : 'var(--text-primary)',
                      border: 'none',
                      borderRadius: '8px',
                      cursor: 'pointer',
                      textAlign: 'left',
                      fontSize: '0.9rem',
                      marginBottom: '0.25rem',
                      transition: 'all 0.2s ease'
                    }}
                  >
                    {themeOption === 'light' && '☀️ Light'}
                    {themeOption === 'dark' && '🌙 Dark'}
                    {themeOption === 'system' && '💻 System'}
