function Cart({ cart, setCart }) {
  const total = cart.reduce((sum, item) => sum + (item.price * item.quantity), 0);

  return (
    <div style={{ padding: '2rem' }}>
      <h1>Shopping Cart</h1>
      {cart.length === 0 ? (
        <p>Your cart is empty</p>
      ) : (
        <>
          {cart.map(item => (
            <div key={item.id} style={{ display: 'flex', justifyContent: 'space-between', padding: '1rem', border: '1px solid #ddd', marginBottom: '1rem' }}>
              <div>
                <h3>{item.name}</h3>
                <p>₹{item.price} x {item.quantity}</p>
              </div>
              <button onClick={() => setCart(cart.filter(i => i.id !== item.id))}>Remove</button>
            </div>
          ))}
          <div style={{ fontSize: '1.5rem', fontWeight: 'bold' }}>
            Total: ₹{total}
          </div>
          <button style={{ padding: '1rem 2rem', background: '#e91e63', color: 'white', border: 'none', borderRadius: '8px', marginTop: '1rem' }}>
            Proceed to Checkout
          </button>
        </>
      )}
    </div>
  );
}

