import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import { useCart } from '../context/CartContext';
import { useWishlist } from '../context/WishlistContext';
import { useToast } from '../context/ToastContext';
import { generateInvoicePDF } from '../utils/pdfGenerator';
import Chatbot from '../components/Chatbot';
import { 
  Package, 
  ShoppingBag, 
  Heart, 
  User, 
  MapPin, 
  Phone, 
  Mail,
  Clock,
  CheckCircle,
  Truck,
  Star,
  MessageCircle,
  CreditCard,
  AlertCircle,
  Calendar,
  Search,
  Filter,
  Download,
  Eye
} from 'lucide-react';

const UserDashboard = () => {
  const { user } = useAuth();
  const { cartItems, addToCart } = useCart();
  const { wishlistItems, removeFromWishlist, addToWishlist } = useWishlist();
  const { showToast } = useToast();
  const navigate = useNavigate();
  const [activeTab, setActiveTab] = useState('overview');
  const [orders, setOrders] = useState([]);

  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');
  const [statusFilter, setStatusFilter] = useState('all');
  const [paymentDue, setPaymentDue] = useState(0);
  const [selectedProduct, setSelectedProduct] = useState('');
  const [rating, setRating] = useState(0);
  const [reviewText, setReviewText] = useState('');
  const [showPaymentModal, setShowPaymentModal] = useState(false);

  useEffect(() => {
    fetchUserData();
  }, []);

  const getExpectedDeliveryDate = (productType, orderDate) => {
    const date = new Date(orderDate);
    if (productType === 'food') {
      date.setDate(date.getDate() + 2); // 2 days for food
    } else if (productType === 'crochet') {
      date.setDate(date.getDate() + 14); // 2 weeks for crochet
    }
    return date;
  };

  const getGreetingMessage = () => {
    const hour = new Date().getHours();
    if (hour < 12) return 'Good morning';
    if (hour < 17) return 'Good afternoon';
    return 'Good evening';
  };

  const filteredOrders = orders.filter(order => {
    const matchesSearch = (order.orderNumber || order.id || '').toString().includes(searchTerm) || 
                         (order.customerName || '').toLowerCase().includes(searchTerm.toLowerCase());
    const matchesStatus = statusFilter === 'all' || order.status === statusFilter;
    return matchesSearch && matchesStatus;
  });

  const fetchUserData = async () => {
    try {
      // Get orders from localStorage for current user only
      const allOrders = JSON.parse(localStorage.getItem('userOrders') || '[]');
      const userOrders = allOrders.filter(order => order.customerName === user?.name || order.userId === user?.id);

      const formattedOrders = userOrders.map(order => ({
        _id: order.orderId,
        orderNumber: order.orderNumber,
        total: order.total,
        status: order.status,
        paymentMethod: order.paymentMethod,
        createdAt: order.createdAt,
        estimatedDelivery: order.estimatedDelivery,
        items: order.items,
        productImage: order.items?.[0]?.productImage || order.items?.[0]?.images?.[0] || '/images/placeholder.jpg',
        productName: order.items?.[0]?.name || 'Order Items'
      }));

      setOrders(formattedOrders);
      // Calculate payment due for current user's COD orders only
      const userPaymentDue = formattedOrders
        .filter(o => o.paymentMethod === 'cod' && o.status !== 'delivered')
        .reduce((sum, o) => sum + o.total, 0);
      setPaymentDue(userPaymentDue);

      setLoading(false);
    } catch (error) {
      console.error('Error fetching user data:', error);
      setLoading(false);
    }
  };

  const getStatusIcon = (status) => {
    switch (status) {
      case 'received':
        return <Clock className="w-4 h-4 text-blue-500" />;
      case 'in-progress':
        return <Package className="w-4 h-4 text-yellow-500" />;
      case 'out-for-delivery':
        return <Truck className="w-4 h-4 text-orange-500" />;
      case 'delivered':
        return <CheckCircle className="w-4 h-4 text-green-500" />;
      default:
        return <Clock className="w-4 h-4 text-gray-500" />;
    }
  };

  const getStatusColor = (status) => {
    switch (status) {
      case 'received':
        return 'bg-blue-100 text-blue-800';
      case 'in-progress':
        return 'bg-yellow-100 text-yellow-800';
      case 'out-for-delivery':
        return 'bg-orange-100 text-orange-800';
      case 'delivered':
        return 'bg-green-100 text-green-800';
      case 'cancelled':
        return 'bg-red-100 text-red-800';
      default:
        return 'bg-gray-100 text-gray-800';
    }
  };

  const tabs = [
    { id: 'overview', label: 'Overview', icon: <User className="w-4 h-4" /> },
    { id: 'orders', label: 'My Orders', icon: <Package className="w-4 h-4" /> },
    { id: 'reviews', label: 'Write Review', icon: <Star className="w-4 h-4" /> },
    { id: 'categories', label: 'Shop Categories', icon: <ShoppingBag className="w-4 h-4" /> },
    { id: 'wishlist', label: 'Wishlist', icon: <Heart className="w-4 h-4" /> }
  ];

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-pink-600"></div>
      </div>
    );
  }

  return (
    <div style={{ minHeight: '100vh', background: 'var(--bg-primary)', padding: '2rem 0' }}>
      <div className="container" style={{ maxWidth: '1200px', margin: '0 auto' }}>
        {/* Welcome Header */}
        <div className="animate-fade-in" style={{ marginBottom: '2rem' }}>
          <div style={{
            background: 'linear-gradient(135deg, var(--primary-color), var(--secondary-color))',
            borderRadius: '16px',
            padding: '2rem',
            color: 'white',
            boxShadow: '0 10px 30px rgba(0,0,0,0.1)'
          }}>
            <h1 style={{ fontSize: '2.5rem', fontWeight: '800', marginBottom: '0.5rem' }}>
              {getGreetingMessage()}, {user?.name}! 
            </h1>
            <p style={{ opacity: '0.9', fontSize: '1.1rem', marginBottom: '1rem', color: 'var(--text-strong)' }}>
              We're happy to see you again. Here's what's happening with your orders.
            </p>
            {paymentDue > 0 && (
              <div style={{
                marginTop: '1.5rem',
                background: 'var(--bg-secondary)',
                borderRadius: '16px',
                padding: '1.5rem',
                border: '1px solid var(--border-color)',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'space-between',
                gap: '1rem'
              }}>
                <div style={{ display: 'flex', alignItems: 'center', gap: '1rem' }}>
                  <div style={{
                    padding: '0.75rem',
                    background: 'rgba(249, 115, 22, 0.1)',
                    borderRadius: '12px',
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'center'
                  }}>
                    <CreditCard size={24} style={{ color: '#f97316' }} />
                  </div>
                  <div>
                    <div style={{ fontSize: '0.9rem', color: 'var(--text-secondary)', marginBottom: '0.25rem' }}>Payment Pending</div>
                    <div style={{ fontSize: '1.5rem', fontWeight: '700', color: 'var(--text-primary)' }}>{paymentDue}</div>
                    <div style={{ fontSize: '0.8rem', color: 'var(--text-muted)' }}>COD orders awaiting payment</div>
                  </div>
                </div>
                <button 
                  onClick={() => setShowPaymentModal(true)}
                  style={{
                  background: 'var(--primary-color)',
                  color: 'white',
                  padding: '0.75rem 1.5rem',
                  borderRadius: '12px',
                  border: 'none',
                  fontWeight: '600',
                  fontSize: '0.9rem',
                  cursor: 'pointer',
                  display: 'flex',
                  alignItems: 'center',
                  gap: '0.5rem',
                  transition: 'all 0.2s ease'
                }}
                onMouseEnter={(e) => {
